UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, ¿te refieres a Johnson? He oido que esta escondido.', `text0_1_loc7` = 'Ah, ¿te refieres a Johnson? He oido que esta escondido.', `text1_0_loc7` = '¡Ah! Tirth, le he estado buscando durante dias, pero se ve que no tiene ganas de que le encuentren. ¡Menudo pollo es!', `text1_1_loc7` = '¡Ah! Tirth, le he estado buscando durante dias, pero se ve que no tiene ganas de que le encuentren. ¡Menudo pollo es!', `text2_0_loc7` = 'Bueno, no me oyo decir esto, pero... quiza si se le preguntas a Kronkpiloto encima de en la torre, el te lo dira...', `text2_1_loc7` = 'Bueno, no me oyo decir esto, pero... quiza si se le preguntas a Kronkpiloto encima de en la torre, el te lo dira...' WHERE `entry` = 779;
