UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Que mejor lugar para buscar algo de  malversidad y oscurantismo, que la seccion Negra de Forjaz?, la bien llamada La Caverna Abandonada. Seguro que encuentras alli a Corazon de cardo, probablemente invocara a alguna forma de vil criatura para hacer su oferta.', `text0_1_loc7` = '¿Que mejor lugar para buscar algo de  malversidad y oscurantismo, que la seccion Negra de Forjaz?, la bien llamada La Caverna Abandonada. Seguro que encuentras alli a Corazon de cardo, probablemente invocara a alguna forma de vil criatura para hacer su oferta.', `text1_0_loc7` = 'Un brujo, eh... bueno, si piensas hacer ese tipo de cosas por aqui entonces mejor que mantengas tus demonios a raya $gzagal:zagala;. No me gustan los demonios, pero me encanta poner mi hacha en ellos. ¿Me traes uno?', `text1_1_loc7` = 'Un brujo, eh... bueno, si piensas hacer ese tipo de cosas por aqui entonces mejor que mantengas tus demonios a raya $gzagal:zagala;. No me gustan los demonios, pero me encanta poner mi hacha en ellos. ¿Me traes uno?' WHERE `entry` = 2775;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Chics! Buscas formacion en el camino del guerrero, ¿verdad? ¡Kelstrum Rompepedras te pondra en tu camino! Tan solo ve hacia la Sala de Armas al este de las puertas de Forjaz.', `text0_1_loc7` = '¡Chics! Buscas formacion en el camino del guerrero, ¿verdad? ¡Kelstrum Rompepedras te pondra en tu camino! Tan solo ve hacia la Sala de Armas al este de las puertas de Forjaz.', `text1_0_loc7` = '¡Kelstrum es un bocazas y un fanfarron... y si no fuera porque tambien es uno de los guerreros mas habiles que jamas se hayan conocido!', `text1_1_loc7` = '¡Kelstrum es un bocazas y un fanfarron... y si no fuera porque tambien es uno de los guerreros mas habiles que jamas se hayan conocido!' WHERE `entry` = 2776;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Que tipo de instructor de profesion estas buscando?', `text0_1_loc7` = '¿Que tipo de instructor de profesion estas buscando?' WHERE `entry` = 2793;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Necesitas un instructor en el arte de cocinar? Bueno, entonces ve a La Tetera de Bronce, al noreste de La Gran Fundicion. ¡Daryl Riknussun te ensenara a cocinar un plato que te hara resucitar!', `text0_1_loc7` = '¿Necesitas un instructor en el arte de cocinar? Bueno, entonces ve a La Tetera de Bronce, al noreste de La Gran Fundicion. ¡Daryl Riknussun te ensenara a cocinar un plato que te hara resucitar!', `text1_0_loc7` = '¡Aj! ¡Daryl Riknussun es un cocinero excelente! ¡Hace un pimiento tan picante que se podria templar acero con el! No creo que te ensene la receta pero seguro que te ayudara a convertirte en $Gun buen cocinero:una buena cocinera;. Lo encontraras en La Tetera de Bronce, en la parte noreste de La Gran Fundicion.', `text1_1_loc7` = '¡Aj! ¡Daryl Riknussun es un cocinero excelente! ¡Hace un pimiento tan picante que se podria templar acero con el! No creo que te ensene la receta pero seguro que te ayudara a convertirte en $Gun buen cocinero:una buena cocinera;. Lo encontraras en La Tetera de Bronce, en la parte noreste de La Gran Fundicion.' WHERE `entry` = 2796;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los primeros auxilios son un oficio muy valioso. Nuestra instructora residente, Nissa Piedra de fuego, puede ensenarte. Buscala al sur de La Gran Fundicion, donde esta el medico de Forjaz.', `text0_1_loc7` = 'Los primeros auxilios son un oficio muy valioso. Nuestra instructora residente, Nissa Piedra de fuego puede ensenarte. Buscala al sur de La Gran Fundicion, donde esta el medico de Forjaz.', `text1_0_loc7` = '¿Primeros auxilios? No me pareces $Gherido:herida;. Si de verdad estas $Ginteresado:interesada; en aprender primeros auxilios, hazlo con Nissa Piedra de fuego. Ve a verla a su tienda de La Gran Fundicion, El Medico de Forjaz.', `text1_1_loc7` = '¿Primeros auxilios? No me pareces $Gherido:herida;. Si de verdad estas $Ginteresado:interesada; en aprender primeros auxilios, hazlo con Nissa Piedra de fuego. Ve a verla a su tienda de La Gran Fundicion, El Medico de Forjaz.' WHERE `entry` = 2799;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Torre de cristal de Un\'Goro es un misterio para todos. Para sus escasos habitantes, se trata de una suerte de leyenda, especulandose mucho acerca de su origen.' WHERE `entry` = 2810;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un examen detallado de la torre te permitira encontrar una pequena mesa con cristales azules, amarillos y verdes, dispuestos en una formacion especifica.$B$BUn gran circulo de cristales azules esta flanqueado a cada lado por un circulo mas pequeno de cristales amarillos y otro de cristales verdes.$B$BToma nota cuidadosamente de los colores de los cristales, teniendo en cuenta que J.D. estara muy interesado en escuchar lo que has descubierto.' WHERE `entry` = 2811;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un examen detallado de la torre te permitira encontrar una pequena mesa con cristales amarillos, rojos y verdes, dispuestos en una formacion especifica.$B$BUn gran circulo de cristales amarillos esta flanqueado a cada lado por un circulo mas pequeno de cristales rojos y otro de cristales verdes.$B$BToma nota cuidadosamente de los colores de los cristales, teniendo en cuenta que J.D. estara muy interesado en escuchar lo que has descubierto.' WHERE `entry` = 2812;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un examen detallado de la torre te permitira encontrar una pequena mesa con cristales rojos, azules y verdes, dispuestos en una formacion especifica.$B$BUn gran circulo de cristales azules esta flanqueado a cada lado por un circulo mas pequeno de cristales azules y otro de cristales verdes.$B$BToma nota cuidadosamente de los colores de los cristales, teniendo en cuenta que J.D. estara muy interesado en escuchar lo que has descubierto.' WHERE `entry` = 2813;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Para algunos, la caza es una forma de vida, una profesion elegida. Para otros, es cosa del pasado, un simbolo de coraje, un rito de paso, dependiendo de cada tribu.$B$BPara mi, la raza y la profesion no significan nada; lo que yo respeto es como poner a prueba el espiritu y la mente. Para uno, enfrentarse a las fuerzas de la naturaleza y probar las habilidades propias es una verdadera prueba de honor.$B$BSi deseas probarte a ti $Gmismo:misma; y ganar mi respeto, habla mas conmigo.' WHERE `entry` = 2816;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Quizas no esperabas encontrarme en esta cueva, ¿verdad?$B$BMi nombre es J.D. y en realidad no puedo decirte como llegue hasta aqui, ¡pero lo que si te puedo decir es que esta cueva es ahora mi hogar!$B$BMe paso la vida estudiando los extranos cristales que cabe encontrar en todo el crater.' WHERE `entry` = 2817;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los simbolos de la tablilla se han transcrito cuidadosamente...' WHERE `entry` = 2820;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Has iniciado un camino honorable, $N. Mis ancestros respetan tu voluntad, pero no tendras mi aprobacion hasta que no hayas seguido paso a paso mis instrucciones.$B$BDebes saber lo que tienes que hacer si deseas saber si tienes espiritu de $Gcazador:cazadora;.' WHERE `entry` = 2821;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy mi mas cordial bienvenida, $C, como hacen los Mano Destrozada.$B$BNo te sorprendas tanto, $N. Hay sombras incluso en los puntos mas altos de Orgrimmar, algo debe de esconderse alli. Los Mano Destrozada juegan ese papel gustosamente. Incluso el bueno de Thrall reconoce que los Mano Destrozada son necesarios para garantizar la seguridad de los orcos y los trols.$B$BPero no has venido aqui para hablar de mi oficio, has venido para otra cosa.' WHERE `entry` = 2822;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Gracias a ti, podemos desentranar los secretos de los cristales y las torres misteriosas, $N. Si necesitas mas informacion en cualquier momento, ¡ven a verme en otra ocasion!' WHERE `entry` = 2833;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La Torre de Cristal del Este acepta cristales azules, amarillos y verdes.$B$BSe centra en la energia que puede crearse usando cristales azules como fuente principal de poder. Existen dos efectos posibles: uno proporciona puntos adicionales para el espiritu y el otro crea un escudo de ataque.$B$BLa fuerza de los cristales requiere cristales azules y verdes. Esto fortalecera tu espiritu.$B$BLa aguja de cristal requiere el uso de cristales azules y amarillos. Esto creara un escudo que perjudica a los atacantes.' WHERE `entry` = 2834;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Al parecer, el ravasaurio de Un\'Goro esta relacionado con los raptores de los baldios: es muy inteligente, imprevisible, y sigue un codigo de conducta determinado. El mas ravasaurio mas grande que he encontrado es Lar\'korwi. Es una de las bestias mas peligrosas que he visto jamas. En taurahe, Lar\'korwi significa \"zarpa afilada,\" pero este detalle no le hace justicia.$B$BNo subestimes a Lar\'korwi si piensas cazarlo, pero te ayudare en lo que pueda si lo haces.' WHERE `entry` = 2835;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La Torre de Cristal del Oeste acepta cristales rojos, verdes y azules.$B$BLa torre se centra en la energia que puede crearse usando cristales rojos como fuente principal de poder. Existen dos efectos posibles: uno para aumentar la proteccion contra el dano y otro para debilitar las defensas del enemigo.$B$BLa proteccion de cristal requiere el uso de cristales rojos y verdes. Esto fortalecera tu armadura.$B$BEl rendimiento de cristal requiere el uso de cristales azules y rojos. Puede debilitar la armadura de un blanco especifico.' WHERE `entry` = 2837;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'En Frondavil hay plantas que pueden librarse del mal cuando se les aplique el balsamo para plantas Cenarion. Se como fabricar el balsamo a partir de distintas clases de objetos. Segun tus conocimientos, veras que puedes ayudarme de varias maneras.$B$BTambien vas a necesitar una antorcha Cenarion para poder trabajar aqui. Se trata de un objeto que te permite ver el mal y la podredumbre de la naturaleza ocultos a simple vista.' WHERE `entry` = 2849;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los restos estan cubiertos de moscas y gusanos.' WHERE `entry` = 2850;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida a Tanaris, $N. ¿En que te puedo ayudar hoy? No pareces un herrero, pero se que las apariencias enganan.' WHERE `entry` = 2954;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estan perdidos, $R. Tratados escritos por los maestros elfos. Dispersos por el mundo cuando se desato la Plaga sobre Quel\'Thalas.$B$BAhora estamos buscando esos tratados. Los buscamos para desentranar los secretos que esconden - para recuperar el poder que una vez tuvimos.$B$BYo busco los diez tratados escritos por el maestro Kariel Winthalus. Si encuentras uno de esos tratados, dejame que absorba sus propiedades magicas y te fabricare una fusion de poder... por supuesto, todo tiene su precio. Mi precio te parecera elevado, pero deberas pagarlo.' WHERE `entry` = 2993;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Que la Luz te proteja hoy.', `text0_1_loc7` = 'Que la Luz te proteja hoy.' WHERE `entry` = 2999;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿¿Que querer de Raze??' WHERE `entry` = 3001;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'De todos los lugares maravillosos de Darnassus, ¿en que direccion quieres que te oriente?', `text0_1_loc7` = 'De todos los lugares maravillosos de Darnassus, ¿en que direccion quieres que te oriente?' WHERE `entry` = 3016;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedes guardar tus bienes mas valiosos en el banco de Darnassus, que esta en el centro de una ciudad maravillosa.', `text0_1_loc7` = 'Puedes guardar tus bienes mas valiosos en el banco de Darnassus, que esta en el centro de una ciudad maravillosa.', `text1_0_loc7` = '¡Vaya, otro materialista! Bueno, si estas $Gobsesionado:obsesionada; con tus preciosos objetos seguramente querras guardarlos en el banco de Darnassus, en el centro de nuestra ciudad.', `text1_1_loc7` = '¡Vaya, otro materialista! Bueno, si estas $Gobsesionado:obsesionada; con tus preciosos objetos seguramente querras guardarlos en el banco de Darnassus, en el centro de nuestra ciudad.' WHERE `entry` = 3017;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los hipogrifos de Vesprystus te conduciran rapidamente a tu destino. Buscalo en la aldea Ruth’theran, a traves del portal que esta en la zona oeste de Darnassus.', `text0_1_loc7` = 'Los hipogrifos de Vesprystus te conduciran rapidamente a tu destino. Buscalo en la aldea Ruth’theran, a traves del portal que esta en la zona oeste de Darnassus.', `text1_0_loc7` = 'Es a Vesprystus a quien buscas. Guarda a los hipogrifos que llegan y parten de la base de Teldrassil. Lo encontraras atravesando el portal que se encuentra al oeste de Darnassus y que conduce a Aldea Rut\'theran.', `text1_1_loc7` = 'Es a Vesprystus a quien buscas. Guarda a los hipogrifos que llegan y parten de la base de Teldrassil. Lo encontraras atravesando el portal que se encuentra al oeste de Darnassus y que conduce a Aldea Rut\'theran.' WHERE `entry` = 3018;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si deseas formar una hermandad, tienes que buscar a Lysheana, maestra de hermandades. La encontraras en el Bancal del Artesano, en la zona noreste de Darnassus.', `text0_1_loc7` = 'Si deseas formar una hermandad, tienes que buscar a Lysheana, maestra de hermandades. La encontraras en el Bancal del Artesano, en la zona noreste de Darnassus.', `text1_0_loc7` = 'Lysheana puede ofrecerte los documentos adecuados, reside en el Bancal del Artesano en la parte noreste de Darnassus. ¡Que tu asociacion llene de gloria a los elfos de la noche!', `text1_1_loc7` = 'Lysheana puede ofrecerte los documentos adecuados, reside en el Bancal del Artesano en la parte noreste de Darnassus. ¡Que tu asociacion llene de gloria a los elfos de la noche!' WHERE `entry` = 3019;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si bien para mi no hay mejor cama que la fresca hierba que cubre las praderas de Teldrassil, las camas que Saelienne prepara en la posada del Bancal de los Artesanos son muy adecuadas para los sibaritas.', `text0_1_loc7` = 'Si bien para mi no hay mejor cama que la fresca hierba que cubre las praderas de Teldrassil, las camas que Saelienne prepara en la posada del Bancal de los Artesanos son muy adecuadas para los sibaritas.', `text1_0_loc7` = '¿Ya ha amanecido? ¡La noche nunca tiene suficientes horas! Bueno, Saelienne cuidara bien de ti. Puedes encontrarla atendiendo a sus huespedes en el Bancal del Artesano, en la parte noreste de Darnassus.', `text1_1_loc7` = '¿Ya ha amanecido? ¡La noche nunca tiene suficientes horas! Bueno, Saelienne cuidara bien de ti. Puedes encontrarla atendiendo a sus huespedes en el Bancal del Artesano, en la parte noreste de Darnassus.' WHERE `entry` = 3020;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay un buzon en la parte exterior del banco. Lo encontraras en el centro de Darnassus.', `text0_1_loc7` = 'Hay un buzon en la parte exterior del banco. Lo encontraras en el centro de Darnassus.', `text1_0_loc7` = '¿Un buzon? ¿Tienes que enviar una carta o tal vez has recibido una? ¡Espero que sean buenas noticias! Encontraras un buzon a las puertas del banco en el centro de Darnassus.', `text1_1_loc7` = '¿Un buzon? ¿Tienes que enviar una carta o tal vez has recibido una? ¡Espero que sean buenas noticias! Encontraras un buzon a las puertas del banco en el centro de Darnassus.' WHERE `entry` = 3021;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aqui en Darnassus encontraras expertos instructores que han perfeccionado las habilidades de su clase elegida en anos de entrenamiento y dedicacion. Puedo ponerte en contacto con el que podria ser tu mentor. Basta que digas que este es el camino que has elegido.', `text0_1_loc7` = 'Aqui en Darnassus encontraras expertos instructores que han perfeccionado las habilidades de su clase elegida en anos de entrenamiento y dedicacion. Puedo ponerte en contacto con el que podria ser tu mentor. Basta que digas que este es el camino que has elegido.' WHERE `entry` = 3022;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Eres afortunado al venir a Darnassus, en donde podras encontrarte con una cazadora tan avezada como Jocaste entre nuestros instructores. Te animo a que la visites en el Enclave Cenarion y que le pidas que sea tu tutora.', `text0_1_loc7` = 'Eres afortunado al venir a Darnassus, en donde podras encontrarte con una cazadora tan avezada como Jocaste entre nuestros instructores. Te animo a que la visites en el Enclave Cenarion y que le pidas que sea tu tutora.', `text1_0_loc7` = 'Jocaste fue la mejor cazadora de su tiempo. Hoy puedes encontrarla en Enclave Cenarion donde se dedica a instruir a los que comienzan a recorrer la senda del cazador.', `text1_1_loc7` = 'Jocaste fue la mejor cazadora de su tiempo. Hoy puedes encontrarla en Enclave Cenarion donde se dedica a instruir a los que comienzan a recorrer la senda del cazador.' WHERE `entry` = 3023;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En eterna vigia en su proteccion del mundo natural, los druidas estan guiados por el archidruida Fandral Corzocelada que reside en el Enclave Cenarion. Lo encontraras junto a los otros instructores de druidas en la seccion septentrional de Darnassus.', `text0_1_loc7` = 'En eterna vigia en su proteccion del mundo natural, los druidas estan guiados por el archidruida Fandral Corzocelada que reside en el Enclave Cenarion. Lo encontraras junto a los otros instructores de druidas en la seccion septentrional de Darnassus.', `text1_0_loc7` = 'En momentos dificiles, un druida puede ser un calido amigo a quien acudir. Pregunta a cualquier druida del Enclave Cenarion y te diran lo mismo.', `text1_1_loc7` = 'En momentos dificiles, un druida puede ser un calido amigo a quien acudir. Pregunta a cualquier druida del Enclave Cenarion y te diran lo mismo.' WHERE `entry` = 3024;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Templo de la Luna es el hogar de los sacerdotes de Elune. Alli encontraras a Jandria y Lariia, quienes te proporcionaran toda la ayuda necesaria en tu camino al sacerdocio.', `text0_1_loc7` = 'El Templo de la Luna es el hogar de los sacerdotes de Elune. Alli encontraras a Jandria y Lariia, quienes te proporcionaran toda la ayuda necesaria en tu camino al sacerdocio.', `text1_0_loc7` = '¡El poder de Elune te obliga! ¡El poder de Elune te obliga!$B¡Puedes marcharte! ¡Busca a Jandria en el pasillo central del Templo de la Luna en la zona sur de Darnassus!', `text1_1_loc7` = '¡El poder de Elune te obliga! ¡El poder de Elune te obliga!$B¡Puedes marcharte! ¡Busca a Jandria en el pasillo central del Templo de la Luna en la zona sur de Darnassus!' WHERE `entry` = 3025;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tienes que acudir a Syurna si quieres seguir el camino de los picaros. Ella vive en el Enclave Cenarion bajando el camino en espiral en el arbol situado mas al oeste.', `text0_1_loc7` = 'Tienes que acudir a Syurna si quieres seguir el camino de los picaros. Ella vive en el Enclave Cenarion bajando el camino en espiral en el arbol situado mas al oeste.', `text1_0_loc7` = 'Existen algunos que intentan seguir el camino de los picaros, pero solo los elfos de la noche disfrutan del cobijo de la noche. Si quieres seguir el camino tenebroso, busca a Syurna en el Enclave Cenarion.', `text1_1_loc7` = 'Existen algunos que intentan seguir el camino de los picaros, pero solo los elfos de la noche disfrutan del cobijo de la noche. Si quieres seguir el camino tenebroso, busca a Syurna en el Enclave Cenarion.' WHERE `entry` = 3026;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encontraras a Sildinair en el Bancal del Guerrero entre los guardias, escrutando el bosque con sus ojos despiertos en busca de enemigos que pretendan entrar en nuestra hermosa ciudad.', `text0_1_loc7` = 'Encontraras a Sildinair en el Bancal del Guerrero entre los guardias, escrutando el bosque con sus ojos despiertos en busca de enemigos que pretendan entrar en nuestra hermosa ciudad.', `text1_0_loc7` = 'Sildinair ha instruido a muchos de los centinelas que guardan nuestra amada ciudad. Estoy seguro de que puede distinguir a un guerrero de tu talla. Buscala en el Bancal del Guerrero junto a la entrada de la ciudad.', `text1_1_loc7` = 'Sildinair ha instruido a muchos de los centinelas que guardan nuestra amada ciudad. Estoy segura de que puede distinguir a un guerrero de tu talla. Buscala en el Bancal del Guerrero junto a la entrada de la ciudad.' WHERE `entry` = 3033;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los artesanos de Darnassus encierran un milenio de conocimiento para el beneficio del oficio que has elegido. Ahora dime, ¿que oficio consideras el mas interesante?', `text0_1_loc7` = 'Los artesanos de Darnassus encierran un milenio de conocimiento para el beneficio del oficio que has elegido. Ahora dime, ¿que oficio consideras el mas interesante?' WHERE `entry` = 3034;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si realmente deseas aprender esta profesion, te guiaria hasta Ainethil, una alquimista experta que esta en el Bancal del Artesano, en el norte de Darnassus.', `text0_1_loc7` = 'Si realmente deseas aprender esta profesion, te guiaria hasta Ainethil, una alquimista experta que esta en el Bancal del Artesano, en el norte de Darnassus.', `text1_0_loc7` = '¿Un alquimista? Ningun problema, $C. Ainethil, gracias a su trabajo duro y esfuerzo, se ha convertido en la mejor alquimista del Bancal del Artesano. Si fuera tu iria alli sin perder tiempo. ¡Sin perder tiempo! ¡Vamos, vete!', `text1_1_loc7` = '¿Un alquimista? Ningun problema, $C. Ainethil, gracias a su trabajo duro y esfuerzo, se ha convertido en la mejor alquimista del Bancal del Artesano. Si fuera tu iria alli sin perder tiempo. ¡Sin perder tiempo! ¡Vamos, vete!' WHERE `entry` = 3035;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Alegorn es un maestro chef que lleva perfeccionando su cocina desde hace siglos. Deberias considerarte tremendamente afortunado en el caso de que decidiera ensenarte. Si crees que eres $Gdigno:digna; de ser su aprendiz, lo encontraras en el Bancal del Artesano.', `text0_1_loc7` = 'Alegorn es un maestro chef que lleva perfeccionando su cocina desde hace siglos. Deberias considerarte tremendamente afortunado en el caso de que decidiera ensenarte. Si crees que eres $Gdigno:digna; de ser su aprendiz, lo encontraras en el Bancal del Artesano.', `text1_0_loc7` = 'Los platos creados por Alegorn son para morirse. Siempre esta ocupado preparando nuevas delicias en el Bancal del Artesano. No olvides probar su receta Verde Cenarion, si es que aun le queda algo.$BAhora, si no te importa, tengo que investigar la ola de ciervos desaparecidos.', `text1_1_loc7` = 'Los platos creados por Alegorn son para morirse. Siempre esta ocupado preparando nuevas delicias en el Bancal del Artesano. No olvides probar su receta Verde Cenarion, si es que aun le queda algo.$BAhora, si no te importa, tengo que investigar la ola de ciervos desaparecidos.' WHERE `entry` = 3036;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando no tienes a nadie que cure tus heridas, los conocimientos de primeros auxilios pueden salvarte la vida. Yo preguntaria a Dannelor, en el Bancal del Artesano, para pedirle instruccion en este oficio.', `text0_1_loc7` = 'Cuando no tienes a nadie que cure tus heridas, los conocimientos de primeros auxilios pueden salvarte la vida. Yo preguntaria a Dannelor, en el Bancal del Artesano, para pedirle instruccion en este oficio.' WHERE `entry` = 3037;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta habilidad requiere mucho tiempo pero resulta gratificante. Habla con Astaia en el Bancal del Artesano y puede que aprendas algo util.', `text0_1_loc7` = 'Esta habilidad requiere mucho tiempo pero resulta gratificante. Habla con Astaia en el Bancal del Artesano y puede que aprendas algo util.', `text1_0_loc7` = 'Astaia ha pescado algunos tesoros muy curiosos durante todo el tiempo que ha pasado pescando. ¿Por que no vas a verla en el Bancal de los Mercaderes y dejas que te ensene algunos?', `text1_1_loc7` = 'Astaia ha pescado algunos tesoros muy curiosos durante todo el tiempo que ha pasado pescando. ¿Por que no vas a verla en el Bancal de los Mercaderes y dejas que te ensene algunos?' WHERE `entry` = 3038;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La recoleccion y el conocimiento de las hierbas es una especialidad de Firodren Llamaluna. Buscalo en los Jardines del Templo, en la zona sudoeste de Darnassus.', `text0_1_loc7` = 'La recoleccion y el conocimiento de las hierbas es una especialidad de Firodren Llamaluna. Buscalo en los Jardines del Templo, en la zona sudoeste de Darnassus.', `text1_0_loc7` = '¿Acaso somos herboristas en ciernes? Entonces deberas visitar a Firodren Llamaluna, en los Jardines del Templo. Puede ensenarte algunos trucos del oficio.', `text1_1_loc7` = '¿Acaso somos herboristas en ciernes? Entonces deberas visitar a Firodren Llamaluna, en los Jardines del Templo. Puede ensenarte algunos trucos del oficio.' WHERE `entry` = 3039;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La habilidad con la que Telonis fabrica armaduras no tiene comparacion. Deberias agradecer que no te cobre mas por el conocimiento que transmite a sus discipulos. Buscalo en el Bancal del Artesano, en la zona noreste de Darnassus.', `text0_1_loc7` = 'La habilidad con la que Telonis fabrica armaduras no tiene comparacion. Deberias agradecer que no te cobre mas por el conocimiento que transmite a sus discipulos. Buscalo en el Bancal del Artesano, en la zona noreste de Darnassus.', `text1_0_loc7` = 'Plantamos las verduras. El conejo se come las verduras. El lobo devora al conejo. Nosotros matamos al lobo. Aprovechamos la carne y el pellejo del lobo. Es el circulo magico de la vida, o como quieras llamarlo.$BAhora ve a ver a Telonis en el Enclave Cenarion para iniciar tu camino y dejame regresar a mis labores.', `text1_1_loc7` = 'Plantamos las verduras. El conejo se come las verduras. El lobo devora al conejo. Nosotros matamos al lobo. Aprovechamos la carne y el pellejo del lobo. Es el circulo magico de la vida, o como quieras llamarlo.$BAhora ve a ver a Telonis en el Enclave Cenarion para iniciar tu camino y dejame regresar a mis labores.' WHERE `entry` = 3040;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desde la muerte, los animales nos proporcionan vida y proteccion. Si crees que puedes mostrar el debido respeto por su sacrificio, puede que la profesion del desollador sea la indicada para ti. Para aprender mas sobre este oficio, habla con Eladriel en el Bancal del Artesano, en el norte de Darnassus.', `text0_1_loc7` = 'Desde la muerte, los animales nos proporcionan vida y proteccion. Si crees que puedes mostrar el debido respeto por su sacrificio, puede que la profesion del desollador sea la indicada para ti. Para aprender mas sobre este oficio, habla con Eladriel en el Bancal del Artesano, en el norte de Darnassus.', `text1_0_loc7` = 'De acuerdo a nuestra cultura creemos que no puede desperdiciarse nada. Si nos vemos obligados a dar caza a una criatura, hacemos todo lo posible para que su sacrificio no sea en vano.$BSi deseas aprender el modo de quitarle la piel a una bestia a la que se ha dado muerte, busca a Eladriel en el Bancal del Artesano.', `text1_1_loc7` = 'De acuerdo a nuestra cultura creemos que no puede desperdiciarse nada. Si nos vemos obligados a dar caza a una criatura, hacemos todo lo posible para que su sacrificio no sea en vano.$BSi deseas aprender el modo de quitarle la piel a una bestia a la que se ha dado muerte, busca a Eladriel en el Bancal del Artesano.' WHERE `entry` = 3042;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si pretendes convertirte en sastre sin duda tendras que acudir a Me’Lynn. Ella vive en el Bancal del Artesano en Darnassus, al nordeste.', `text0_1_loc7` = 'Si pretendes convertirte en sastre sin duda tendras que acudir a Me’Lynn. Ella vive en el Bancal del Artesano en Darnassus, al nordeste.', `text1_0_loc7` = 'Mirando las prendas elaboradas por Me\'Lynn podria pensarse que su aguja ha sido ungida por la propia Elune. A menos que tu aguja tambien lo este, lo mejor sera que le hagas una visita y que atiendas a sus ensenanzas. La puedes encontrar en el Bancal del Artesano, en la parte noreste de Darnassus.', `text1_1_loc7` = 'Mirando las prendas elaboradas por Me\'Lynn podria pensarse que su aguja ha sido ungida por la propia Elune. A menos que tu aguja tambien lo este, lo mejor sera que le hagas una visita y que atiendas a sus ensenanzas. La puedes encontrar en el Bancal del Artesano, en la parte noreste de Darnassus.' WHERE `entry` = 3044;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Hola?' WHERE `entry` = 3055;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Si, claro, $N! Lo siento... crei que ya habias terminado.' WHERE `entry` = 3056;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La casa de subastas de Orgrimmar esta en el Valle de la Fuerza. Buscala en el este, junto a Importaciones Bocarda.', `text0_1_loc7` = 'La casa de subastas de Orgrimmar esta en el Valle de la Fuerza. Buscala en el este, junto a Importaciones Bocarda.', `text1_0_loc7` = 'Tienes la voz ronca de tanto gritar, ¿eh? Encuentra la casa de subastas de Orgrimmar en el Valle de la Fuerza.', `text1_1_loc7` = '¿Tienes la voz ronca de tanto gritar, eh? Encuentra la casa de subastas de Orgrimmar en el Valle de la Fuerza.' WHERE `entry` = 3075;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Son sorprendentes, ¿verdad? Todos estos artefactos y restos.$B$BNo se que me impresiona mas, si su tamano o las historias que se cuentan sobre ellos. Hay tanto que estudiar y aprender... y los vinculos que se pueden establecer... $B$B¿Sabias que se dice que muchas razas comparten la herencia de los elfos de la noche?' WHERE `entry` = 3077;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Por lo mas sagrado, dime que has venido a rescatarnos de esta tierra abandonada. Aunque estoy con Expediciones Marshal, te daria mi diente de oro por subirme en tu mochila y dejar este lugar.' WHERE `entry` = 3094;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Hola! Soy Hol\'anyee, la jefa cientifica y biologa de Expediciones Marshal. Mi hermano y yo, junto con nuestros companeros, viajamos por Azeroth para explorar la majestuosidad de la naturaleza salvaje. Es emocionante, pero... um... ¡Creo que esta vez nos hemos excedido en nuestra dosis de emocion!' WHERE `entry` = 3095;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $Gextrano:extrana;, ¡te doy la bienvenida a la majestuosidad salvaje del crater Un’Goro!$B$BMi nombre es Williden Marshal. Mi hermana, nuestros companeros y yo formamos Expediciones Marshal, un grupo de cientificos y exploradores del Azeroth mas salvaje y desconocido.$B$BHemos tenido un percance serio y necesitamos que nos ayudes si puedes.' WHERE `entry` = 3096;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No tengo idea de por que quieres abandonar estas playas soleadas y aguas cristalinas. Esto es como el paraiso. Pero no se lo digas a mi hermano Zapetta. Ya esta suficientemente enfadado por tener que pasar el dia entero en Tirisfal. Este zepelin hace parada en Orgrimmar, asi que no tendras que escuchar sus lamentos. Pero si vas pronto a Tirisfal, sigue mi consejo y no dejes que se queje.', `text0_1_loc7` = 'No tengo idea de por que quieres abandonar estas playas soleadas y aguas cristalinas. Esto es como el paraiso. Pero no se lo digas a mi hermano Zapetta. Ya esta suficientemente enfadado por tener que pasar el dia entero en Tirisfal. Este zepelin hace parada en Orgrimmar, asi que no tendras que escuchar sus lamentos. Pero si vas pronto a Tirisfal, sigue mi consejo y no dejes que se queje.' WHERE `entry` = 3133;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Busca a la tabernera Pala en el edificio que hay al sur del Banco de Cima del Trueno, en la pequena pendiente.', `text0_1_loc7` = 'Busca a la tabernera Pala en el edificio que hay al sur del Banco de Cima del Trueno, en la pequena pendiente.', `text1_0_loc7` = 'La tabernera Pala te ayudara a descansar el cuerpo y el espiritu. Busca a la tabernera Pala en el edificio que hay al sur del Banco de Cima del Trueno, en la pequena pendiente.', `text1_1_loc7` = 'La tabernera Pala te ayudara a descansar el cuerpo y el espiritu. Busca a la tabernera Pala en el edificio que hay al sur del Banco de Cima del Trueno, en la pequena pendiente.' WHERE `entry` = 3153;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedes encontrar un buzon justo fuera del Banco de Cima del Trueno, en la pequena pendiente.', `text0_1_loc7` = 'Puedes encontrar un buzon justo fuera del Banco de Cima del Trueno, en la pequena pendiente.', `text1_0_loc7` = 'Creo que se ha enviado un nuevo cargamento de correo hace no mucho. Encontraras el buzon en el exterior del Banco de Cima del Trueno, en la pequena pendiente.', `text1_1_loc7` = 'Creo que se ha enviado un nuevo cargamento de correo hace no mucho. Encontraras el buzon en el exterior del Banco de Cima del Trueno, en la pequena pendiente.' WHERE `entry` = 3154;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta en la zona baja de Cima del Trueno. Busca junto al banco. No esta lejos de aqui.', `text0_1_loc7` = 'Esta en la zona baja de Cima del Trueno. Busca junto al banco. No esta lejos de aqui.', `text1_0_loc7` = 'La casa de subastas se encuentra en la cumbre central de Cima del Trueno. Busca junto a la parte inferior de la Torre de los jinetes del viento. Que tus antepasados cuiden de ti.', `text1_1_loc7` = 'La casa de subastas se encuentra en la cumbre central de Cima del Trueno. Busca junto a la parte inferior de la Torre de los jinetes del viento. Que tus antepasados cuiden de ti.' WHERE `entry` = 3155;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puede que esto te escandalice, pero el negocio no ha ido nada bien ultimamente...' WHERE `entry` = 3156;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te doy la bienvenida a Theramore, $N.$B$BTe pido disculpas por ser tan cortante y por no tener tiempo para atenderte como es debido, pero mi ciudad es mi prioridad. Muchas cosas amenazan nuestra forma de vida y la santidad de la Alianza. Debo estar totalmente alerta frente a las fuerzas externas que escapan a nuestro control si quiero que mi gente este segura y viva feliz.$B$BTal vez tengamos mas tiempo para hablar despues.' WHERE `entry` = 3157;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Gracias a ti, $N, Hendel esta bajo nuestra custodia.$B$BSi bien el interrogatorio es lento, te aseguro que nos dira todo lo que sabe.$B$BPor ahora, ten paciencia. Te buscare de inmediato si necesitamos ayuda.$B$BHasta entonces, disfruta de Theramore y te invito a quedarte el tiempo que quieras dentro de los muros de nuestra ciudad como amigo de la Alianza.' WHERE `entry` = 3158;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Justo fuera de la Entrada Principal de la ciudad hay un poste indicador. Apunta hacia la torre del zepelin. Habla con el maestro de zepelines Frezza, en la parte superior de la torre.', `text0_1_loc7` = 'Justo fuera de la Entrada Principal de la ciudad hay un poste indicador. Apunta hacia la torre del zepelin. Habla con el maestro de zepelines Frezza, en la parte superior de la torre.' WHERE `entry` = 3173;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Volcor no te mira cuando te acercas a el. Parece concentrarse en evitar que se le caigan las vendas. Tras una pausa, levanta la mirada hacia ti, un poco perpleja.>$B$BOh, disculpa, $C. Este no es un buen momento. Espero que lo comprendas.' WHERE `entry` = 3213;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Bueno, tal como lo veo, $N, tenemos dos opciones al abandonar esta caverna.$B$BPodemos irnos a hurtadillas: yo uso la capa que me diste y tu tendras que buscarte la vida o, y esta puede ser una opcion mas dificil, considerando mis heridas, luchamos de espaldas, cada cual por su cuenta.$B$BDejo que lo decidas tu, ya que conoces tus fuerzas mas que yo.' WHERE `entry` = 3214;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Gmuchacho:muchacha;, si buscas los mejores precios que hay fuera de Forjaz, has venido al lugar adecuado.' WHERE `entry` = 3218;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola! Soy Gubber Blump.' WHERE `entry` = 3234;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '... fritos en sarten, fritos en mucho aceite y a la plancha...' WHERE `entry` = 3253;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $N. Te doy la bienvenida al Refugio de la Zaga.' WHERE `entry` = 3293;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Las Estepas de Fuego es un lugar peligroso pero, los eruditos como yo, hacemos lo que sea por avanzar en nuestro conocimiento.' WHERE `entry` = 3296;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En decadas pasadas, en guerras y conflictos de otros tiempos, nuestra especie estuvo a un paso de la extincion. Hemos soportado muchas cosas, $R.$B$BEl ataque de la Legion Ardiente. El Alma demoniaca. Los aspectos divididos. El ascenso de Alamuerte. La esclavizacion de nuestra reina, Alexstrasza, por los Faucedraco.$B$BEn todo ello, hemos demostrado tener la inquebrantable voluntad de los dragones, la fuerza imperterrita que ha luchado para representar nuestra existencia menguante en este mundo.' WHERE `entry` = 3301;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ciertamente. Aparezco delante de ti en una forma que no puedes comprender, mortal. Claro, tu no podrias soportar estar ante la presencia del dragon conocido como Vaelastrasz.$B$B<Vaelan rie.>$B$B¿Continuo?' WHERE `entry` = 3302;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tal vez deberia decirte por que estoy aqui.$B$B¿Conoces el aspecto Alamuerte? O a sus descendientes, Nefarian y Onyxia?' WHERE `entry` = 3303;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Alamuerte, anteriormente conocido como Neltharion, fue el aspecto que encarno la fuerza del mundo: concedia poder casi ilimitado en este reino mortal a traves del creador de titanes conocido como Khaz’goroth: Forjador del mundo.$B$BMediante la traicion y el engano, este ser inmundo convencio a los otros cuatro aspectos para que transmitieran sus poderes a un elemento, conocido como el Alma demoniaca, debilitandolos enormemente y provocando su division.' WHERE `entry` = 3304;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Asi es. El elemento fue destruido finalmente y Alamuerte estuvo a punto de sucumbir frente al poder conjunto y renovado de los cuatro Aspectos restantes. Alamuerte ahora resulta desconocido. No obstante, lo que si sabemos es que Nefarian, uno de sus descendientes, gobierna esta ciudadela y desea continuar la mision de su padre desde el punto en que la dejo.' WHERE `entry` = 3305;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Antes de su destierro, Alamuerte estaba intentando crear un descendiente impuro. Deseaba crear razas que no solo fueran inmunes a los poderes y habilidades innatos de sus Aspectos, sino que estuvieran bajo el control total de Alamuerte.$B$BAl parecer, el hijo de Nefarian logro lo que su padre nunca consiguio, al menos en parte.$B$BFue Ysera, aspecto de los suenos, fue quien advirtio estas abominaciones y nos obligo a actuar. Si esta especie de dragonantes ‘cromaticos’ existe, tiene que se destruida.' WHERE `entry` = 3306;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No es nuestra opcion y tampoco seria posible. Nefarian esta protegido por salas antiguas, tanto como los propios Aspectos. Ellas me impiden entrar en el Sagrario Interior de esta ciudadela. La magia negra evita que cualquiera de los otros Vuelos entre sin haber sido ordenados... a menos que...' WHERE `entry` = 3307;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Solo un ser, que no es de la especie de los dragones, puede obtener el Sello Adornado de Ascension y atravesar la barrera protegida. Si bien la posesion de este objeto podria suponer mi destruccion, tal vez tu podrias utilizarlo y hacer descender la barrera.' WHERE `entry` = 3308;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tienes que encontrar a alguien, entre los desdichados que habitan la parte baja de esta ciudadela, que haya recibido el sello de ascension no Adornado. Es un gran honor para uno de estos seres inferiores recibir este objeto, ya que constituye el primer paso para entrar en la parte superior de la ciudadela y convertirse en uno de los guardias de elite de Nefarian, la legion Puno Negro. Encuentra ese objeto y devuelvemelo para que pueda examinar sus propiedades. Entonces sabre como proceder una vez que haya terminado mi analisis del sello.' WHERE `entry` = 3309;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te lo advierto, mortal, es excepcionalmente raro que uno de los clanes inferiores de esta ciudadela posea este elemento. Tu busqueda podria llevar decadas. Recomiendo que destruyas a esta bestias impunemente.' WHERE `entry` = 3310;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$N, tengo que averiguar como han proliferado los efectos del musgovil en toda la Costa Oscura. La corrupcion de Teldrassil y de las tierras que nos rodean es muy importante para los druidas del Circulo Cenarion y para todos los elfos de la noche.$B$BMi objetivo es averiguar por que ha ocurrido todo esto y ponerle punto y final.' WHERE `entry` = 3334;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puesto que la hoja de una espada nunca es demasiado afilada ni la armadura demasiado solida, siempre es necesario contar con el talento de un encantador. Taladan es uno de ellos y puede mostrarte como urdir toda clase de encantamientos para mejorar tus armas y fortalecer tu armadura. Buscalo en el Bancal del Artesano.', `text0_1_loc7` = 'Puesto que la hoja de una espada nunca es demasiado afilada ni la armadura demasiado solida, siempre es necesario contar con el talento de un encantador. Taladan es uno de ellos y puede mostrarte como urdir toda clase de encantamientos para mejorar tus armas y fortalecer tu armadura. Buscalo en el Bancal del Artesano.', `text1_0_loc7` = 'Estoy halagado, pero la verdad es que no me interesa... ¿Que? ¿Quieres saber donde aprendi a encantar? ¡Oh, que embarazoso! Veamos, deberias... Seguro que podrias... ir a ver... um... ¡Taladan! Si, ¡visita a Taladan en el Bancal del Artesano!', `text1_1_loc7` = 'Estoy halagada, pero la verdad es que no me interesa... ¿Que? ¿Quieres saber donde aprendi a encantar? ¡Oh, que embarazoso! Veamos, deberias... Seguro que podrias... ir a ver... um... ¡Taladan! Si, ¡visita a Taladan en el Bancal del Artesano!' WHERE `entry` = 3337;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ah, $R... Poseo gran saber del mundo y de hacer talismanes con gran poder.' WHERE `entry` = 3375;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Mau\'ari sabe por que la buscas...$B$BDame el E\'ko que tu encontrar, transformarlo para ti puedo.' WHERE `entry` = 3377;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si, si, ya se que a Dadanga no le gusta el humo. Y por el olor parece que a Dadanga tampoco del gusta banarse.' WHERE `entry` = 3380;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'El Alijo de Mau\'ari guarda grandes secretos... Los fragmentos que se utilizaron para crearlo son reflectantes y puedes utilizar su brillante superficie para absorber el poder de otras cosas y reflejarlo sobre ti mismo.$B$BSi guardas el talisman en tu inventario podras recoger E\'ko, el poder espiritual de las bestias que combatas. Cuando encuentres E\'ko daselo a Mau\' ari.' WHERE `entry` = 3382;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'E\'ko es el poder que poseen todas las criaturas. El talisman que he hecho para ti te permitira capturarlo en una manifestacion fisica.$B$BConozco un encantamiento que puede transformar el E\'ko que encuentres y convertirlo en juju.$B$BEntonces podras coger el Alijo de Mau\'ari y utilizarlo para reflejar el poder del juju sobre ti $Gmismo:misma;.$B$BVenga, dale una oportunidad. Trae el E\'ko a Mau\'ari y nos divertiremos.' WHERE `entry` = 3383;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Eh? No tengo tiempo para charlas, papa quiere que termine este encargo.' WHERE `entry` = 3398;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Las criaturas de Cuna del Invierno son una opcion para empezar.$B$BPero puede haber mas sitios donde mirar...' WHERE `entry` = 3402;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo siento, estoy bastante ocupado ahora mismo haciendo herraduras para Verner Osbien de la Comarca del Lago.' WHERE `entry` = 3405;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Te parece que me sobra el tiempo para darle a la sinhueso?' WHERE `entry` = 3406;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sabes que gracias a LoEs2 puedes ver los textos en espanol?' WHERE `entry` = 3407;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si escucho la mas minima palabra sobre aves acuaticas, necesitaras un tubo metido en la garganta para poder comer.' WHERE `entry` = 3409;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tu interrupcion me esta costando tiempo, un tiempo muy valioso, mas vale que merezca la pena.' WHERE `entry` = 3418;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'E\' ko es la forma prima del poder sobrenatural, y Juju es la esencia concentrada. Utilizando un encantamiento puedo transformar el E\' ko en Juju. Cuando se convierte en Juju puedes utilizar el Alijo de Mau\' ari para reflejar en ti los poderes.' WHERE `entry` = 3424;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La vida de un herrero no es facil, pero llena de acero tus huesos y de fuego tu corazon. Mi padre era herrero, y mi abuelo antes que el en las forjas bajo Grim Batol. Y ahora mi hijo sigue nuestros pasos, estoy orgulloso de ese chico.' WHERE `entry` = 3456;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No salgas asi de las sombras. ¡La proxima vez avisa!', `text1_1_loc7` = 'Desde que las sombras reclamaron el Bosque del Ocaso no he podido volver a dormir. No me dejan dormir los fantasmales sonidos de gritos que trae la brisa. Parecen gritos de corderos, y nunca se detendran.' WHERE `entry` = 3461;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los espiritus te han traido hasta mi, y puedo sentir su bendicion.' WHERE `entry` = 3471;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Si? Pues claro que soy un herrero. ¿Que? ¿Crees que los enanos son los unicos que valen su peso en yunques? Muy bien, pues que asi sea. ¡Encuentra a un enano que te ayude!' WHERE `entry` = 3488;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aunque odio admitirlo, el oso me supero con creces. ¡Dios mio, es un oso enorme!$B$BNo obstante, si puedes vencer al oso sera una hazana de la que estar orgulloso. Ha dado problemas a muchos de nuestros mejores cazadores.$B$B<Te da unos golpecitos en la espalda.>$B$BDerrota al oso y te aceptaremos como miembro regular de la Cabana del Errante.' WHERE `entry` = 3493;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aqui, en la Cabana del Errante valoramos la habilidad cazando mas que cualquier otra cosa. ¿Quieres mi aprobacion? Tendras que ganartela.$B$B¡Enfrentate al desafio, $Gmuchacho:muchacha;!' WHERE `entry` = 3494;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Michael Garrett en el cinturon superior en el Barrio del Comercio.', `text0_1_loc7` = 'Michael Garrett en el cinturon superior en el Barrio del Comercio.', `text1_0_loc7` = 'El hombre de los murcielagos esta en el nivel superior del Barrio del Comercio.', `text1_1_loc7` = 'El hombre de los murcielagos esta en el nivel superior del Barrio del Comercio.' WHERE `entry` = 3515;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡$GBienvenido:Bienvenida; a la Cabana del Errante, $N!' WHERE `entry` = 3523;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Que profesion?', `text0_1_loc7` = '¿Que profesion?' WHERE `entry` = 3541;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Que instructor es el que buscas?', `text0_1_loc7` = '¿Que instructor es el que buscas?' WHERE `entry` = 3542;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Que estas buscando?', `text0_1_loc7` = '¿Que estas buscando?' WHERE `entry` = 3543;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Escucha las historias de un anciano. $N. Tengo mucho de que hablar...' WHERE `entry` = 3545;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:Bienvenida; al refugio, $N. Algunos vienen aqui con algun fin, otros por accidente... Pero todos tienen una historia unica que contar.' WHERE `entry` = 3546;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Grrr...' WHERE `entry` = 3550;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Vine a El Cruce desde el Sur buscando ayuda, $C, pero solo encuentro cobardes que me dan la espalda, hasta el poderoso Thork me insulta de este modo. Es un buen lider, pero al rechazar mi peticion me ha enfadado mucho.$B$BAfirma que mi colera me hace ser precipitado, que nubla mi juicio, ¡pero no descansare hasta que la venganza sea mia!' WHERE `entry` = 3553;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'El cuerpo es el de una guerrera orca.$B$BEsta claro que murio en combate, probablemente contra los jabaespines de la zona.' WHERE `entry` = 3557;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Giras el cuerpo y ves las marcas de espadas y hechizos en el cuerpo de la difunta orca... su armadura esta totalmente destruida y su enredado cabello le oculta el rostro.$B$BSegun la descripcion de Mankrik no tienes ninguna duda de que es su mujer.$B$BLe doleran las noticias, pero sabes que deberia conocer la verdad.' WHERE `entry` = 3558;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a El Cruce, $C. Mi nombre es Thork, lidero las defensas de este lugar en nombre del Jefe de Guerra.$B$BPor favor, sientete como en casa. Mantente en tus asuntos y a nadie le importara que estes por aqui.$B$BNuestra seguridad esta muy amenazada. Si ya te has familiarizado con la situacion del pais habla con algun otro, quizas te puedan encomendar alguna tarea.' WHERE `entry` = 3559;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ah, un visitante! Debe ser un aventurero, no llegan muchos turistas por este camino. Coge una silla y algo de beber, ponte $Gcomodo:comoda;. Dime, parece que te gusta tener algo de carne sobre los huesos. ¿Piensas competir en los combates de jaulas?', `text0_1_loc7` = '¡Ah, un visitante! Debe ser un aventurero, no llegan muchos turistas por este camino. Coge una silla y algo de beber, ponte $Gcomodo:comoda;. Dime, parece que te gusta tener algo de carne sobre los huesos. ¿Piensas competir en los combates de jaulas?' WHERE `entry` = 3566;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, joven $C.' WHERE `entry` = 3573;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Valle de los Retos moldeara hasta el mas debil guerreo, digno de unirse a la Horda. Quienes no puedan superar sus pruebas seran puestos a tostarse bajo el abrasador sol del desierto.', `text0_1_loc7` = 'El Valle de los Retos moldeara hasta el mas debil en un guerreo digno de unirse a la Horda. Quienes no puedan superar sus pruebas seran puestos a tostarse bajo el abrasador sol del desierto.' WHERE `entry` = 3583;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, $N. Soy conocida como Chronormu, pero puedes llamarme Cromi. Me gusta que me traten con familiaridad.$B$BSeguro que te preguntas como puedo saber tu nombre. Soy miembro del Vuelo Bronce en armonia eterna con el flujo y reflujo del tiempo. Podria decirse que ya nos habiamos encontrado y nos volveremos a encontrar.$B$BEspero que la presentacion visual que he elegido no te perturbe. Se me puede acusar de ser una centinela de lugares llenos de afliccion, pero he decidido no ahogarme el desconsuelo de este lugar.' WHERE `entry` = 3584;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola otra vez, $N. Siento que el flujo del tiempo se ve amenazado por fuerzas verdaderamente poderosas. Tu incondicional ayuda siempre es bien recibida por Vuelo Bronce.$B$BMientras que el pasado permanece inalterable, el futuro sufre continuos cambios. Las decisiones que tomes en este momento pueden orientar ese cambio.' WHERE `entry` = 3585;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tu mensaje debe ser muy importante, $C, para que te haya adentrado tanto en el pantano...' WHERE `entry` = 3590;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Soy un oficial de la ciudad, trabajo a cargo del censo de Entranas. Desde nuestra liberacion de la Plaga, hemos intentado seguir con precision la evolucion de la poblacion de nuestra ciudad. Puedo acceder a los registros a traves de metodos arcanos y de una aguda disciplina mental. Si tienes negocios conmigo sobre este asunto, adelante. Al menos deberias saber si es asi.' WHERE `entry` = 3653;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Jefe de Guerra me ha ordenado que estudie todo tipo de armas y armaduras. Me ha enviado aqui, al centro de las culturas tauren y orca, en los Baldios, para conocer a tantos viajeros como sea posible y para aprender cosas acerca de las culturas de los Baldios.$B$BCree que hay algo que aprender hasta de la mas patetica de las culturas... como los jabaespin o centauros.' WHERE `entry` = 3654;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tu! ¡$R! Ven aqui.<resopla>$B$BEl tiempo es breve y mi fin -resopla-esta cerca. Quebrantadientes vencera, ya lo veras. -resopla-$B$BMi captura todavia puede ayudar <resopla> a mi gente. ¡La tribu Crines de Acero es mas poderosa que los Erizapuas!$B$BLa Horda quiere averiguar quien lidera las incursiones sobre su pueblo. Tu <resopla> ayudaras a Quebrantadientes a cambio de la informacion que solo yo conozco. ¡Quebrantadientes puede ayudarte! <resopla> Pero debes ayudar a Quebrantadientes.' WHERE `entry` = 3656;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$GBienvenido:Bienvenida; a Ventormenta! Soy uno de los oficiales de la ciudad y sirvo a la ciudadania a traves de nuestra oficina censal. Si necesitas localizar a alguien en concreto, creo que podre ayudarte. Puedo acceder a los registros a traves de metodos arcanos y una aguda disciplina mental.$B$BSi tienes algun asunto conmigo, ¡procede!', `text0_1_loc7` = '¡$GBienvenido:Bienvenida; a Ventormenta! Soy uno de los oficiales de la ciudad y sirvo a la ciudadania a traves de nuestra oficina censal. Si necesitas localizar a alguien en concreto, creo que podre ayudarte. Puedo acceder a los registros a traves de metodos arcanos y una aguda disciplina mental.$B$BSi tienes algun asunto conmigo, ¡procede!' WHERE `entry` = 3657;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mi odio es fuerte -resopla-, pero hay tiempo de contarte una historia, $R.$B$BHace mucho tiempo tuvo lugar una batalla entre dioses. Agamaggan, nuestro gran dios jabali, cayo a tierra tras ser derrotado en la Guerra de los Ancestros, con su sangre derramada y sus huesos partidos en dos. -resopla-$B$BAlli donde su sangre toco el suelo, se alzaron grandes puas, y su craneo adorna una de las cavernas del Sur.' WHERE `entry` = 3658;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quieres comprar una cucaracha?' WHERE `entry` = 3666;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Se me acaba el tiempo... a todos nosotros...', `text1_1_loc7` = 'Tengo mucho que hacer... tanto que hacer antes de que llegue la Peste...', `text2_1_loc7` = 'John, carino, ¿donde estas?', `text3_1_loc7` = '¿Que le pasa a todo el mundo? Tengo... frio...', `text4_1_loc7` = 'Shhh... ¿hueles algo?' WHERE `entry` = 3668;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Hermandad del torio no pierde el tiempo con hormigas. ¡Largate!' WHERE `entry` = 3673;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Catalogo de otono de la Hermandad del torio:$B$B¡En su interior encontraras las mejores ofertas para todas las necesidades de un aventurero! Incluso se indica donde recoger los componentes y suministros mas dificiles de encontrar.' WHERE `entry` = 3677;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a la primera linea, $C. Aunque los politicos conspiren e intriguen entre ellos, aqui veran que no se les permiten tales lujos. Este es el Baluarte, y aqui luchamos por nuestra continua supervivencia dia tras dia.$B$BSi has venido en busca de una oportunidad para demostrar que eres $Gun heroe:una heroina;, encontraras muchas oportunidades para hacerlo... especialmente porque dirigimos nuestra ofensiva a Andorhal y cualquiera fuerza maligna que controle a la Plaga alli.' WHERE `entry` = 3753;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a las fronteras, $C. El Reino de Ventormenta, con la ayuda de sus aliados, me ha enviado para combatir la creciente amenaza de la Plaga. Aqui, en el Alto del Orvallo, luchamos dia a dia por una supervivencia que pueda ser duradera.$B$BSi has venido hasta aqui en busca de una oportunidad para mostrarte como un heroe, no te faltaran las ocasiones de hacerlo... especialmente cuando lancemos nuestras ofensivas hacia Andorhal y contra cualquier fuerza maligna que controle ahi a los miembros de la Plaga.' WHERE `entry` = 3754;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me alegra ver a otros que combaten la Plaga en Stratholme, porque es aqui donde son mas fuertes.$B$BSaludos. Soy Aurius. Una vez fui paladin de la Mano de Plata, pero como otros que flaquearon en su fe... cai en desgracia. ¡La Plaga me sedujo y casi me transformo en un jinete de la muerte!$B$BPor ultimo, y sintiendo los peligros que me amenazaban, me refugie en esta capilla. Es un lugar sagrado donde el primer paladin, Uther el Iluminado, fue ordenado miembro de la Orden de la Mano Plata.' WHERE `entry` = 3755;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No me queda mucho tiempo en este mundo, pero si la Luz lo quiere, bastara para cumplir mi destino. Cuando te enfrentes al Baron Osahendido estare a tu lado.' WHERE `entry` = 3756;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Dejame. Mis heridas son graves y temo que podran conmigo. Gracias, $N. Morire aqui, pero gracias a ti... morire en paz.' WHERE `entry` = 3757;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No pongas a prueba mi paciencia, mortal.' WHERE `entry` = 3758;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ey colega. Ve hablar con mi maestro Gadrin alla. ¡Cuidado con el Zalazane, estar loco con el, y yo arreglar todo!' WHERE `entry` = 3794;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si, colega, yo asistente de Gadrin soy. Este ser trabajo voluntario que mi modo es de terminar con la Escuela de medicos brujos. Como tu ayudarme yo te ayudo gratis. ¿Que necesitar?' WHERE `entry` = 3795;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estoy... estoy gravemente herido...' WHERE `entry` = 3797;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Escucha, $R. Escuchame bien...$B$BLos jinetes de dragones vinieron por la noche, cabalgando a lomos del Vuelo Negro. Golpearon rapidamente y con una precision letal, llevandose a las jovenes crias, como yo, y escapando ocultos en el velo de la oscuridad.$B$BVi con horror como utilizaban a mis hermanos y hermanas para sus experimentos. Con nuestra esencia habrian de crear el Vuelo Cromatico.' WHERE `entry` = 3798;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nefarian habia ordenado destruir inmediatamente a los sujetos no aptos, pero esas bestias repugnantes no iban a hacerlo sin someternos antes a una gran tortura.$B$BCuando estuvimos destrozados, reunieron nuestros restos y nos arrojaron a estas bestias. Utilizaron nuestros cuerpos como instrumento para sus perversos juegos: fuimos arrojados a la lava o a algo aun peor: a los brazos abiertos y ansiosos de la sadica legion de ahi abajo.' WHERE `entry` = 3799;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Viniste justo cuando iban a arrojarme a esos demonios, te doy las gracias. ¿Hay algun modo en el que yo, o mejor dicho, nosotros, los del Vuelo Azul, podamos ayudarte?$B$BHas llegado hasta aqui, $R, y has arriesgado mucho. ¿Seguiras adelante para combatir a Nefarian y a su legion de Roca Negra?' WHERE `entry` = 3800;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si has venido para cazar dragones, se $Gbienvenido:bienvenida;. ¿Adonde debo enviar mis condolencias?' WHERE `entry` = 3801;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los espiritus me advirtieron de que vendrias, de que me buscabas por alguna razon, una razon que quizas ni tu mismo conocerias. Asi que... ¿sabes por que estas aqui?' WHERE `entry` = 3803;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Oh, hola! Mi nombre es Sarah. ¿Que puedo hacer por ti?' WHERE `entry` = 3804;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te doy la bienvenida, $N. Y tambien el Circulo Esmeralda.$B$BCon suerte juntos podremos ayudar a rejuvenecer y proteger este bosque antano poderoso. Muchas fuerzas pretenden destruirlo, desde dentro... y desde fuera.$B$BNo te dejes convencer por el poder que aqui reside... solo te corrompera, como lo ha hecho con toda esta tierra.' WHERE `entry` = 3807;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tu estomago no aguanta un viaje en grifo, ¿eh? Has tenido suerte, tenemos una alternativa con la que puedes mantener los pies en el suelo... bueno, mas o menos. Se trata del transporte de los gnomos, el Tranvia Subterraneo. Esta oportunamente situado en la parte trasera del Distrito de los Enanos.', `text0_1_loc7` = 'Tu estomago no aguanta un viaje en grifo, ¿eh? Has tenido suerte, tenemos una alternativa con la que puedes mantener los pies en el suelo... bueno, mas o menos. Se trata del transporte de los gnomos, el Tranvia Subterraneo. Esta oportunamente situado en la parte trasera del Distrito de los Enanos.', `text1_0_loc7` = '¿Te han hablado del Tren, verdad? ¡Es una autentica maravilla! Lo encontraras hacia el final del Distrito de los Enanos. Oh, y procura no sacar los brazos ni las pierdas del tren cuando este en marcha.', `text1_1_loc7` = '¿Te han hablado del Tren, verdad? ¡Es una autentica maravilla! Lo encontraras hacia el final del Distrito de los Enanos. Oh, y procura no sacar los brazos ni las pierdas del tren cuando este en marcha.' WHERE `entry` = 3813;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El libro habla la union de los dragones para hacer frente a otro dragon mas poderoso llamado Alamuerte. De ello se deduce que el Vuelo Cromatico casi habia sido desatado sobre el mundo. Los Aspectos estaban preparados.' WHERE `entry` = 3815;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Se ha establecido uno en el Bancal de los Mercaderes. Si sigues el camino que lleva al Sureste desde el banco, no deberias tener ningun problema en encontrarlo.', `text0_1_loc7` = 'Se ha establecido uno en el Bancal de los Mercaderes. Si sigues el camino que lleva al Sureste desde el banco, no deberias tener ningun problema en encontrarlo.', `text1_0_loc7` = 'Oh, la omnipresente obsesion por las riquezas materiales. La fortuna te sonrie, encontraras la casa de subastas en el Bancal de los Mercaderes.', `text1_1_loc7` = 'Oh, la omnipresente obsesion por las riquezas materiales. La fortuna te sonrie, encontraras la casa de subastas en el Bancal de los Mercaderes.' WHERE `entry` = 3833;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta en el Distrito de Mercaderes. Siguiendo recto desde la entrada de Ventormenta.', `text0_1_loc7` = 'Esta en el Distrito de Mercaderes. Siguiendo recto desde la entrada de Ventormenta.', `text1_0_loc7` = '¿$GOtro:Otra; negociante, eh? Puedes encontrar la casa de subastas en el Distrito de Mercaderes. Cerca del banco.$B', `text1_1_loc7` = '¿$GOtro:Otra; negociante, eh? Puedes encontrar la casa de subastas en el Distrito de Mercaderes. Cerca del banco.$B' WHERE `entry` = 3834;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay muchos establecimientos para tomar un trago en Ventormenta, pero solo en uno puedes pasar la noche. Dirigete a La Rosa aurea del Distrito de Mercaderes. He oido que el precio de una habitacion es bastante razonable, y las sabanas estan limpias.', `text0_1_loc7` = 'Hay muchos establecimientos para tomar un trago en Ventormenta, pero solo en uno puedes pasar la noche. Dirigete a La Rosa aurea del Distrito de Mercaderes. He oido que el precio de una habitacion es bastante razonable, y las sabanas estan limpias.', `text1_0_loc7` = 'Ah, La Rosa aurea... con sus comodas camas y los banos calientes... con solo pensar en esa posada me entra... aauuhhh... Eres $Gafortunado:afortunada; si vas hacia alla... la encontraras en la parte oeste del Distrito de Mercaderes... suaves almohadas, calidas mantas... aaauhhh...', `text1_1_loc7` = 'Ah, La Rosa aurea... con sus comodas camas y los banos calientes... con solo pensar en esa posada me entra... aauuhhh... Eres $Gafortunado:afortunada; si vas hacia alla... la encontraras en la parte oeste del Distrito de Mercaderes... suaves almohadas, calidas mantas... aaauhhh...' WHERE `entry` = 3860;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay buzones repartidos por toda la ciudad. Puedes encontrar uno enfrente de todas las casas de subastas, bancos o posadas.', `text0_1_loc7` = 'Hay buzones repartidos por toda la ciudad. Puedes encontrar uno enfrente de todas las casas de subastas, bancos o posadas.' WHERE `entry` = 3861;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, $C. Soy Jessir Arcolunar. He venido desde Darnassus buscando a dos amigos que vinieron aqui en busca de senales del Consejo de la Sombra en Frondavil.$B$BParece que esas infames criaturas que se reclaman culpables de gran parte del sufrimiento de la raza orca pretenden recobrar parte de su poder.$B$BMis dos amigos fueron a Jaedenar para mostrar de un modo u otro lo que estaba ocurriendo.$B$BEsperaba que estuvieran aqui a mi llegada, pero ya llevan varios dias de retraso.' WHERE `entry` = 3864;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Hola! Acabo de volver de explorar las Ruinas de Kel\'Theril, al Suroeste. Ahora pretendo investigar al lechucico salvaje que se encuentra sobre esa colina, en el Matorral del Ala del Buho.$B$B¿Has hablado con Eralas Cieloambar de Aldea Rut\'theran? el me pidio venir aqui y quizas tenga tambien una tarea para ti.' WHERE `entry` = 3866;
