UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No tenemos instructor de alquimia en Poblado Pezuna de Sangre, pero si en Cima del Trueno, al Norte. Sigue el camino hacia alli y cuando llegues a la cumbre pregunta a uno de los vigias de la Cima por las senas.', `text0_1_loc7` = 'No tenemos instructor de alquimia en Poblado Pezuna de Sangre, pero si en Cima del Trueno, al Norte. Sigue el camino hacia alli y cuando llegues a la cumbre pregunta a uno de los vigias de la Cima por las senas.', `text1_0_loc7` = 'Utilizar los frutos de la Madre Tierra para crear pociones que nos ayuden en nuestra eterna lucha es una empresa loable. Para aprender a hacerlo debes viajar hacia el Norte, hacia nuestro gran hogar de Cima del Trueno. Cuando llegues a la cumbre, busca la ayuda de uno de los vigias de la Cima, te dara indicaciones mas concretas.', `text1_1_loc7` = 'Utilizar los frutos de la Madre Tierra para crear pociones que nos ayuden en nuestra eterna lucha es una empresa loable. Para aprender a hacerlo debes viajar hacia el Norte, hacia nuestro gran hogar de Cima del Trueno. Cuando llegues a la cumbre, busca la ayuda de uno de los vigias de la Cima, te dara indicaciones mas concretas.' WHERE `entry` = 4058;
