UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si deseas aprender la senda de la botanica, debes dirigirte a Cima del Trueno. Sigue el camino que sale al norte de Poblado Pezuna de Sangre y coge el ascensor hasta la cumbre del alto. Cuando llegues pide mas ayuda a uno de los vigias de la Cima.', `text0_1_loc7` = 'Si deseas aprender la senda de la botanica, debes dirigirte a Cima del Trueno. Sigue el camino que sale al norte de Poblado Pezuna de Sangre y coge el ascensor hasta la cumbre del alto. Cuando llegues pide mas ayuda a uno de los vigias de la Cima.', `text1_0_loc7` = 'Los que pueden ensenarte herboristeria estan en el gran hogar de Cima del Trueno, hacia el Norte. Sigue el camino que sale del poblado y dirigete alli. En la base del risco, sube con el ascensor hasta la cumbre. Encontraras a muchos vigias de la Cima que te ayudaran a seguir tu camino.', `text1_1_loc7` = 'Los que pueden ensenarte herboristeria estan en el gran hogar de Cima del Trueno, hacia el Norte. Sigue el camino que sale del poblado y dirigete alli. En la base de los acantilados, sube con el ascensor hasta la cumbre. Encontraras a muchos vigias de la Cima que te ayudaran a seguir tu camino.' WHERE `entry` = 4064;
