UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si, llevo algun tiempo dirigiendo esta tienda. El negocio ha ido bastante bien ultimamente. Muchos aventureros nuevos pasan por aqui...' WHERE `entry` = 5561;
