UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Lantresor te mira como si viese a traves de ti.>$B$BPuede que haya otra solucion... Aceptare lo que pide tu lider, pero solo si haces exactamente lo que diga.$B$BEste trato podria beneficiarnos mucho a los dos.' WHERE `entry` = 9368;
