UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Hay que averiguar lo que ese siniestro grupo esta haciendo en el Poblado Grangol\'var!' WHERE `entry` = 9520;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tenemos una mision que cumplir, $Gamigo:amiga;: no dejarnos sorprender por nadie.$B$B¡Y eso significa que hay que actuar antes que ellos!' WHERE `entry` = 9521;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'He tenido que hacer algo mal en algun sitio para que me destinaran aqui.' WHERE `entry` = 9522;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡¿Has oido que han perdido el control total de Murmullo en el Laberignore into de las Sombras?!', `text0_1_loc7` = '¡¿Has oido que han perdido el control total de Murmullo en el Laberignore into de las Sombras?!', `text1_0_loc7` = 'No nos pagan lo suficiente por hacer esto. Pero este pueblo ruinoso sin duda es mejor que merodear dentro de Auchindoun.', `text1_1_loc7` = 'No nos pagan lo suficiente por hacer esto. Pero este pueblo ruinoso sin duda es mejor que merodear dentro de Auchindoun.', `text2_0_loc7` = 'Me enrole porque dijeron que me pagarian la academia. ¡Mi tio dice que podre ir la proxima temporada!', `text2_1_loc7` = 'Me enrole porque dijeron que me pagarian la academia. ¡Mi tio dice que podre ir la proxima temporada!', `text3_0_loc7` = '¡Eh, hola! Tu debes de ser $Gel nuevo:la nueva; recluta. Encantado de conocerte.$B$B¿Algun mensaje del Laberignore into de las Sombras de Auchindoun sobre si ya hemos recuperado el control de Murmullo?', `text3_1_loc7` = '¡Eh, hola! Tu debes de ser $Gel nuevo:la nueva; recluta. Encantado de conocerte.$B$B¿Algun mensaje del Laberignore into de las Sombras de Auchindoun sobre si ya hemos recuperado el control de Murmullo?', `text4_0_loc7` = 'Si te interesa, algunos iremos a tomar algo a Shattrath despues.', `text4_1_loc7` = 'Si te interesa, algunos iremos a tomar algo a Shattrath despues.', `text5_0_loc7` = '¿Te apetece participar con nosotros en la liga de la pelota de cuero? Jugamos de lunes a miercoles casi siempre.$B$BVente si quieres despues de este turno. Iremos a dar unas patadas.', `text5_1_loc7` = '¿Te apetece participar con nosotros en la liga de la pelota de cuero? Jugamos de lunes a miercoles casi siempre.$B$BVente si quieres despues de este turno. Iremos a dar unas patadas.', `text6_0_loc7` = 'Espero que este lugar sirva como base de la vanguardia de las operaciones del Consejo de la Sombra. Se que suena raro, pero echo de menos el Laberignore into de las Sombras.', `text6_1_loc7` = 'Espero que este lugar sirva como base de la vanguardia de las operaciones del Consejo de la Sombra. Se que suena raro, pero echo de menos el Laberignore into de las Sombras.', `text7_0_loc7` = '<Se acerca un poco.>$B$BEntre tu y yo. Estoy pensando en escaquearme una noche de estas, ¿me entiendes?', `text7_1_loc7` = '<Se acerca un poco.>$B$BEntre tu y yo. Estoy pensando en escaquearme una noche de estas, ¿me entiendes?' WHERE `entry` = 9523;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quien te crees para aparecer por aqui y hablarme sin mas? Soy el lider del Consejo de la Sombra en este pueblo. ¡Fuera de mi vista!$B$BSi te vuelvo a ver, sere la ultima persona que veas, ¿entendido?$B$BQuizas te gustaria que te enterraran en el laberignore into de Auchindoun, o te sacrificaran a Murmur.' WHERE `entry` = 9524;
