UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Asi que quieres ayudarnos a reparar el gran Exodar? He oido que pasaran anos antes de que El Exodar vuelva a estar operativo. Tienes dos horas. Es broma. Nuestros ingenieros estan en las partes bajas de la Grada de los Mercaderes. Ve con la Luz.', `text0_1_loc7` = '¿Asi que quieres ayudarnos a reparar el gran Exodar? He oido que pasaran anos antes de que El Exodar vuelva a estar operativo. Tienes dos horas. Es broma. Nuestros ingenieros estan en las partes bajas de la Grada de los Mercaderes. Ve con la Luz.' WHERE `entry` = 9536;
