UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace mucho tiempo, llevabamos monos de una sola pieza. Gracias a nuestros modernos sastres, ya no tenemos que vestir algo tan poco practico. Puedes encontrar a esos disenadores de vanguardia en la Grada de los Mercaderes.', `text0_1_loc7` = 'Hace mucho tiempo, llevabamos monos de una sola pieza. Gracias a nuestros modernos sastres, ya no tenemos que vestir algo tan poco practico. Puedes encontrar a esos disenadores de vanguardia en la Grada de los Mercaderes.' WHERE `entry` = 9559;
