UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gracias, amigos; os debo mi libertad. ¿Donde esta Taretha? Tenia la esperanza de volver a verla y darle las gracias antes de partir hacia las montanas.' WHERE `entry` = 9578;
