UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Por favor, no me digas nada. No quiero tener mas problemas con ellos.', `text0_1_loc7` = 'Por favor, no me digas nada. No quiero tener mas problemas con ellos.' WHERE `entry` = 9587;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si no has venido a ayudar a la causa, vete de aqui.' WHERE `entry` = 9595;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Con tu ayuda, ¡quizas podamos ganar!' WHERE `entry` = 9596;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Molino Tarren esta mas alla de estos arboles. La conmocion de Durnholde deberia darnos algo de tiempo para encontrar a Taretha antes de que todo el pais este inundado de soldados humanos. Echemos un vistazo a ese granero que esta cerca y luego dirijamonos al pueblo.' WHERE `entry` = 9597;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedes comprarme algo utilizando muestras Halaa de batalla e investigacion.' WHERE `entry` = 9598;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo instruirte en el oficio del desollador. ¿Te interesa?', `text0_1_loc7` = 'Puedo instruirte en el oficio del desollador. ¿Te interesa?' WHERE `entry` = 9599;
