UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Druida? Nadie aqui sigue ese camino... pero creo haber oido de alguien que coincide con esa descripcion cerca de El Exodar. Shalannius, creo.', `text0_1_loc7` = '¿Druida? Nadie aqui sigue ese camino... pero creo haber oido de alguien que coincide con esa descripcion cerca de El Exodar. Shalannius, creo.' WHERE `entry` = 10077;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Acteon es nuestro rastreador residente, y es de los buenos, ademas – las pieles que viste son prueba de su destreza. Su campamento esta al sureste de la Avanzada Azur.', `text0_1_loc7` = 'Acteon es nuestro rastreador residente, y es de los buenos, ademas – las pieles que viste son prueba de su destreza. Su campamento esta al sureste de la Avanzada Azur.' WHERE `entry` = 10078;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Que? ¿Quieres donar ropa a Lunargenta?$B$B¿Vas detras de un jinete de halcon, verdad? Muy bien, muy bien... haz este favor a la ciudad, y me asegurare de que tu reputacion mejore.' WHERE `entry` = 10079;
