UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, $N. ¿Necesitas llegar rapido al Alto de los Vientonegro, nuestra base en las Montanas Skethyl? Tengo una linda raya abisal preparada.$B$B¿Quieres ir ahora? ¡Tan solo di la palabra $gchico grande: nena;!' WHERE `entry` = 10978;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Buenos dias. Cuando hayas subido un poco mas en las filas de la Guardia del cielo Sha’tari, podre ofrecerte un vuelo directo a la Avanzada de la Guardia del Cielo, nuestro campamento en lo alto de las Montanas Filospada.' WHERE `entry` = 10980;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Bebe o vete!' WHERE `entry` = 10987;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ese es el espiritu, $gchico:chica;! ¡Estamos muy orgullosos del trabajo que estas haciendo para nosotros!$B$BY ya sabes, si quieres un viaje rapido a las Montanas Skethyl, baja a hablar con la cuidadora de la Guardia del cielo Irena. Ella se encargara.' WHERE `entry` = 10989;
