UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestra tecnico de eter de la Guardia del cielo y su compania estan estudiando la forja de cristal Bash’ir. ¡Necesita ayuda! ¡Ve al Alto Bash’ir, encuentra a la tecnico y mantenla a salvo!' WHERE `entry` = 11066;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Siento que has despejado el camino para mis hermanos. Mi conexion con ellos y con el templo aun es fuerte. ¿Quieres adentrarte mas?' WHERE `entry` = 11081;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Arakkoa invisibles... estan alrededor nuestro... observandonos... esperando!' WHERE `entry` = 11089;
