UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Buen trabajo! Ahora Arthas esta al corriente del grano apestado de Stratholme y esta a punto de comenzar la matanza. Pero algo no va bien. Siento una presencia extrana en esta linea temporal ademas de nosotros y eso debe de significar que el Vuelo Infinito anda por aqui.' WHERE `entry` = 12992;
