UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los enemigos se acercan por todos los flancos y todos los ancestros hablan de abandonar este puesto. Yo digo que deberiamos quedarnos y luchar como los guerreros que somos.' WHERE `entry` = 13035;
