UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Soy Ubungo. Represento a la tribu Zandalari.$B$BSe han puesto en marcha cosas malas, $R. Los Drakkari van a cavar su propia tumba con lo que planean.$B$BAlgunos trols no aprenden nunca.' WHERE `entry` = 13050;
