UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No lo soporto mas...$B$B¡Te matan y luego te comen! ¡Eso si tienes suerte!', `text0_1_loc7` = 'No lo soporto mas...$B$B¡Te matan y luego te comen! ¡Eso si tienes suerte!', `text1_0_loc7` = '¡Tienes que ayudarme a salir de aqui, $R!$B$B¡No quiero convertirme en uno de ellos!', `text1_1_loc7` = '¡Tienes que ayudarme a salir de aqui, $R!$B$B¡No quiero convertirme en uno de ellos!', `text2_0_loc7` = 'Desde que llegue aqui no he logrado pegar ojo ni comer.$B$BNo estoy hecho para la Cruzada...', `text2_1_loc7` = 'Desde que llegue aqui no he logrado pegar ojo ni comer.$B$BNo estoy hecha para la Cruzada...', `text3_0_loc7` = '<sollozo> Mi tio sirvio en El Alba Argenta.$B$B¿Como logro enfrentarse a estos seres inmundos?', `text3_1_loc7` = '<sollozo> Mi tio sirvio en El Alba Argenta.$B$B¿Como logro enfrentarse a estos seres inmundos?', `text4_0_loc7` = '¡Cada vez que cierro los ojos veo a los miembros de la Plaga rodeandome!$B$BTengo que salir de aqui...', `text4_1_loc7` = '¡Cada vez que cierro los ojos veo a los miembros de la Plaga rodeandome!$B$BTengo que salir de aqui...', `text5_0_loc7` = 'Voy a morir aqui... Lo se.', `text5_1_loc7` = 'Voy a morir aqui... Lo se.', `text6_0_loc7` = '¡¡Estamos condenados!!', `text6_1_loc7` = '¡¡Estamos condenados!!', `text7_0_loc7` = '¡Sal de ahi mientras puedas, $C!$B$BNo podemos ganar...', `text7_1_loc7` = '¡Sal de ahi mientras puedas, $C!$B$BNo podemos ganar...' WHERE `entry` = 13069;
