UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sin duda. Mal\'ganis esta dirigiendo a la Plaga desde la Plaza de los Cruzados, pero las verjas que llevan alli desde la entrada de la ciudad estan cerradas. Lo que no sabe es que un pequeno grupo todavia puede llegar a la Plaza pasando por el Ayuntamiento. ¿Te uniras a mi en este ataque?' WHERE `entry` = 13126;
