UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Corazon Frenetico nunca ser amigos de lenguas-grandes. ¡Tu $Gtonto:tonta; por volver aqui, cosa traidora!' WHERE `entry` = 13138;
