UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este puesto es seguro, $R.$B$BHace ya algun tiempo que se eliminaron a los enemigos de esta zona.' WHERE `entry` = 13156;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Gracias por liberarme, $R.$B$BAun estoy un poco mareada por el veneno. Creo que me quedare aqui hasta que recupere las fuerzas.' WHERE `entry` = 13157;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡El poder de los impios nos permite mandar sobre la propia muerte!' WHERE `entry` = 13172;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La sangre es vida... Y la vida de nuestros enemigos es tan facil de controlar como el flujo y reflujo de la sangre.' WHERE `entry` = 13173;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nosotros somos lo que viaja en el frio viento. El gelido escalofrio de la desesperacion... El helado abrazo de la muerte...' WHERE `entry` = 13174;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'A todos nos apena ver a los Drakkari destruyendose a si mismos de esta forma.$B$BEl poder que obtienen matando a los dioses naturales del territorio es transitorio.$B$BLo que ganen a corto plazo lo perderan para toda la eternidad.' WHERE `entry` = 13176;
