UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esto no se esta volviendo mas facil. Stratholme esta ardiendo. Debemos enfrentarnos a la Plaga y a las llamas para llegar hasta Mal\'Ganis. Preparaos.' WHERE `entry` = 13179;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Zim\'Torga puede proporcionarnos cierto nivel de proteccion, pero la presencia de la Plaga y las acciones de los Drakkari la han debilitado mucho.$B$BAhunae y yo nos ocuparemos del resto de nuestras defensas.' WHERE `entry` = 13180;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El hacha de Ahunae acabara con los Drakkari.$B$B¡Y tu te encargaras de todo lo que escape a mi hacha!' WHERE `entry` = 13234;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El tramposo Senor de la Magia Urom protege el tercer anillo. Aparecera solo e indefenso, ¡pero que no te enganen las apariencias! Urom es un poderoso conjurador que dirige un grupo de criaturas fantasmales. Buscale arriba.$BPuedo otorgarte el poder de invocar a un draco del Vuelo Rojo. Habla con Eternos o Verdisa si prefieres aprovechar el poder del Bronce o del Verde.' WHERE `entry` = 13237;
