UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Colmillo de Hielo me ha llevado en incontables batallas.$B$BNo dejes que su falta de armadura te engane. Es la osa mas dura que conozco.', `text1_1_loc7` = '¡No te retrases, moza!$B$B¡Hay que luchar!', `text2_1_loc7` = '¡Luchamos por la gloria de Brunnhildar!', `text3_1_loc7` = 'Colmillo de Hielo demuestra un gran valor al entrar en el campo de batalla sin armadura.$B$BQuizas su ejemplo pueda infundirte algo de valor, $N.' WHERE `entry` = 13565;
