UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He estado ausente durante demasiado tiempo, luchando por Azeroth para redescubrir mi pasado y reclamar mi trono. Con todos los desafios que ha tenido que superar la Alianza en mi ausencia, ver que hay heroes como tu defendiendo nuestra coalicion llena de gozo mi corazon.' WHERE `entry` = 13593;
