UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El explorador Natoescarcha intenta ponerse en pie mientras te acercas, pero vuelve a caer al suelo mientras se agarra una herida sin duda dolorosa en el costado.>' WHERE `entry` = 13611;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No pensaba que esto fuera a suceder, $Gzagal:zagala;. He perdido mucha sangre... es imposible que pueda volver.' WHERE `entry` = 13612;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una manada de arpias llego desde el valle. Las lideraba una grande llamada Sirana... No tenia ningun lugar al que escapar. Hacia mucho tiempo que no eran agresivas... No me lo esperaba... Baje la guardia... Ha sido culpa mia.' WHERE `entry` = 13613;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es un poco tarde... para eso... $Gmuchacho:muchacha;.$B$B<El explorador Natoescarcha tose algo de sangre sobre la nieve.>$B$BCoge... al joven que esta al mando por mi.$B$B<El explorador Natoescarcha se desploma mientras exhala su ultimo aliento.>' WHERE `entry` = 13614;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mira a tu alrededor y deja que la devastacion cause su efecto, $C. Hasta el ultimo trol que vivia aqui esta muerto o ha huido a los niveles superiores.$B$BY solo empeora.$B$BEn un acto de desesperacion, los trols Drakkari vivos sacrificaron a sus propios dioses animales para poder beber la sangre de los dioses y conseguir un poder increible. Aunque tuvieron exito al mantener a raya a la Plaga con su nueva fuerza, los resultados fueron catastroficos. La prueba es que el antiguo imperio de Zul\'Drak yace en ruinas.' WHERE `entry` = 13615;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Alejate un poco, ¿quieres? ¡Estoy intentando simular que sigo bajo el control del Rey Exanime!' WHERE `entry` = 13616;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gerk, Burr, Dargath: todos se han ido... Solo quedamos MacKellar y yo. Espero que hayas traido algunas buenas noticias.' WHERE `entry` = 13619;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Silencio por favor! ¡Cachorros durmiendo!' WHERE `entry` = 13628;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Se $Gbienvenido:bienvenida; entre nosotros, $C.' WHERE `entry` = 13637;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Con mi libertad llega una tormenta de venganza!' WHERE `entry` = 13639;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Escucha lo que tengo que contarte sobre nuestro enemigo antes de que empecemos. Querras estar $Gpreparado:preparada; cuando estemos machacando a la Plaga, ¿verdad?' WHERE `entry` = 13640;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Imagina todo el conocimiento y sabiduria que hay encerrados en Ulduar. ¡Alli podrian estar los mismos secretos de la vida!' WHERE `entry` = 13642;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Senor de batalla Ymirjar, Algar el Elegido reune a sus ejercitos de vargul en el Relicario de Dolor, al noreste de esta jaula.$B$BEl principe Navarius esta al otro lado de los Campos Muertos, al este de aqui, cerca de los grandes escalones que llevan al segundo nivel de Zul\'Drak.$B$BNo se conoce la posicion actual de Thrym, algo curioso teniendo en cuenta su tamano.$B$B¡Hay que ocuparse de todos!' WHERE `entry` = 13647;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Veneno... me arden las entranas...', `text0_1_loc7` = 'Veneno... me arden las entranas...' WHERE `entry` = 13650;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debemos atacar Jotunheim continuamente. ¡No darles ninguna oportunidad de recobrar el aliento, ni de dormir, ni de aceptar la derrota!$B$BTirion Vadin no podra conseguir lo que estas haciendo ni con todo el poder de la Crematoria, ni con todo el apoyo de la Cruzada Argenta.$B$BAhora, date prisa con lo tuyo, $N. Estan ocurriendo demasiadas cosas como para pararse a charlar.' WHERE `entry` = 13654;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando estes $Glisto:lista; te colocare sobre mi cabeza y marcharemos hacia el corazon de la Plaga en Zul\'Drak. ¡Deberas dirigirme para aplastar y destruir a nuestros enemigos!' WHERE `entry` = 13656;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Estamos desbordados, $R! ¡Estate $Gatento:atenta;!' WHERE `entry` = 13658;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Espada de ebano esta en deuda contigo por tu ayuda en la toma de La Camara de las Sombras. Tenemos suerte de tenerte entre nosotros.$B$BMientras la Alianza y la Horda luchan entre ellos, haciendo el trabajo del Rey Exanime, nosotros nos mantendremos centrados en la tarea que nos ocupa.$B$BDonde la Cruzada Argenta puede dudar y pensar dos veces en la mejor manera de aplastar al enemigo, nosotros no tendremos dificultades morales.$B$BTu seras nuestro instrumento de venganza, $N.' WHERE `entry` = 13659;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te arrancare todas las extremidades.', `text1_1_loc7` = '¿Me desafias?', `text2_1_loc7` = 'No pareces tan duro.', `text3_1_loc7` = '¿Crees que tienes lo que hace falta?' WHERE `entry` = 13660;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Bienvenida, companera de batallas. Puedo ayudarte a recuperar companeros perdidos.' WHERE `entry` = 13661;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Si no puedes ayudar de otro modo, mata a estas cosas!' WHERE `entry` = 13671;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo ocuparme de mi mismo, $N.' WHERE `entry` = 13673;
