UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No me molestes, estorbo. Estoy estudiando el combate de ineptos de estos llamados luchadores.$B$BYo les ganare a todos y despues ascendere para servir al Rey Exanime.' WHERE `entry` = 13678;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Parece que podrias hacernos ganar algo de dinero. ¿Que tal una vuelta por dentro del Anfiteatro de la Angustia, colega?' WHERE `entry` = 13679;
