UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta copia del Cuerno de Hodir se alza en memoria del Rey Arngrim y todos los demas que perecieron en la batalla de Truenotono.' WHERE `entry` = 13685;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Que puedo hacer por ti?' WHERE `entry` = 13690;
