UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Preparate, $R.$B$BLos nagas pueden atacar en cualquier momento.', `text1_0_loc7` = 'Esta region es uno de los epicentros de un cataclismo que amenaza con desgarrar nuestro mundo.$B$BSi conseguimos sobrevivir, debemos intentar descubrir su causa.', `text2_0_loc7` = 'Los ataques que han sufrido estos barcos son claramente intencionados.$B$BDebemos descubrir que interes tienen los nagas en todo esto.'  WHERE `entry` = 15569;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El continuo sacrificio del Veredicto Cinereo y de los heroes como tu nos acerca a todos a la victoria final. He venido a la Ciudadela de la Corona de Hielo para prestar mi ayuda a aquellos que luchan contra la Plaga. ¡Juntos acabaremos con la tirania del Rey Exanime!' WHERE `entry` = 15602;
