UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Os hara falta algo mas que coraje para derrotar al Rey Exanime. Buena suerte.' WHERE `entry` = 15604;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Habeis tomado una decision. Ahora os corresponde vivir o morir por ella.' WHERE `entry` = 15605;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ahora que la Ofensiva Grito de Guerra esta en la Ciudadela de la Corona de Hielo, la victoria esta a nuestro alcance! ¡La muerte del Rey Exanime dara gloria a la Horda!' WHERE `entry` = 15606;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Claro! ¡Si que te habia visto aqui antes! ¿Como he podido olvidarlo? Puedo teletransportarte a Stratholme ahora si quieres.' WHERE `entry` = 15704;
