UPDATE `locales_page_text` SET `Text_loc7`='¿Estoy perdiendo el juicio? Hoy le abri la cabeza a uno de mis patrulleros mas fieles para poder ver mejor dentro de su alma. Mientras observaba detenidamente su conducto respiratorio destrozado, pense: ¡No he comido nada en varios dias!' WHERE `entry`='1153';
