UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">Capitulo 1: LA TORRE DEL NORTE</H1><BR/><BR/><IMG src=\"Interface\Pictures\11482_crystals_mini_north\" align=\"left\"/><P align=\"right\">La torre del norte<BR/>acepta cristales<BR/>amarillos, rojos,<BR/>y verdes.<BR/></P><BR/><BR/><BR/><BR/><P align=\"left\">La torre se centra en la energia que se puede generar usando los cristales amarillos como fuente principal de poder.</P></BODY></HTML>' WHERE `entry`='1511';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">Combinacion de cristales del norte</H1><BR/><P align=\"left\">Hay dos efectos posibles: uno produce dano y el otro lo anula. </P><BR/><P align=\"left\">CARGA DE CRISTAL: Requiere cristales rojos y amarillos. <BR/>Esto produce una gran explosion.</P><BR/><P align=\"left\">CRISTAL DE RESTAURACIoN: Requiere cristales amarillos y verdes. <BR/>Este cristal te proporcionara un aura de curacion.</P></BODY></HTML>' WHERE `entry`='1512';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">DIAGRAMA DE LA TORRE DEL NORTE</H1><BR/><IMG src=\"Interface\Pictures\11482_crystals_north\"/></BODY></HTML>' WHERE `entry`='1513';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">Capitulo 2: LA TORRE DEL ESTE</H1><BR/><BR/><IMG src=\"Interface\Pictures\11482_crystals_mini_east\" align=\"left\"/><P align=\"right\">La torre del este<BR/>acepta cristales<BR/>azules, amarillos<BR/>y verdes.<BR/></P><BR/><BR/><BR/><BR/><P align=\"left\">La torre se centra en la energia que se puede generar usando los cristales azules como fuente principal de poder.</P></BODY></HTML>' WHERE `entry`='1514';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">Combinacion de cristales del este</H1><BR/><P align=\"left\">Hay dos efectos posibles: uno proporciona puntos adicionales para el espiritu y el otro crea un escudo de ataque.</P><BR/><P align=\"left\">FUERZA DE LOS CRISTALES: Requiere el uso de cristales azules y verdes.<BR/>Esto aumentara tu espiritu.</P><BR/><P align=\"left\">AGUJA DE CRISTAL: Requiere cristales azules y amarillos.<BR/>Creara un escudo que hara dano a los atacantes.</P></BODY></HTML>' WHERE `entry`='1515';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">DIAGRAMA DE LA TORRE DEL ESTE</H1><BR/><IMG src=\"Interface\Pictures\11482_crystals_east\"/></BODY></HTML>' WHERE `entry`='1516';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">DIAGRAMA DE LA TORRE DEL ESTE</H1><BR/><IMG src=\"Interface\Pictures\11482_crystals_east\"/></BODY></HTML>' WHERE `entry`='1530';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">Capitulo 3: LA TORRE DEL OESTE</H1><BR/><BR/><IMG src=\"Interface\Pictures\11482_crystals_mini_west\" align=\"left\"/><P align=\"right\">La torre del oeste<BR/>acepta cristales<BR/>rojos, verdes<BR/>y azules.<BR/></P><BR/><BR/><BR/><BR/><P align=\"left\">La torre se centra en la energia que se puede generar usando los cristales rojos como fuente principal de poder.</P></BODY></HTML>' WHERE `entry`='1531';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">Combinacion de cristales del oeste</H1><BR/><P align=\"left\">Hay dos efectos posibles: uno que incrementa tu proteccion de dano y otro que debilita las defensas del enemigo.</P><BR/><P align=\"left\">SALA DE CRISTAL: Requiere cristales rojos y verdes.<BR/>Esto aumentara tu armadura.</P><BR/><P align=\"left\">COSECHA DE CRISTALES: Requiere cristales azules y rojos.<BR/>Esto puede reducir la armadura de un objetivo especifico.</P></BODY></HTML>' WHERE `entry`='1532';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">DIAGRAMA DE LA TORRE DEL OESTE</H1><BR/><IMG src=\"Interface\Pictures\11482_crystals_west\"/></BODY></HTML>' WHERE `entry`='1533';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">DIAGRAMA DE LA TORRE DEL OESTE</H1><BR/><IMG src=\"Interface\Pictures\11482_crystals_west\"/></BODY></HTML>' WHERE `entry`='1550';
UPDATE `locales_page_text` SET `Text_loc7`='¡Saludos, aventureros!$B$BYo, Krinkle Buenacero, necesito gente dispuesta a arriesgarse para realizar algunas de mis tareas mas arduas. Debido a mis numerosas obligaciones y al volumen de trabajo que he logrado asumir, garantizo que cualquiera que sea lo suficientemente valiente como para recoger esta lista de objetos y traermelos intactos sera generosamente recompensado.$B$BLa lista se detalla en las siguientes paginas...' WHERE `entry`='1551';
UPDATE `locales_page_text` SET `Text_loc7`='Patas de cristal solido$B$BLas aranas cristalderma de la Garganta de Fuego estan hechas de un material muy similar al cristal. Sus patas, si no se danan cuando se las mata, constituyen excelentes conductores en algunos experimentos de ingenieria de algunos de mis companeros.$B$BVoy a necesitar unas cuantas para realizar pruebas. Las aranas, al tener ocho patas, no deberian suponer problema alguno para un fornido guerrero o para un mago experimentado.' WHERE `entry`='1552';
UPDATE `locales_page_text` SET `Text_loc7`='Un paquete que no llega a tiempo a su destino$B$BHe contratado a alguien a quien crei un piloto fiable para que me trajera un paquete desde Theramore. Por lo visto, me equivoque. Y, a pesar de que el piloto vivio para contarla, abandono su zeppelin y todo su cargamento para salvar su pellejo.$B$BLo cierto es que necesito ese paquete. Segun me ha dicho Beezil, se estrello al oeste de la bahia en el marjal Revolcafango. Yo baje justo despues de que el atravesara las colinas, bordeando la costa.$B$BMmm... nunca me dijo cual fue la causa de que se estrellara...' WHERE `entry`='1553';
UPDATE `locales_page_text` SET `Text_loc7`='Flameberge equilibrada de Buenacero$B$BFinalmente, deseo recuperar una de mis obras mas finas creadas hasta la fecha. Esta Flameberge fue una espada increible que otorgue a quien consideraba un honorable y excelente guerrero. Si, pero ¿no fue derrotado por un murloc llamado Jarquai? Mas o menos eso ocurrio.$B$BOcurrio cuando viajaba por la costa del Pantano de las Penas cuando tuvo la idea de atacar a la criatura. Fracaso en su intento y, aparentemente, huyo, pero no sin antes abandonar el arma a merced del murloc.' WHERE `entry`='1554';
UPDATE `locales_page_text` SET `Text_loc7`='Flameberge equilibrada de Buenacero$B$BFinalmente, deseo recuperar una de mis obras mas finas creadas hasta la fecha. Esta Flameberge fue una espada increible que otorgue a quien consideraba un honorable y excelente guerrero. Si, pero ¿no fue derrotado por un murloc llamado Jarquai? Mas o menos eso ocurrio.$B$BOcurrio cuando viajaba por la costa del Pantano de las Penas cuando tuvo la idea de atacar a la criatura. Fracaso en su intento y, aparentemente, huyo, pero no sin antes abandonar el arma a merced del murloc.' WHERE `entry`='1555';
UPDATE `locales_page_text` SET `Text_loc7`='Buscame esos objetos y regresa a Gadgetzan con todos ellos, junto con este libro de contabilidad, y te recompensare.' WHERE `entry`='1556';
