UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS DE OFICIALES DE LA HORDA</H1><BR/><P align=\"center\">Parte 1</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank14\" align=\"left\"/><P align=\"right\">Gran Senor de la Guerra</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank13\" align=\"left\"/><P align=\"right\">Senor de la Guerra</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank12\" align=\"left\"/><P align=\"right\">Gran general</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank11\" align=\"left\"/><P align=\"right\">General</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank10\" align=\"left\"/><P align=\"right\">Campeon</P><BR/></BODY></HTML>' WHERE `entry`='2658';
UPDATE `locales_page_text` SET `Text_loc7`='Si echas un vistazo en el interior del Ojo, apareceran en tu mente imagenes de una gran batalla.$b$bEste terrible conflicto parece ocurrir en algun lugar de Lordaeron.$b$bSolo se distingue una solitaria figura femenina, de pie y rodeada de miles de cadaveres, esquivando una marea de la Plaga. No tiene esperanza alguna de hacer frente a la marea enemiga.$b$bDe sus manos brotan haces de luz, abriendose camino entre centenares de no-muertos.' WHERE `entry`='2659';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS DE SOLDADOS DE LA HORDA</H1><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank05\" align=\"left\"/><P align=\"right\">Gran capataz</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank04\" align=\"left\"/><P align=\"right\">Capataz primero</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank03\" align=\"left\"/><P align=\"right\">Capataz</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank02\" align=\"left\"/><P align=\"right\">Bruto</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank01\" align=\"left\"/><P align=\"right\">Explorador</P><BR/></BODY></HTML>' WHERE `entry`='2660';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS MILITARES DE LA HORDA Y DE LA ALIANZA</H1><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRankHorde\" align=\"left\"/><BR/><P align=\"right\">¡Throm\'ka! En este</P><P align=\"right\">libro encontraras</P><P align=\"right\">muchas listas de</P><P align=\"right\">rangos militares</P><P align=\"right\">de la Horda y</P><P align=\"right\">de la insignificante Alianza.</P><P align=\"right\">Primero aparecen los rangos de la</P><P align=\"right\">Horda y luego de la Alianza,</P><P align=\"right\">cada una con listados de rangos de oficiales y de soldados. Esta ordenada de tal manera que los mas poderosos aparecen al principio y los mas debiles al final.</P></BODY></HTML>' WHERE `entry`='2661';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS DE OFICIALES DE LA HORDA</H1><BR/><P align=\"center\">Parte 1</P><IMG src=\"Interface\PvPRankBadges\PvPRank14\" align=\"left\"/><BR/><P align=\"right\">Gran Senor de la Guerra</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank13\" align=\"left\"/><BR/><P align=\"right\">Senor de la Guerra</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank12\" align=\"left\"/><BR/><P align=\"right\">Gran general</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank11\" align=\"left\"/><BR/><P align=\"right\">General</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank10\" align=\"left\"/><BR/><P align=\"right\">Campeon</P><BR/><BR/></BODY></HTML>' WHERE `entry`='2662';
