UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS DE LOS OFICIALES DE LA ALIANZA</H1><BR/><P align=\"center\">Parte 1</P><IMG src=\"Interface\PvPRankBadges\PvPRank14\" align=\"left\"/><BR/><P align=\"right\">Gran mariscal</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank13\" align=\"left\"/><BR/><P align=\"right\">Mariscal de campo</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank12\" align=\"left\"/><BR/><P align=\"right\">Mariscal</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank11\" align=\"left\"/><BR/><P align=\"right\">Comandante</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank10\" align=\"left\"/><BR/><P align=\"right\">Teniente coronel</P><BR/><BR/></BODY></HTML>' WHERE `entry`='2665';
UPDATE `locales_page_text` SET `Text_loc7`='Al parecer, todo para nada. Poco despues, en torno a la sacerdotisa se agolpan los cadaveres de los campesinos que la rodeaban...$b$bLos caidos se han levantado.' WHERE `entry`='2666';
UPDATE `locales_page_text` SET `Text_loc7`='Simone la Seductora:$B$BEncontraras a Simone contaminando el crater Un\'Goro. No te dejes enganar por su disfraz. Aproximate a ella con cuidado y retala a un combate.' WHERE `entry`='2671';
UPDATE `locales_page_text` SET `Text_loc7`='Klinfran el Enloquecido:$B$BKlinfran vaga por las Estepas Ardientes. Acercate a el con precaucion y obligale a mostrarte su verdadera forma.' WHERE `entry`='2672';
UPDATE `locales_page_text` SET `Text_loc7`='Solenor el Destripador:$B$BEn los aridos parajes de Silithus vaga el desalmado Solenor el Destripador. Indudablemente, aparecera disfrazado y, definitivamente, es un peligro. Acercate a el y obligale a transformarse.' WHERE `entry`='2673';
UPDATE `locales_page_text` SET `Text_loc7`='Artorius el Fatidico:$B$BArtorius trae la corrupcion y la maldicion a todo lo que toca en Cuna del Invierno. Encuentralo y matalo.' WHERE `entry`='2674';
UPDATE `locales_page_text` SET `Text_loc7`='Ten cuidado, cazador, debes combatir a estos demonios segun sus reglas. Si sienten que no son superiores a ti, sin duda huiran.$B$BVE SOLO.' WHERE `entry`='2675';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS MILITARES DE LA ALIANZA</H1><BR/><P align=\"center\">OFICIALES</P><BR/><P align=\"center\">Gran mariscal</P><P align=\"center\">Mariscal de campo</P><P align=\"center\">Mariscal</P><P align=\"center\">Comandante</P><P align=\"center\">Teniente coronel</P><P align=\"center\">Campeon caballero</P><P align=\"center\">Capitan caballero</P><P align=\"center\">Teniente caballero</P><P align=\"center\">Caballero</P><BR/><P align=\"center\">TROPA</P><BR/><P align=\"center\">Alferez</P><P align=\"center\">Sargento primero</P><P align=\"center\">Sargento</P><P align=\"center\">Cabo</P><P align=\"center\">Soldado</P><BR/></BODY></HTML>' WHERE `entry`='2676';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS MILITARES DE LA HORDA</H1><BR/><P align=\"center\">OFICIALES</P><BR/><P align=\"center\">Gran Senor de la Guerra</P><P align=\"center\">Senor de la Guerra</P><P align=\"center\">Gran general</P><P align=\"center\">General</P><P align=\"center\">Campeon</P><P align=\"center\">Centurion</P><P align=\"center\">Legionario</P><P align=\"center\">Guardia de sangre</P><P align=\"center\">Guardia de piedra</P><BR/><P align=\"center\">TROPA</P><BR/><P align=\"center\">Gran capataz</P><P align=\"center\">Capataz primero</P><P align=\"center\">Capataz</P><P align=\"center\">Bruto</P><P align=\"center\">Explorador</P></BODY></HTML>' WHERE `entry`='2677';
UPDATE `locales_page_text` SET `Text_loc7`='Para vincular a un corcel nefasto, debes realizar estas tres tareas:$B$BCrear un circulo de invocacion superior.$B$BDentro del circulo, abre un portal hacia Xoroth y conduce al terrorecus a traves del portal.$B$BDerrota al corcel nefasto y luego domina su espiritu.$B$BEn las siguientes paginas encontraras una explicacion detallada de como realizar esta tarea. Puede que no sea facil, pero has demostrado ser muy capaz. Sin duda, con concentracion y habilidad, el corcel nefasto sera tuyo.$B$BContinua leyendo,$BMor\'zul Sangredoble' WHERE `entry`='2691';
UPDATE `locales_page_text` SET `Text_loc7`='Instrumentos de un ritual$B$BAntes de que inicies tu trabajo, deberas disponer de los siguientes instrumentos magicos:$B$BJarra de J\'eevee$BMagnetita negra$BGlifos xorothianos$B$BMi sirviente Gorzeeki lo tiene y te lo puede ofrecer a buen precio. No intentes siquiera realizar el ritual si no dispones de todos estos instrumentos. Todos resultan esenciales.$B$BEn las siguientes paginas, describire como utilizar cada uno de ellos.' WHERE `entry`='2692';
UPDATE `locales_page_text` SET `Text_loc7`='Circulo de vinculo superior$B$BEs preciso crear un circulo de vinculo superior en un lugar en el que la magia tenga fuerza. En medio de las ruinas de Eldre\'Thalas, encontraras un sitio de estas caracteristicas, tambien llamado La Masacre. En Eldre\'Thalas se encuentra prisionero un ser de gran poder, Immol\'thar. En el pedestal de su prision debes realizar el ritual para crear el circulo.$B$BEncuentra tu camino hasta el pedestal y luego deja que J\'eevee salga de su tarro.' WHERE `entry`='2693';
UPDATE `locales_page_text` SET `Text_loc7`='La Campana, la Rueda y la Vela$B$BCuando liberes a J\'eevee, este colocara la Campana, la Rueda y la Vela, y aparecera un circulo. Este es el comienzo del ritual. Debes estar alerta. Los objetos antes mencionados transmiten una gran cantidad de energia y son propensos a fallar. Cuando esto ocurra, deberas usar la magnetita para reiniciarlos, antes de que fracase todo el ritual.$B$BSi los tres objetos han fallado antes de que logres reiniciarlos, el ritual terminara y deberas comenzar de cero.' WHERE `entry`='2694';
UPDATE `locales_page_text` SET `Text_loc7`='Ademas de transmitir las energias del ritual, la Campana, la Rueda y la Vela tienen propiedades unicas.$B$BCuando suena, la Campana de Dethmoora confiere vigor y energia a los brujos que esten dentro del circulo.$B$BCuando gira, la Rueda de la Marcha Negra protege de cualquier dano a los que estan dentro del circulo.$B$BLa Vela del Apocalipsis, cuando arde, envia una extrana energia a los enemigos que entran en el circulo.$B$BGracias a estos dones, es muy importante que todos estos objetos funcionen durante el ritual.' WHERE `entry`='2695';
UPDATE `locales_page_text` SET `Text_loc7`='Tienes que tener en cuenta que la magnetita, utilizada para reiniciar la Campana, la Rueda o la Vela si fracasan, requiere fragmentos de alma. Cada vez que reinicies un objeto ritual con la magnetita, se consumira uno de tus fragmentos de alma, asi que asegurate de tener suficientes reservas de fragmentos antes de iniciar el ritual.' WHERE `entry`='2696';
UPDATE `locales_page_text` SET `Text_loc7`='Como completar el ritual$B$BPuedes anotar tus progresos utilizando las runas magicas situadas a lo largo del borde del circulo. Cuando aparezcan nueve runas, el ritual se habra completado y veras como surge la energia desde el circulo recargado.$B$BDesde aqui, podras convocar a los glifos xorothianos y abrir un portal hacia Xoroth para hacer pasar un corcel nefasto por el.$B$BDerrota al corcel nefasto y libera su espiritu. Enfrentate al espiritu y este quedara embelesado. Entonces, seras recompensado con el secreto de su invocacion.' WHERE `entry`='2697';
UPDATE `locales_page_text` SET `Text_loc7`='Como completar el ritual$B$BPuedes anotar tus progresos utilizando las runas magicas situadas a lo largo del borde del circulo. Cuando aparezcan nueve runas, el ritual se habra completado y veras como surge la energia desde el circulo recargado.$B$BDesde aqui, podras convocar a los glifos xorothianos y abrir un portal hacia Xoroth para hacer pasar un corcel nefasto por el.$B$BDerrota al corcel nefasto y libera su espiritu. Enfrentate al espiritu y este quedara embelesado. Entonces, seras recompensado con el secreto de su invocacion.' WHERE `entry`='2711';
UPDATE `locales_page_text` SET `Text_loc7`='Aqui yace Durotan, primer jefe del clan Lobo Gelido y padre de Thrall, nuestro honorable Jefe de Guerra. Fue el mas valiente de los nuestros, traicionado por aquellos que querian ver esclavizado a nuestro pueblo. Durotan dio su vida para que ganaramos la libertad. Lo glorificamos: su legado ha llegado hasta nosotros a traves de su hijo.$B$BDrek\'Thar, Clarividente de los Lobo Gelido' WHERE `entry`='2731';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS MILITARES DE LA HORDA</H1><BR/><P align=\"center\">OFICIALES</P><BR/><P align=\"center\">Gran Senor de la Guerra</P><P align=\"center\">Senor de la Guerra</P><P align=\"center\">Gran General</P><P align=\"center\">General</P><P align=\"center\">Campeon</P><P align=\"center\">Centurion</P><P align=\"center\">Legionario</P><P align=\"center\">Guardia de sangre</P><P align=\"center\">Guardia de piedra</P><BR/><P align=\"center\">SOLDADOS</P><BR/><P align=\"center\">Gran capataz</P><P align=\"center\">Capataz primero</P><P align=\"center\">Capataz</P><P align=\"center\">Bruto</P><P align=\"center\">Explorador</P><BR/></BODY></HTML>' WHERE `entry`='2770';
UPDATE `locales_page_text` SET `Text_loc7`='Pelando la cebolla$BGuia para desmantelar a los Pico Tormenta$B-Por Drek\'Thar$B$BEn estas paginas encontraras amplios conocimientos sobre tacticas de guerra y politica. Un soldado instruido es un soldado que se apoya en la experiencia. Utiliza este libro como guia en tu batalla por el Valle de Alterac.' WHERE `entry`='2771';
UPDATE `locales_page_text` SET `Text_loc7`='Capitulo 1 - El frente$B$BDesmantelar el ejercito de los Pico Tormenta es una tarea similar a la de pelar una cebolla. Para llegar al nucleo, hay que empezar eliminando las capas exteriores.$B$BLas lineas del frente, que comprenden fundamentalmente a los montaraces de Pico Tormenta y los Centinelas de la Alianza, estan ligadas al bunker del capitan. Es el capitan Balinda Corapernal el que da poder a estas unidades y proporciona los refuerzos necesarios cuando las lineas estan bajo sitio. Golpea al capitan y las lineas del frente se quebraran.' WHERE `entry`='2772';
UPDATE `locales_page_text` SET `Text_loc7`='Capitulo 2 - Las Torres Gemelas$B$BNo te equivoques, Vanndar Pico Tormenta es un enemigo astuto y, ciertamente, no es nada tonto. Una vez rota la linea del frente, es preciso destruir los objetivos defensivos de caracter secundario. Se trata de las torres gemelas de Piedrahogar y Ala Gelida, que controlan los Soldados de Pico Tormenta, lejos de Dun Baldar y de las patrullas Pico Tormenta.' WHERE `entry`='2773';
UPDATE `locales_page_text` SET `Text_loc7`='Ten cuidado, Soldado, ya que ambas torres estan muy bien fortificadas y estan capitaneadas por uno de los Comandantes de elite de Vanndar. Cuando tus fuerzas rompan las fortificaciones, asegurate de que el Comandante que esta en su interior haya sido... silenciado. Esta ultima tarea tambien forma parte del desmantelamiento de Pico Tormenta.' WHERE `entry`='2774';
UPDATE `locales_page_text` SET `Text_loc7`='Capitulo 3 - Los cuatro Comandantes$B$BLa tercera capa de cebolla del ejercito Pico Tormenta comprende a los cuatro Comandantes. A este respecto, Vanndar ha imitado a nuestras gloriosas defensas de Lobo Gelido. Logicamente, el te diria otra cosa... Pero me estoy desviando de la cuestion.$B$BLos cuatro Comandantes controlan el flujo y reflujo de los Soldados de Pico Tormenta que defienden Dun Baldar con valentia. Ocupate de silenciarlos y los Soldados Pico Tormenta caeran.$B$BAhora solo queda una capa por pelar.' WHERE `entry`='2775';
UPDATE `locales_page_text` SET `Text_loc7`='Capitulo 4 - Los bunkeres de Dun Baldar$B$BComo ya habras advertido, las capas defensivas de Pico Tormenta estan colocadas de tal manera que impidan que un movimiento energico pueda desmantelar todo el ejercito. Antes de que preguntes, si, coincide exactamente con la forma en que estan desplegadas nuestras fuerzas... y no, no pienso modificar nuestra estructura defensiva. Ellos nos copiaron a nosotros, ¿por que habria de cambiar?' WHERE `entry`='2776';
UPDATE `locales_page_text` SET `Text_loc7`='¿Por donde iba? Ah, si, los bunkeres de Dun Baldar (situados en Dun Baldar), controlan a los defensores de Pico Tormenta, las unidades de guardia de elite y los hombres de mayor confianza de Vanndar. Destruye las dos torres y los refuerzos enviados para ayudar a Vanndar dejaran de existir.' WHERE `entry`='2777';
UPDATE `locales_page_text` SET `Text_loc7`='Epilogo$B$BUna vez que hayas hecho todo esto, te queda el centro de la cebolla: El dulce nucleo. Vanndar Pico Tormenta estara solo e indefenso. Las reglas de conducta militar requieren que, antes de realizar tareas de limpieza, le preguntemos si desea rendirse. En cuanto le veas, asegurate de mencionarle lo de la rendicion y luego... matalo.' WHERE `entry`='2778';
UPDATE `locales_page_text` SET `Text_loc7`='La alcachofa de los Lobo Gelido: Relatos sobre la gloria de Pico Tormenta$BPor Vanndar Pico Tormenta$B$B¿Que son los Lobo Gelido? La respuesta es sencilla: Los Lobo Gelido son salvajes que intentan acabar con nuestra imprescindible soberania imperial. Al igual que ocurre con una alcachofa, los Lobo Gelido cuentan con un espinoso conjunto de defensas acorazadas y tambien, como ocurre con la alcachofa, una vez que se extrae la capa exterior, el corazon queda expuesto y listo para ser engullido con una suave salsa de ajo.$B$B¡Delicioso!' WHERE `entry`='2779';
UPDATE `locales_page_text` SET `Text_loc7`='Capitulo 1 - El frente$B$BLas lineas del frente de las defensas de los Lobo Gelido estan formadas por los guerreros Lobo Gelido. Estos guerreros estan desplegados en las lineas del frente, desde la fortaleza del capitan Galvangar: Baluarte Sangre Fria. La forma mas eficaz de eliminar esta capa de las defensas consiste en destruir el Baluarte Sangre Fria.$B$BPon la fortaleza bajo sitio y destruye al capitan Galvangar. Una vez que lo hayas logrado, las lineas del frente se desmoronaran.' WHERE `entry`='2780';
UPDATE `locales_page_text` SET `Text_loc7`='Capitulo 2 - Sangre Fria y Torre de la Punta$B$BUna vez desaparecida la primera capa, la segunda capa de la \"alcachofa\" quedara expuesta. Mediante la destruccion de las torres Lobo Gelido, situadas cerca de las lineas del frente, lograras neutralizar a los Legionarios y a las patrullas que se apinan en la retaguardia.$B$BLas torres estan bien fortificadas y cada una de ellas alberga uno de los Comandantes de elite de Drek\'Thar. Hay que eliminarlos. De esta manera, el camino para desmantelar las defensas Lobo Gelido quedara expedito.' WHERE `entry`='2781';
UPDATE `locales_page_text` SET `Text_loc7`='Capitulo 3 - Los cuatro Comandantes$B$BSi has seguido sabiamente esta guia, en este momento, dos de los Comandantes de Drek\'Thar deberian estar muertos. ¡Buen trabajo, Soldado! No obstante, su resolucion continua siendo un problema. Debes completar la tarea de romper la cadena de mando, acabando con los dos Comandantes restantes.$B$BUna vez que hayan desaparecido los cuatro Comandantes, los legionarios Lobo Gelido que cuidan el Bastion Lobo Gelido quedaran aislados y sin superiores. ¡La fruta estara madura!' WHERE `entry`='2782';
UPDATE `locales_page_text` SET `Text_loc7`='Capitulo 4 - Las torres Lobo Gelido del este y el oeste$B$BSolo te queda una capa para alcanzar la victoria. Las unidades de guardianes mas poderosas y en quienes mas confia Drek\'Thar: Los Guardianes Lobo Gelido.$B$BLos Guardianes Lobo Gelido proceden de pelotones de la reserva situados en las torres Lobo Gelido del este y el oeste. Destruye estas torres y veras como los guardianes Lobo Gelido restantes huyen de forma vergonzosa.' WHERE `entry`='2783';
UPDATE `locales_page_text` SET `Text_loc7`='Epilogo$B$B\"Sumerge la alcachofa en crema de ajo y mayonesa. Acompana la comida con una reposada y embriagadora cerveza.\"$B$BAhora Drek\'Thar esta solo, con los dos perros que lo acompanan. Captura el Puesto de auxilio Lobo Gelido para que te sirva de ayuda en la batalla y destruye a Drek\'Thar. Cuando el general caiga, esta tierra volvera finalmente a su legitimo dueno: Los Pico Tormenta.' WHERE `entry`='2784';
UPDATE `locales_page_text` SET `Text_loc7`='Te convocamos, Zanza de Zuldazar.$B$BBendice a los que pidan tu ayuda, Loa Zanza. Bendice a aquellos que establecerian una alianza con el pueblo de Zandalar.$B$BAyudanos en tiempo de necesidad. Ayudanos, Loa. Danos el poder para derrotar a nuestros enemigos. Danos el poder para derrotar una vez mas al Dios de la Sangre.' WHERE `entry`='2785';
UPDATE `locales_page_text` SET `Text_loc7`='¡Torneo de pesca de Tuercespina!$BDomingo por la tarde de las 14.00 a las 16.00.$B$BPesca en cualquier punto situado en la costa de Tuercespina (exceptuando la Bahia del Botin) y fijate en los cardumenes de pezrico que apareceran a lo largo de la costa. Asegurate de que tu boya de pesca cae en medio del cardumen y, en lugar de pescar la variedad habitual de Tuercespina, obtendras un pez especial. ¡La primera persona que capture cuarenta pezricos puede pedir al maestro de pesca Anzuelo, que esta en la Bahia del Botin, que se los cambie por un premio!$B$BIncluso si no eres la primera persona que capture los cuarenta pezricos, aun puedes cambiarlos por dinero en efectivo. Ademas, aquellos que pesquen otras variedades especiales ¡tambien recibiran premios!' WHERE `entry`='2786';
UPDATE `locales_page_text` SET `Text_loc7`='Su nombre trajo la luz a la oscura Azeroth.$B$BSu valor derribo los muros del sufrimiento.$B$BLos Mano de Plata nunca harian nada mejor$B$Bque dar al mundo el caballero que se sacrifico por los demas.' WHERE `entry`='2787';
UPDATE `locales_page_text` SET `Text_loc7`='No fue la fama lo que perseguia,$B$Bni convertirse en senor para su gente.$B$BPor el contrario, escogio luchar por Lordaeron,$B$Bcon la esperanza de que los suyos ya no tuvieran que hacerlo.' WHERE `entry`='2788';
UPDATE `locales_page_text` SET `Text_loc7`='El tragico relato de este caballero sagrado$B$Bnunca terminara con su impia muerte.$B$BLo glorificamos ¡y le daremos siempre las gracias!$B$B¡Uther siempre conocera la gloria y la Luz!' WHERE `entry`='2789';
UPDATE `locales_page_text` SET `Text_loc7`='Su nombre nunca morira.$BSu sacrificio siempre$Bnos mostrara el camino.$B$BUna vez rotas las cadenas$Bque estrangulaban nuestro honor,$Bya no podran esclavizarnos.' WHERE `entry`='2790';
UPDATE `locales_page_text` SET `Text_loc7`='¿Puedes oir ese grito?$BEs un grito de batalla de la Horda:$B¡Victoria o muerte!$B$BDebemos recordar$Bsu fuerza cara a la muerte.$BSu sueno ahora se ha hecho realidad.' WHERE `entry`='2791';
UPDATE `locales_page_text` SET `Text_loc7`='¡Peligros por todas partes!$BLos enemigos intentan$Bencadenarnos de nuevo.$B$BCuando luchemos, pensad en$Baquel que sabia lo que habia que hacer.$B¡Grito Infernal por siempre!' WHERE `entry`='2792';
UPDATE `locales_page_text` SET `Text_loc7`='Han pasado casi mil anos desde la \'Guerra del Mar de Dunas\'. Durante esa guerra, en el corazon del desierto de Silithus, Fandral Corzocelada hubo de soportar una gran tragedia y una gran carga. Se cree que los acontecimientos descritos a continuacion son los que finalmente conformaron la desagradable manera de ser del archidruida.' WHERE `entry`='2793';
UPDATE `locales_page_text` SET `Text_loc7`='En el pasado, una antigua raza de insectoides sensibles, conocida como los qiraji, competia por dominar la mayor parte del territorio de Kalimdor. Fueron los presuntuosos e imperialistas Corzocelada y su ejercito de elfos de la noche los que se enfrentaron cara a cara a los qiraji en la batalla.' WHERE `entry`='2794';
UPDATE `locales_page_text` SET `Text_loc7`='Padre e hijo lucharon fervientemente para contener la marea creciente de enjambres de insectos en Silithus. Desde la fortaleza de Ahn\'Qiraj, una corriente de silitidos, que parecia interminable, continuo reforzando las filas de las fuerzas invasoras qiraji. Todo Silithus estaba bajo sitio.' WHERE `entry`='2795';
UPDATE `locales_page_text` SET `Text_loc7`='En una decision que Corzocelada lamentaria, el Corzocelada mas joven, Valstann, ordeno defender el puesto de los elfos de la noche de la Aldea de Viento del Sur.$BDesafortunadamente, Valstann perdio su vida el mismo dia que la Aldea del Viento del Sur cayo bajo los ejercitos de Ahn\'Qiraj.' WHERE `entry`='2796';
UPDATE `locales_page_text` SET `Text_loc7`='Imperterrito y con el corazon endurecido, un vengativo Fandral Corzocelada habria de seguir adelante. La derrota en Silithus produciria una gran perdida para la nacion de los elfos de la noche y, posiblemente, para todo el mundo.$B$BLa batalla se desarrollo por espacio de meses. Todo Silithus fue tomado por los qiraji. Corzocelada y su ejercito fueron obligados a retroceder hacia Tanaris. Con un ejercito diezmado e innumerables inocentes caidos, la arrogancia con la que se habia conducido anteriormente Fandral Corzocelada desaparecio por completo.' WHERE `entry`='2797';
UPDATE `locales_page_text` SET `Text_loc7`='Con su orgullo totalmente devastado, Corzocelada habria de sentir, por segunda vez en su vida, el temblor en la boca del estomago que solo puede producir el miedo. Luego, fue en Tanaris donde Corzocelada se acerco a Anacronos, progenie de Nozdormu, y rogo la ayuda del Vuelo Bronce.' WHERE `entry`='2798';
UPDATE `locales_page_text` SET `Text_loc7`='Inicialmente, Anacronos rechazo la posibilidad de implicar al Vuelo Bronce en los asuntos de razas inferiores. Pero cuando los qiraji atacaron descaradamente las Cavernas del Tiempo, Anacronos accedio a enviar refuerzos a los elfos de la noche.' WHERE `entry`='2799';
UPDATE `locales_page_text` SET `Text_loc7`='Pronto, Anacronos se dio cuenta de que incluso el poderoso Vuelo Bronce no podria contener la marea de las fuerzas invasoras qiraji. Por cada cincuenta insectos aniquilados mediante su aliento, un centenar esperaban para ocupar su lugar. Con la caida de la noche, las lineas de los elfos empezaron a resquebrajarse, y con la posibilidad de ser empujados hacia el norte en las tierras salvajes de Feralas cada vez mas cerca, Anacronos decidio que los hijos de los Aspectos debian conocer la situacion.' WHERE `entry`='2800';
UPDATE `locales_page_text` SET `Text_loc7`='En primer lugar, convoco al Vuelo de Ysera. Merithra del Sueno responderia a su llamada y, a partir de ahi, se darian por enterados el Vuelo de Alexstrasza y el Vuelo de Malygos.' WHERE `entry`='2801';
UPDATE `locales_page_text` SET `Text_loc7`='Los dragones se reunirian en la estratosfera, encima de Silithus: Cientos de metros por encima de la fortaleza de Ahn\'Qiraj.$B$BFue Anacronos, hijo de Nozdormu, Aspecto del Tiempo.$B$BDesde el Sueno Esmeralda vendria Merithra, hija de Ysera, Aspecto de la Naturaleza.$B$BCaelestrasz, hijo de Alexstrasza, Aspecto de la Vida, representaria al Vuelo Rojo.$B$BArygos, hijo de Malygos, Aspecto de la Magia, representaria al Vuelo Azul.' WHERE `entry`='2802';
UPDATE `locales_page_text` SET `Text_loc7`='Los dragones observaron como una interminable corriente de silitidos y qiraji salian en tropel de Ahn\'Qiraj. Pronto les quedo claro que esta guerra no podia acabar mediante el uso de la fuerza. Desde el interior del templo principal, miles y miles de combatientes qiraji estaban esperando. Con el paso de cada segundo, se incubaban y preparaban mas y mas, listos para asaltar el mundo.' WHERE `entry`='2803';
UPDATE `locales_page_text` SET `Text_loc7`='Arygos fue el primero en darse cuenta de las extranas emanaciones. El templo monolitico situado en el cuadrante sur de Ahn\'Qiraj irradiaba una oscura magia, distinta de todo lo que hasta ahora conocia procedente de los qiraji. Aunque lo intentaron, nadie fue capaz de penetrar en el templo para localizar la fuente de esta energia. Solo algo de inmenso poder podria autoprotegerse por completo. Tal vez... algo controla a los insectos. Quizas algo incluso controle a los qiraji.' WHERE `entry`='2804';
UPDATE `locales_page_text` SET `Text_loc7`='Los dragones sabian que tenian que reaccionar con rapidez, no fuera que el mundo sufriera otra catastrofe de proporciones epicas. Tras una cuidadosa deliberacion, se decidio que la mejor accion que cabia emprender consistiria en detener los ejercitos anfitriones en su origen o cerca del mismo.' WHERE `entry`='2805';
UPDATE `locales_page_text` SET `Text_loc7`='Se crearia una gran barrera, originada desde la esencia de los elfos de la noche y de los dragones. Seria una barrera magica que podria contener a los silitidos y a sus Senores Supremos dentro de los muros de Ahn\'Qiraj, previniendo futuras incursiones. El propio Ahn\'Qiraj se convertiria en una prision.' WHERE `entry`='2806';
UPDATE `locales_page_text` SET `Text_loc7`='Dia 1$BNuestro peregrinaje ha acabado. Finalmente, llegamos a Silithus, donde vamos a establecer contacto. Hemos venido de varios sitios para colaborar en lo que es una tarea monumental.$B$BHay una tremenda excitacion en el aire.$B$B Manana dedicaremos todas nuestras energias a construir lo que nos permitira entrar en comunicacion con ELLOS.$B$B¡Manana vamos a construir una piedra de viento!' WHERE `entry`='2807';
UPDATE `locales_page_text` SET `Text_loc7`='Dia 2$BLa localizacion de una piedra de viento ha sido objeto de una gran discusion. Ni siquiera el mas sabio de nosotros puede determinar con exactitud a que lugar se referia exactamente la antigua profecia.$B$BHe estado con el grupo original del norte e, indudablemente, el tiempo demostrara que estamos en lo cierto. El argumento de que las lineas de energia se intersecan en angulos perjudiciales en este lugar resulta ridiculo e inocente.$B$BHemos reunido a gran velocidad los materiales de construccion necesarios.' WHERE `entry`='2808';
UPDATE `locales_page_text` SET `Text_loc7`='Dia 15$BNuestra dedicacion ha tenido su recompensa. Somos el primer campamento que finaliza la construccion de una piedra de viento. ¡Si los otros idiotas se hubieran quedado con nosotros, habriamos terminado la tarea en menos de una semana!$B$BManana iniciaremos los rituales que nos permitiran invocar al baron Kazum en persona. Ha pedido ser el primero en comprobar el sistema y, puesto que se enfada con facilidad, no hemos podido decirle que no.$B$BEspero que este muy contento con nosotros.' WHERE `entry`='2809';
UPDATE `locales_page_text` SET `Text_loc7`='Para entrega inmediata a todos los miembros del Martillo Crepuscular.$B$BLa busqueda del traidor, Ortell, progresa de forma muy satisfactoria. Nuestros exploradores estan estrechando el cerco en relacion con una red de cavernas situadas en los Paramos de Poniente.$B$BLa traicion de Ortell no caera en el olvido y la posibilidad de que se fugue representa un gran riesgo para la seguridad de nuestra organizacion.$B$BCualquier individuo que oculte informacion sobre el paradero de Ortell sera castigado debidamente.$B$BSenor Crepuscular Everun' WHERE `entry`='2810';
UPDATE `locales_page_text` SET `Text_loc7`='Para entrega inmediata a todos los miembros del Martillo Crepuscular.$B$BConsiderando el estado actual de las cosas en Silithus, la distribucion de todos los textos de El creyente verdadero debe realizarse utilizando una escolta de no menos de cuatro miembros armados del Martillo Crepuscular.$BCualquier miembro que infrinja esta nueva politica sera castigado sumariamente.$B$BSenor Crepuscular D\'Sak' WHERE `entry`='2811';
UPDATE `locales_page_text` SET `Text_loc7`='Para entrega inmediata a todos los miembros del Martillo Crepuscular.$B$BLa siguiente informacion describe el uso de piedras de viento para la comunicacion con nuestros maestros del Consejo Abisal.$B$BCualquier uso frivolo de las piedras de viento sera castigado con la mayor dureza.$B$BSenor Crepuscular Havunth' WHERE `entry`='2812';
UPDATE `locales_page_text` SET `Text_loc7`='Templarios Abisales$B$BComo iniciado, se te permite establecer contacto con los Templarios Abisales para celebrar rituales rutinarios. No olvides ponerte siempre la vestimenta adecuada cuando utilices piedras de viento para evitar que se activen nuestros encantamientos defensivos.$B$BSi surge cualquier clase de emergencia no prevista y no hay un sobrestante presente, estas autorizado a invocar a un Templario Abisal.$B$BPara conocer los detalles acerca de los rituales de invocacion pertinentes, consulta el numero de este mes de El creyente verdadero.' WHERE `entry`='2813';
UPDATE `locales_page_text` SET `Text_loc7`='Duques Abisales$B$BSolo a los acolitos en posesion de un medallon de estatus se les permite establecer contacto con los Duques Abisales. Los medallones de estatus solo deben ser utilizados por aquellos individuos para los cuales se han expedido.$B$BLos miembros del Martillo Crepuscular que hayan mantenido una comunicacion no autorizada con los Duques Abisales o que hayan utilizado sin permiso los medallones de estatus seran castigados y me ocupare personalmente de infligirles el castigo.' WHERE `entry`='2814';
UPDATE `locales_page_text` SET `Text_loc7`='El Alto Consejo de los Senores Abisales$B$BSolo los Senores Crepusculares podran tener contacto con el principe Skaldrenox, el Alto Mariscal Whirlaxis, el Baron Kazum o Lord Skwol.$B$BBajo ninguna circunstancia, un miembro de bajo rango del Martillo Crepuscular tiene permiso para entrar en comunicacion con el Alto Consejo. Todo extrano que sea sorprendido utilizando una piedra de viento de esta manera, quedara reducido a cenizas en el acto.' WHERE `entry`='2815';
UPDATE `locales_page_text` SET `Text_loc7`='Estado del culto, volumen 92$B$BCon el corazon exultante os saludo, hermanos mios, pues la situacion de nuestro culto no podria ser mas dichosa. Somos legion y nuestras metas se acercan cada vez mas a su cumplimiento.$B$BSe nos unen nuevos reclutas. Todo son alabanzas a nuestros senores en Costa Oscura, se inclinan con reverencia en la Garganta de Fuego, abrazan al sirviente de nuestros maestros, Aku\'mai, en las Cavernas de Brazanegra. ¡Ciertamente, hermanos y hermanas, pronto un enjambre de creyentes cubrira todo Azeroth!' WHERE `entry`='2816';
UPDATE `locales_page_text` SET `Text_loc7`='Estado del culto, volumen 127$B$B¡Realmente es un dia muy feliz! ¡Nos han sometido a prueba en Silithus y hemos aprobado!$B$BLos informes acerca de la destruccion de nuestro campamento situado en el extremo norte pueden suponer un reto para los corazones de los no creyentes, pero esta matanza no debe considerarse como una tragedia. ¡No!' WHERE `entry`='2817';
UPDATE `locales_page_text` SET `Text_loc7`='¡Si el Alto Consejo Abisal derrama nuestra sangre, esto debe considerarse como una bendicion! ¡Debemos envidiar a nuestros camaradas caidos, ya que ahora sus espiritus residen en el vientre de la bestia mas justa!$B$B¡Alabad a los dioses antiguos! ¡Alabad a los siervos! ¡Alabad al Martillo Crepuscular!' WHERE `entry`='2818';
UPDATE `locales_page_text` SET `Text_loc7`='Estado del culto, volumen 233$B$B¡Hermanos y hermanas, la oportunidad vuelve a llamar a nuestra puerta! El Circulo Cenarion, un culto infame de amantes de la naturaleza, esta enviando agentes en contra nuestra. Seguramente, intentan evitar que invoquemos piedras rojas. ¡No podemos permitirlo!$B$BLas piedras rojas son esenciales para nuestros nuevos aliados. No podemos permitir que los perros del Circulo Cenarion tengan exito. Manteneos firmes, mis fieles amigos. ¡Manteneos firmes!' WHERE `entry`='2819';
UPDATE `locales_page_text` SET `Text_loc7`='¿Como sabemos que nos quieren?$B$BEs posible que algunos se pregunten por que los dioses antiguos y sus esbirros nos aman. ¿Acaso no nos mataran? ¿Acaso no causaran sufrimiento y dolor a todos? ¿Como es posible que seres tan inclinados a la malevolencia alberguen amor en sus corazones?$B$BExiste una respuesta simple a esta pregunta. Es tan simple que ni siquiera es preciso formularla.' WHERE `entry`='2820';
UPDATE `locales_page_text` SET `Text_loc7`='La fe. La fe es la fuente de donde mana la alegria de la servidumbre. La fe nos dara calor en las frias noches de Silithus. La fe nos mantendra en la gracia de nuestros Senores cuando lleguen los dias del final.$B$BLa fe nos salvara.' WHERE `entry`='2821';
UPDATE `locales_page_text` SET `Text_loc7`='No se puede entender una sola palabra en este libro. ¡No se en que idioma esta escrito!' WHERE `entry`='2822';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><IMG src=\"Interface\Pictures\21037_crudemap_256\"/></BODY></HTML>' WHERE `entry`='2828';
UPDATE `locales_page_text` SET `Text_loc7`='Querido diario:$B$BHoy, mi archienemigo Narain Sabelotodo ha intentado enganarme, enviandome compinches para cubrir su sitio en la ejecucion. SU ejecucion. ¿Podeis creerlo?$B$BHe tenido un monton de problemas disenando este diabolico plan para apartarle de esa maldita choza ¿y ahora hace esto? Da igual... Estoy demasiado $genfadado:enfadada; para seguir escribiendo. Volvere mas tarde.' WHERE `entry`='2829';
UPDATE `locales_page_text` SET `Text_loc7`='Querido diario:$B$BDespues de mi ultima anotacion, sali de mi guarida realmente ofuscado. Vaya, si lo estaba. Decidi redisenar los uniformes de mis esbirros para reflejar mejor mi angustia. Me siento un poco mejor, pero algo falta. Vuelvo enseguida...' WHERE `entry`='2830';
UPDATE `locales_page_text` SET `Text_loc7`='Querido diario:$B$BAhora si me siento bien. Le he pegado a uno de mis esbirros hasta hacerlo llorar como una nina pequena.$B$BMmm... Ahora siento una especie de malestar. ¡Espera un momento! ¡Maldicion, soy un genio del MAL! El mal, ¿sabes? Estoy tan disgustado. Vuelvo enseguida.' WHERE `entry`='2831';
UPDATE `locales_page_text` SET `Text_loc7`='Querido diario:$B$BLe he dado un abrazo y le he dicho que continue con su buena labor. Ahora me siento mejor. Que esto quede entre nosotros, diario.$B$B¿Por donde iba? ¡Ah, si, NARAIN! ¡AHHH! Odio a ese tio. Despues de todo, fue Narain el que descubrio la curva en todos los examenes, cuando ambos estudiabamos en el Instituto Gnomeregan para Manitas.$B$BFui expulsado de la escuela, ¿te lo puedes creer? ¡En lo que a mi respecta, Gnomeregan tiene lo que se merece!$B$BEsto es todo por hoy. ¡Que duermas bien, diario!' WHERE `entry`='2832';
UPDATE `locales_page_text` SET `Text_loc7`='Querido diario:$B$BMe debo estar haciendo mayor. He olvidado por completo lo que te estaba diciendo de Narain y de su engano. ¡De cualquier manera, me enfade tanto cuando vi a un senuelo disfrazado de Narain en lugar de este, que ordene al Numero Dos que destruyera a ese idiota!$B$BMonte en mi maquina voladora y empece a destrozar su precioso, estupido y simple libro en draconico. ¡Que fanfarron! ¿Quien lee en draconico? ¡Lo odio tanto!' WHERE `entry`='2833';
UPDATE `locales_page_text` SET `Text_loc7`='Querido diario:$B$BLo siento, pero me estoy durmiendo. Estuve volando enfadado mientras arrojaba capitulos enteros de este estupido libro. ¡Vi un demonio debajo mio en Negro Rumor y decidi utilizar el primer capitulo para formar un tornado magico de papel y hacer anicos su cuerpo! Fue liberador.$BMagia + papel = diversion.$B$BMe hizo tan feliz, que decidi volar hacia los Reinos del Este. Mi primera parada fue en Nucleo de Magma. ¡Narain siempre hablo de este sitio! \"Nucleo de Magma esto y Ragnaros lo otro...\"' WHERE `entry`='2834';
UPDATE `locales_page_text` SET `Text_loc7`='¿Sabes una cosa? Si le gusta tanto, quizas podria intentar volver a recopilar su libro, yendo a Nucleo de Magma y rescatando el capitulo de entre las cenizas! ¡Ja!$B$BMientras estaba en el vecindario, pense que algunos de esos tecnicos esclavizantes en el laboratorio del viejo Victor Nefarius podrian encontrar un uso para este material draconico. ¡Bombas fuera!' WHERE `entry`='2835';
UPDATE `locales_page_text` SET `Text_loc7`='Esto esta empezando a convertirse en un deporte. ¡Realmente estaba muy entusiasmado rompiendo el maldito libro!$B$BLanzar los siguientes trozos fue muy facil.$B$BEntranas, hogar de los asquerosos muertos y, posiblemente, el sitio donde preparan el mejor chile, merecio un capitulo. Ventormenta, la capital del queso, el otro. Querido diario: ¿alguna vez has anadido queso de Ventormenta al chile de Entranas? ¿HOLA? Parece que suenan algunas alarmas.' WHERE `entry`='2836';
UPDATE `locales_page_text` SET `Text_loc7`='Estaba tan cansado en este momento, que me dedique a dar vueltas con la maquina voladora y me fui a casa. Pero antes realizare otras dos paradas. Di un paseo con mi maquina voladora sobre Lord Kazzak y sus demonios en Escara Impia (detesta que lo haga) y otro cerca de la chimenea de Onyxia.$B$BEstoy $Gcansado:cansada;. Me voy a dormir.' WHERE `entry`='2837';
UPDATE `locales_page_text` SET `Text_loc7`='A quien pueda interesar,$B$BComo me gustan estos juegos que jugamos.$B$BSolo puedo suponer que si estas leyendo esta carta, que de algun modo, a traves de la intervencion divina, o milagro, consiguiste vencerme. Absurdo, lo se, pero cosas mas raras han pasado en la historia de este mundo.' WHERE `entry`='2838';
UPDATE `locales_page_text` SET `Text_loc7`='Como todas las cosas en la vida, hay un lado glorioso, centelleando de tragedia, el caos y el mal de esta triste historia. Aunque me has matado, lo has hecho de modo tan lento y chapucero que me ha quedado tiempo para escribir esta carta. DESPUeS de que destrui tu unica esperanza de salvar este mundo. El polvo de cristalitos rojos es todo lo que queda de tu Fragmento de Cetro.' WHERE `entry`='2839';
UPDATE `locales_page_text` SET `Text_loc7`='Disfruta la Nemesis y el Armamento de Tempestira que hallaras en mi tesoro. Creeme cuando te digo esto: Tengo el gran placer de saber que hice lo que mi padre no pudo…$B$Bsinceramente,$B$BLord Victor Nefarius$B\"Nefarian\"' WHERE `entry`='2840';
UPDATE `locales_page_text` SET `Text_loc7`='En nombre de los Pastos de Bosquehumeante, te vuelvo a dar las gracias por ocuparte de esto. No estamos seguros de cual es el grupo que tiene a Metzen, pero investigar cualquiera de los dos puede constituir un buen punto de partida.$B$BEl tiempo es oro, $gamigo mio:amiga mia; y, en este caso, tenemos muy poco. Se rapido en tus tareas de rescate, ya que estos grupos no deben ser subestimados en lo que respecta a lo que son capaces de hacer.$B$BEn cualquier caso, las dos paginas siguientes son las notas de rescate que hemos recibido. ¡Buena suerte!' WHERE `entry`='2841';
UPDATE `locales_page_text` SET `Text_loc7`='Si quieres que el reno vuelva con vida, deberas ser rapido con la recompensa. Los Piratas de los Mares del Sur no se muestran amables cuando hay retrasos en el pago y sabemos lo valiosa que resulta esta bestia para TU forma de pirateria.$B$BTrae la suma de 1000 piezas de oro a la entrada de la cala del Aparejo Perdido en Tanaris. Cualquier intento por rescatar al reno hara que Metzen de un paseo muy largo sobre una plancha muy corta.$B$BSi...$B$BLos Piratas de los Mares del Sur' WHERE `entry`='2842';
UPDATE `locales_page_text` SET `Text_loc7`='Tu apreciada mascota no lo esta pasando demasiado bien con el implacable calor de la Garganta de Fuego... Si eres listo, deberas cumplir nuestras peticiones sin demora.$B$BDebes traernos cinco rubies estrellados y la suma de 700 piezas de oro. Coloca todo en un solo paquete sin marca alguna. Deja el paquete en las puertas del Paso de Fraguapiedra en el plazo de una semana.$B$BSi no accedes a nuestras peticiones, Metzen se convertira en el plato principal del Festival de Invierno de los enanos Hierro Negro.$B$BMmmm... reno...' WHERE `entry`='2843';
UPDATE `locales_page_text` SET `Text_loc7`='Mas rapido que las aguilas llegaron sus corceles,$B$By el silbo, grito y los llamo por su nombre;$B$B\"¡Arre, Mercer! ¡Arre, Chilton! ¡Arre, Jordan y Kaplan!$B$B¡Adelante, Nagle! ¡Adelante, Pardo! ¡Adelante, Goodman y Metzen!$B$B¡Hacia lo alto del porche! ¡Hacia lo alto del muro!$B$B¡Ahora salid de aqui! ¡Fuera de aqui! ¡Fuera de aqui todos!\"' WHERE `entry`='2844';
UPDATE `locales_page_text` SET `Text_loc7`='Espero fervientemente que, a traves de mi investigacion, pueda encontrar una fuente suplementaria de energia magica que sea segura para mi gente. Sin La Fuente del Sol, debemos encontrar una forma de continuar con nuestro modo de vida sin sucumbir a la tentacion de la magia arcana.$B$B¡Creo que el futuro de los Sin\'dorei puede servir, una vez mas, como ejemplo brillante para todos!' WHERE `entry`='2846';
UPDATE `locales_page_text` SET `Text_loc7`='Todavia no ha habido suerte. La poca magia a la que tengo acceso directo se debe canalizar a traves de la Cumbre. Pero si que tengo varias ideas intrigantes. Hare que mis aprendices mas brillantes sigan estos caminos de investigacion y vere lo que se nos ocurre.' WHERE `entry`='2847';
UPDATE `locales_page_text` SET `Text_loc7`='Ha pasado algun tiempo desde la ultima vez que escribi algo en estas paginas. Sigue sin haber nada prometedor. Ayer recibi un mensaje que decia que uno de los alumnos de la Isla del Caminante del Sol, un tal Felendren, no siguio los consejos de sus mentores y sucumbio a la afliccion.$B$BRedoblare nuestros esfuerzos.' WHERE `entry`='2848';
UPDATE `locales_page_text` SET `Text_loc7`='Nada. Pero no perdere la esperanza. Los Sin\'dorei no se pueden permitir estar en posicion de dependencia magica en este momento, cuando estamos sitiados en el sur.$B$BHe ideado un enfoque totalmente nuevo y, si tiene exito, me permitira filtrar las \"impurezas\" que hay en algunas fuentes de energia magica viles que he de separar para el futuro. Debo proceder con precaucion.' WHERE `entry`='2849';
UPDATE `locales_page_text` SET `Text_loc7`='¡Asombroso! Hemos tenido algo de exito, aunque la cantidad de magia que pudimos extraer era minuscula. Voy a apartar a la mayoria de los aprendices de sus estudios para centrarnos en este enfoque nuevo y prometedor.$B$BCon algo de suerte, deberiamos poder refinar el proceso y matar dos pajaros de un tiro ? una fuente de energia abundante para nosotros, y una forma de contrarrestar cualquier magia vil que podamos encontrar en el futuro.' WHERE `entry`='2850';
UPDATE `locales_page_text` SET `Text_loc7`='¡No! Mientras experimentaba con el nuevo proceso, mi mejor aprendiz, Telethayon, de repente y sin aviso se marchito antes mis ojos, sucumbiendo al estado que aflige a mis hermanos. Intente pararlo, pero ya era demasiado tarde. No me quedo mas remedio que liberarlo de su sufrimiento.$B$BTal es el precio del descubrimiento, pero ya siento que me pesa demasiado.' WHERE `entry`='2851';
UPDATE `locales_page_text` SET `Text_loc7`='Es demasiado como para poder soportarlo... dos aprendices mas han sucumbido. Estamos siendo tan cuidadosos... no entiendo que ha ido mal.$B$BTendre que abandonar estas investigaciones y empezar de cero.' WHERE `entry`='2852';
UPDATE `locales_page_text` SET `Text_loc7`='Llegue demasiado tarde. Un tercer aprendiz habia estado compartiendo la investigacion vil con algunos de los otros sin yo saberlo. Voy a intentar contener la situacion, pero, primero, debo alejar a los aprendices que no se han visto afectados.$B$BLo hare haciendo saber a todos que me voy de sabatico al Retiro del Errante, con la esperanza de hallar un nuevo enfoque para el problema.$B$BDebo encontrar una forma de compensar este horrible error de juicio.' WHERE `entry`='2853';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS DE OFICIALES DE LA HORDA</H1><BR/><P align=\"center\">Parte 2</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank09\" align=\"left\"/><P align=\"right\">Centurion</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank08\" align=\"left\"/><P align=\"right\">Legionario</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank07\" align=\"left\"/><P align=\"right\">Guardia de sangre</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank06\" align=\"left\"/><P align=\"right\">Guardia de piedra</P><BR/></BODY></HTML>' WHERE `entry`='2854';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS DE SOLDADOS DE LA HORDA</H1><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank05\" align=\"left\"/><P align=\"right\">Gran capataz</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank04\" align=\"left\"/><P align=\"right\">Capataz primero</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank03\" align=\"left\"/><P align=\"right\">Capataz</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank02\" align=\"left\"/><P align=\"right\">Bruto</P><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank01\" align=\"left\"/><P align=\"right\">Explorador</P><BR/></BODY></HTML>' WHERE `entry`='2855';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS DE OFICIALES DE LA ALIANZA</H1><BR/><P align=\"center\">Parte 2</P><IMG src=\"Interface\PvPRankBadges\PvPRank09\" align=\"left\"/><BR/><P align=\"right\">Campeon caballero</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank08\" align=\"left\"/><BR/><P align=\"right\">Capitan caballero</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank07\" align=\"left\"/><BR/><P align=\"right\">Teniente caballero</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank06\" align=\"left\"/><BR/><P align=\"right\">Caballero</P><BR/><BR/></BODY></HTML>' WHERE `entry`='2856';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">RANGOS DE SOLDADOS DE LA ALIANZA</H1><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank05\" align=\"left\"/><BR/><P align=\"right\">Alferez</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank04\" align=\"left\"/><BR/><P align=\"right\">Sargento primero</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank03\" align=\"left\"/><BR/><P align=\"right\">Sargento</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank02\" align=\"left\"/><BR/><P align=\"right\">Cabo</P><BR/><BR/><IMG src=\"Interface\PvPRankBadges\PvPRank01\" align=\"left\"/><BR/><P align=\"right\">Soldado</P><BR/><BR/></BODY></HTML>' WHERE `entry`='2857';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML>$B<BODY>$B<H1 align=\"center\">$BAnthony Ray Stark$B</H1>$B<H2 align=\"center\">$B1961 - 2005$B</H2>$B</BODY>$B</HTML>' WHERE `entry`='2858';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">BLANDoN DE EVOCACIoN: GUiA DEL USUARIO</H1><BR/><P align=\"center\">Donde encontrar lugares encantados</P><BR/><P align=\"center\">INTRODUCCIoN</P><BR/><P align=\"left\">En estas paginas se encuentran las localizaciones de los espiritus caidos y corruptos y de las almas implicadas en el Incidente del amuleto de Lord Valthalak. Aunque ya has salvado a mis antiguos companeros, tengo la impresion de que los repugnantes ecos de la maldad que alguna vez torturaron sus espiritus aun palpitan en aquellos lugares encantados. Tal era el poder malefico del espiritu de Lord Valthalak.</P><BR/><P align=\"left\">Asimismo, se puede utilizar el blandon para invocar a Lord Valthalak, aunque, como en el caso anterior, es preciso tomar extremas precauciones al hacerlo.</P><BR/></BODY></HTML>' WHERE `entry`='2859';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">INTRODUCCIoN, continuacion...</H1><BR/><P align=\"left\">Este blandon funciona exactamente como el Blandon de senalizacion. Simplemente, colocalo en cualquiera de los cinco lugares encantados y te servira para convocar a los espiritus de los muertos.</P><BR/><P align=\"left\">A modo de nota, ya que se que te interesara saberlo, el Estandarte de Provocacion puede utilizarse de forma similar en el estado de las Profundidades de Roca Negra. Asegurate de darle una leccion a Theldren y a sus matones por mi, cada vez que tengas la oportunidad cuando estes alli.</P><BR/><P align=\"left\">Si deseas mas explicaciones, sigue leyendo.</P><BR/></BODY></HTML>' WHERE `entry`='2860';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">ISALIEN</H1><BR/><P align=\"center\">Sacerdotisa de la Luna de los elfos de la noche</P><BR/><P align=\"left\">Cuando Isalien decidio separarse del resto, lo hizo con la intencion de realizar un peregrinaje a La Masacre, la ciudad ancestral de los elfos de la noche, caida en desgracia, conocida una vez como Eldre\'Thalas. Alli aun existe una biblioteca que atesora grandes conocimientos, un santuario que unos pocos elfos de la noche pudieron salvar de la marea. Alli intento encontrar respuestas sobre su herencia e informacion acerca de los fragmentos de amuleto. Asimismo, cabe destacar que era muy amiga de Falrin Tallarbol.</P><BR/></BODY></HTML>' WHERE `entry`='2861';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">ISALIEN, continuacion...</H1><BR/><P align=\"left\">Desafortunadamente, cuando ella y su inseparable hipogrifo Empireo entraban en la ciudad, fueron atacados por Alzzin el Formaferal y sus esbirros. Alli cayo para no volver a levantarse jamas. Tal vez fue para bien que Isalien nunca desvelara los secretos de la magia negra que Lord Valthalak volco en una parte de su esencia que impregna el amuleto.</P><BR/><P align=\"left\">La estancia al aire libre donde habita Alzzin es uno de estos lugares encantados.</P><BR/></BODY></HTML>' WHERE `entry`='2862';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">MOR PEZUnA GRIS</H1><BR/><P align=\"center\">Druida tauren y primero en caer</P><BR/><P align=\"left\">Mor Pezuna Gris siempre fue un companero fiel e incondicional en la Espada Enmascarada. Desafortunadamente, fue el primero en caer ante los malvados esbirros de Lord Valthalak.</P><BR/><P align=\"left\">Cuando huimos de Valthalak, fuimos perseguidos por un verdadero ejercito de merodeadores y asesinos espectrales. La huida de la Cumbre de Roca Negra fue, cuando menos, caotica. Desgraciadamente, Mor cayo durante el trayecto, aunque sospecho que fue empujado. En cualquier caso, cayo en la parte inferior de la cumbre.</P><BR/></BODY></HTML>' WHERE `entry`='2863';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">MOR PEZUnA GRIS, continuacion...</H1><BR/><P align=\"left\">Mas tarde, descubrimos que Mor habia logrado sobrevivir a la caida. Pero no hubo un final feliz para mi buen amigo: fue tomado prisionero de los trols de Tazz\'Alaor. No mucho mas tarde, mientras estaba siendo torturado por el maestro de guerra Voone, un grupo de asesinos de Valthalak entraron en escena y acabaron el trabajo.</P><BR/><P align=\"left\">En caso de que intentes liberar el eco del espiritu de Mor Pezuna Gris debes saber que la estancia del maestro de guerra Voone es un lugar encantado.</P><BR/></BODY></HTML>' WHERE `entry`='2864';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">JARIEN Y SOTHOS</H1><BR/><P align=\"center\">Hermanos y aspirantes a miembros de las Cruzada Escarlata</P><BR/><P align=\"left\">Jarien y Sothos son dos de las personas mas intolerantes que he conocido. Aun asi, su talento como combatientes era excepcional y tenian dinero, razon por la cual supongo que Anthion, el lider de la Espada Enmascarada, siguio contando con ellos.</P><BR/><P align=\"left\">Los hermanos, finalmente, abrazaron las ensenanzas y la filosofia de la Cruzada Escarlata. Siempre habian manifestado cierto fervor a la hora de vencer a cualquier miembro de la Plaga con el que nos cruzaramos, y se sospechaba que su familia tenia lazos con la organizacion.</P><BR/></BODY></HTML>' WHERE `entry`='2865';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">JARIEN Y SOTHOS, continuacion...</H1><BR/><P align=\"left\">De esta manera se encontraron haciendo frente a las pruebas que debian pasar todos aquellos que intentaran unirse a la Cruzada. Estaban particularmente interesados en conseguir una posicion como caballeros dentro de las filas, algo que, aparentemente, requeria pasar pruebas mas dificiles. Mas adelante, se descubrio que Sothos habia fracasado en su prueba final. Jarien no se mostro de acuerdo y, en abierto desafio, insistio en que el merecia estar entre los caballeros. Segun hemos logrado saber, el gran cruzado Dathrohan acabo con ambos en sus propias estancias.</P><BR/><P align=\"left\">De esta manera, otro de estos lugares encantados es el Trono Carmesi, que esta dentro de El Bastion Escarlata en Stratholme.</P><BR/></BODY></HTML>' WHERE `entry`='2866';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">KORMOK</H1><BR/><P align=\"center\">Ogro nigromante de dos cabezas</P><BR/><P align=\"left\">¿Que se puede decir de Kormok? Era un ogro de dos cabezas de la variedad colerica que, en realidad, no solia ser tan malo. Cuando lo capturamos por primera vez fue como prisionero de un trabajo que habiamos aceptado en las Estepas Ardientes. Su ayuda demostro ser muy valiosa, ya que colaboro con nosotros para librarnos de una situacion muy embarazosa y, de esta manera, se convirtio en uno de los magos mas importantes de la compania.</P><BR/><P align=\"left\">Lo extrano acerca de Kormok era la naturaleza completamente distinta de sus dos cabezas. Una era totalmente alegre e, incluso, un poco tonta, mientras que la otra era astuta, calculadora, y tenia un brillo de muerte en sus ojos. En realidad, era un poco escalofriante.</P><BR/></BODY></HTML>' WHERE `entry`='2867';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">KORMOK, continuacion...</H1><BR/><P align=\"left\">Mas tarde, en el transcurso de nuestros viajes, Kormok quedo expuesto a la parte nigromantica de la magia. Desafortunadamente, la cabeza espeluznante mostraba afinidad con los espiritus muertos resucitados y experimentaba un perverso placer en ello, especialmente en la invocacion de los espiritus exiliados de la Isla del Purgatorio.</P><BR/><P align=\"left\">Tras la disolucion de la compania, Kormok se alisto en Scholomance para proseguir con su \'educacion\'. Los asesinos espectrales penetraron en la escuela facilmente y los asesinaron ante los ojos de su nuevo maestro Ras Levescarcha.</P><BR/><P align=\"left\">Esto convirtio la estancia de Ras Murmuhielo en uno de los lugares encantados en donde cabe utilizar el blandon con exito.</P><BR/></BODY></HTML>' WHERE `entry`='2868';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">LORD VALTHALAK</H1><BR/><P align=\"center\">Noble draconida y antiguo General de Cumbre de Roca Negra</P><BR/><P align=\"left\">Antes del General Drakkisath, estaba Lord Valthalak, un brujo draconida de inmenso poder. Resulta habitual que el liderazgo de la Cumbre cambie de manos y, desafortunadamente para nosotros, desempenamos un papel en el ultimo cambio de manos del poder.</P><BR/><P align=\"left\">Un zahori-contrabandista de la Bahia del Botin, Gremnik Saltatrabas, nos encargo que robaramos el libro de hechizos de Valthalak. No sabiamos si queria estudiarlo o venderlo (o ambas cosas), aunque esto ya no viene al caso. Lo que importa es que nos infiltramos en la parte alta de las Cumbres de Roca Negra y acabamos con Lord Valthalak en su guarida. Aqui es donde realmente empezo la diversion.</P><BR/></BODY></HTML>' WHERE `entry`='2869';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">LORD VALTHALAK, continuacion...</H1><BR/><P align=\"left\">No teniamos ni idea de la magnitud del conocimiento de Valthalak sobre la manipulacion de almas o, probablemente, nunca nos tomamos el trabajo de averiguarlo. Pero, finalmente, lo hicimos y resulto ser la ultima tarea que realizariamos.</P><BR/><P align=\"left\">Tras eliminar a Lord Valthalak y robar su libro de hechizos, Kormok debio de percibir algo en relacion con su amuleto. Insistia en tomarlo como su parte del botin, lo que provoco rapidamente una acalorada discusion y casi una pelea. Por entonces, no sabiamos que el amuleto realmente albergaba buena parte del espiritu de Valthalak. Para nuestro desagrado, lo averiguamos mucho despues.</P><BR/></BODY></HTML>' WHERE `entry`='2870';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">LORD VALTHALAK, continuacion...</H1><BR/><P align=\"left\">Rompiendo en pedazos el amuleto, escapamos del fantasma de Valthalak, asi como de su ejercito de asesinos espectrales. Y el ruido que hicimos mientras luchabamos por el amuleto desperto a los defensores que quedaban de la Cumbre.</P><BR/><P align=\"left\">Casi todos logramos salir de Roca Negra y asi considerabamos que estabamos seguros. Pero, incluso desde el mas alla, Valthalak envio a sus esbirros contra nosotros, por lo que no nos sirvio de nada ocultarnos. Pronto, la Espada Enmascarada se partio tras la mision, asi que nos convertimos en presa facil.</P><BR/><P align=\"left\">Como sabras, esto convierte a la morada de Lord Valthalak, actual estancia de la Bestia en la Cumbre de Roca Negra, en el ultimo de los lugares encantados.</P><BR/></BODY></HTML>' WHERE `entry`='2871';
UPDATE `locales_page_text` SET `Text_loc7`='A todos los hombres y mujeres sanos:$B$BLa Plaga ha regresado y, para hacer frente a esta amenaza, debemos dejar a un lado todos nuestros conflictos. El Alba Argenta acabara con todos aquellos que no empunen las armas contra la amenaza de la Plaga.$B$BLos que han decidido defender nuestras tierras estan reunidos en la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este. Juntos, venceremos.$B$BGuardian de los Pergaminos$BEl Alba Argenta' WHERE `entry`='2872';
UPDATE `locales_page_text` SET `Text_loc7`='Mi querida Emily:$B$BHace pocos dias abandonamos el campamento en este lugar dejado de la mano de la Luz, siguiendo las ordenes del rey de regresar a casa. A pesar de lo inhospito del paisaje, mi corazon estaba henchido de gozo, ya que sabia que tras el frio y penoso viaje hacia la costa y mas alla, podria solazarme entre tus brazos.$B$BHoy alcanzamos la costa y descubrimos que nuestros barcos, con los que ibamos a regresar, se habian convertido en cascajos quemados. No podemos irnos y no tenemos otra opcion que internarnos en el corazon de esta abismal tierra baldia.$B$BHe ido hasta el mismo fin del mundo para mantenerte a salvo, Emily... y ahora... desearia con todo mi corazon haberme quedado contigo en Lordaeron.$B$BEstas presente en cada momento de vigilia. En esta tierra gelida, tu eres mi fuente de calor, mi amada, y nadie podra quitarme eso.$B$BMaxwell' WHERE `entry`='2878';
UPDATE `locales_page_text` SET `Text_loc7`='Reuben,$B$Bte escribo a sabiendas que quizas nunca leas esto. No puedo permanecer inmovil escuchando los golpes contra los muros de la Vega del Amparo. Los no-muertos rodean nuestra aldea y nos atacan constantemente. Tenemos que defender a nuestra gente hasta que lleguen los refuerzos.$B$BMe rompi la pierna durante el ultimo ataque y aqui me ves, inutil y dejando una brecha en nuestras filas. No se escucha otro sonido que el de la batalla y la muerte. El miedo se huele en el aire.$B$BEl principe Arthas esta aqui, peleando en el frente con sus hombres. Si el no estuviese aqui ya hubiesemos muerto hace tiempo. Su amor por este territorio y su gente es abrumante. Sigo sus ordenes con esmero, con mi pierna rota o no, y por ello no puedo quedarme aqui. Tengo que ayudar con mi espada en el frente. Espero que estas palabras te lleguen en un mejor momento.$B$BTu amigo,$BLeagrem' WHERE `entry`='2880';
UPDATE `locales_page_text` SET `Text_loc7`='Heliana:$B$BYo, Vargus, vivo, a pesar de que tu desees lo contrario. Desprecio profundamente ir de un lado a otro metido en esta ridicula armadura.$B$BNuestras ultimas ordenes nos hicieron marchar hacia Andorhal en busca de grano o cualquier otra idiotez. ¿Por que habrian de importarme los almacenes de alimento en Andorhal? Estar en el ejercito es completamente absurdo.$B$BTia Heliana, ten la seguridad de que regresare para hacerme con tu herencia. Aunque quedara gravemente herido y sin alimento, el puro rencor me llevaria en andas para hacerme con la recompensa.$B$BVargus$B$B' WHERE `entry`='2881';
UPDATE `locales_page_text` SET `Text_loc7`='Queridisima Amelia:$B$BEsta noche he visto cosas que me rondaran hasta el final de mis dias.$B$BStratholme esta en llamas... y nosotros somos los causantes del incendio.$B$BNuestro principe nos condujo a las calles de la ciudad por la noche. Ordeno entrar en las casas del pueblo y... matarlos mientras dormian. Estaban apestados, segun Lord Arthas y, antes de que nos mataran, debian ser asesinados.$B$BFue una carniceria. Cientos murieron silenciosamente bajo las espadas de aquellos que habian jurado protegerlos. No pude permanecer ni un minuto mas alli:$B$BHui. Aunque me haya convertido en un desertor, no puedo cometer semejantes atrocidades. En cada casa no veia mas que tu rostro o los de nuestros ninos, confundidos con los de las victimas. Si estar contra esto significa ser un traidor, entonces lo soy.$B$BEspero poder encontrar el camino de regreso que me lleve a estar contigo, pero los caminos son inseguros. Transmite a nuestros hijos mi amor en mi ausencia.$B$BJames' WHERE `entry`='2882';
UPDATE `locales_page_text` SET `Text_loc7`='Samla:$B$BA medida que seguimos a Lord Arthas en direccion al norte, siempre hacia el norte, mi corazon se entristecia cada vez mas. Si bien una vez estuvo tocado por la Luz, ahora siento la oscuridad del espiritu del joven paladin. Su celo esta oscurecido por una inquietante y extrana pesadilla que inunda su alma, imposible de adivinar... $B$BPronto avistaremos tierra en los gelidos paramos. Aunque muchos de sus hombres han enfermado por el frio y por las batallas con las putridas bestias, Arthas nos comunico que lo que esta buscando en el hielo cambiara el sentido de la batalla. Pero sus palabras no me han tranquilizado, porque despues de pronunciarlas... la macabra sonrisa que se dibujo en sus ondulados labios me provoco un escalofrio mas profundo que el de cualquier ventisca.$B$BReza por nosotros, Samla, y reza por nuestro mundo,$B$BTorgal' WHERE `entry`='2883';
UPDATE `locales_page_text` SET `Text_loc7`='Querida Pamela:$B$BManana entraremos en batalla en Andorhal y me temo, amor mio, que esta va a ser mi ultima carta. Los no-muertos no se cansan y nosotros solo somos humanos. No podemos enfrentarnos a ellos. Pero no te inquietes, ya que, aunque estamos seguros de que vamos a morir, nuestras esperanzas permanecen firmes. ¡La Luz vencera!$B$BY, querida mia, me alegra saber que, mientras estos oscuros terrores derriban nuestros muros y caen sobre nosotros, mis ultimos pensamientos no seran de muerte. Te recordare entre mis brazos y, finalmente, tendre paz.$B$BChristoph' WHERE `entry`='2884';
UPDATE `locales_page_text` SET `Text_loc7`='Hola, $N. Al parecer, he extraido cierta informacion procedente de esta nota.$B$BSobre James, me temo que apenas he encontrado nada. Sin embargo, he descubierto ciertos datos familiares sobre Amelia.$B$BSi bien ni ella ni sus hijos sobrevivieron a la guerra, su hermano, un hombre llamado Alexander Calder, si sobrevivio. Puedes encontrarlo en la Caverna Abandonada en Forjaz, estudiando la peligrosa magia de la Legion Ardiente.$B$BElla tenia una prima a la que estaba muy unida, una mujer conocida ahora como Deze Delirioniveo, que apoyo la batalla por la Cuenca de Arathi, en el territorio de Orgrimmar.$B$BCuando estes cerca de cualquiera de ellos, deberias ir a visitarlos. Estoy seguro de que estaran muy interesados en recibir noticias de la pobre Amelia.' WHERE `entry`='2885';
UPDATE `locales_page_text` SET `Text_loc7`='Saludos, $N. He pensado que los resultados de mi investigacion podrian ser de tu interes.$B$BAl parecer, tanto a Reuben como a Leagrem los sobrevivieron, por asi decirlo, sus familiares. Incluso si ya saben lo que ocurrio con el Soldado perdido, visitar a cualquiera de ellos puede valer la pena.$B$BPuedes encontrar a la ex mujer de Reuben, Joanna Salablanca, en el Barrio de la Guerra de Entranas, en donde pasa la mayor parte del tiempo. No estoy seguro de como recibira la noticia sobre su ex marido.$B$BEn lo que respecta a Leagrem, su tia es la supervisora del orfanato de Ventormenta, un trabajo ciertamente admirable.$B$BHagas lo que hagas en la vida, te deseo la mejor de las suertes en tus empenos.' WHERE `entry`='2886';
UPDATE `locales_page_text` SET `Text_loc7`='$N, espero que cuando recibas esta carta te encuentres bien. Tras investigar un poco, he descubierto ciertos datos sobre aquellos que escribieron la nota que recuperaste.$B$BEl padre de Samla conduce a los Renegados y a los Trols durante el peregrinaje a Cima del Trueno, siguiendo los caminos de su fe. Su nombre es Miles Gales y puedes encontrarlo junto con sus fieles no-muertos en las Pozas de las Visiones.$B$BMientras tanto, a Torgal lo sobrevivio su prima Elissa Dumas. Segun he sabido, es una ermitana y pasa sus dias en el Templo de la Luna en Darnassus.$B$BTe ruego que visites a cualquiera de estas personas, $N. Quizas nadie les haya traido noticias sobre la suerte de Torgal o de Samla.' WHERE `entry`='2887';
UPDATE `locales_page_text` SET `Text_loc7`='El Guardian me entrego la carta que descubriste, $N, y creo que apreciaras en lo que vale tener noticias frescas.$B$BEl ultimo \"Vargus\" superviviente que se menciona en la carta ha sido encontrado en el desierto de Silithus, en el Fuerte Cenarion. Se le conoce como Garon Hutchins.$B$BLa proxima vez que vayas a los paramos, deberias hablar con el. Esta muy interesado en conocer los datos que obran en nuestro poder.' WHERE `entry`='2888';
UPDATE `locales_page_text` SET `Text_loc7`='Saludos. Por fin he encontrado un poco de tiempo para examinar la carta que encontraste sobre la Plaga. Aunque me tomo cierto trabajo, creo haber descubierto a un pariente vivo de los fallecidos.$B$BLa hermana de Maxwell, una tal Zarena Cromvento, dirige una pequena armeria en la Bahia del Botin. Desconozco cual era el grado de relacion con su hermano, pero si pasas por ahi, deberias concertar una cita con ella. Poder disponer de datos fehacientes sobre estas cuestiones es algo muy valioso y poco habitual en estos tiempos.' WHERE `entry`='2889';
UPDATE `locales_page_text` SET `Text_loc7`='Finalmente he descubierto ciertos datos sobre el antiguo Soldado que combatiste. El rastro ha sido dificil de seguir, ya que su arbol genealogico no esta debidamente documentado, pero he logrado encontrar a un pariente consanguineo.$B$B¿Puedes creer que el custodio Alen, de la Capilla de la Esperanza de la Luz, es en realidad el hijo de Christoph? Oir noticias sobre su padre seguro que le agradara mucho. Si alguna vez vuelves a estar de servicio en El Alba Argenta en las Tierras de la Peste, por favor, habla con el. Seria todo un detalle.' WHERE `entry`='2890';
UPDATE `locales_page_text` SET `Text_loc7`='En recuerdo al Comandante Supremo aliado, Anduin Lothar: un hombre que lo sacrificaria todo en defensa de su rey, su gente, su hogar...$B$BDejad que nuestros enemigos sepan nuestros nombres. Dejad que nuestros aliados honren nuestro paso. Somos los hijos de Lothar. $B$BGeneral Turalyon' WHERE `entry`='2891';
UPDATE `locales_page_text` SET `Text_loc7`='Aqui yacen los restos de un peon fuerte de corazon. Intento luchar, pero solo se tiro un pedo.' WHERE `entry`='2892';
UPDATE `locales_page_text` SET `Text_loc7`='Articulo 1 de 1' WHERE `entry`='2904';
UPDATE `locales_page_text` SET `Text_loc7`='Aqui esta el bote de cenizas. Estas son las cenizas de mi cordura, mi pasion, y mi conducta. Todo, cada uno de ellos absolutamente destruido. Que todos aquellos que vean estas tierras desoladas del fuego del infierno recuerden este peon caido. El derramo la sangre de la Alianza, y se sacrifico por la Horda hasta enloquecer completamente a causa de los malvados y desalmados que devoraban todo aquello que consideraba mas querido. Que mientras festejen su victoria, sufran su ira. Quiza no en este mundo, pero en cualquier otro venidero. Es mi declaracion, mi solemne juramento, y mi eterna promesa. Vengare mi sufrimiento.' WHERE `entry`='2905';
UPDATE `locales_page_text` SET `Text_loc7`='Inspecciona la extension de las islas e informa.$B$BRecuerda, ¡que no te pillen!$B$BNo me falles, Candress.$B$BS.' WHERE `entry`='2906';
UPDATE `locales_page_text` SET `Text_loc7`='El esbozo muestra Shadra\'Alor y sus tres templos, con varias \'X\' marcadas, que corresponden a las ubicaciones de los huevos robados. Las marcas aparecen en el extremo sur del tempo oriental, cerca de una cabana al noreste del templo oriental, cerca de un altar al este del lago central y en la ladera del templo sur, cerca de la palabra \"exilio.\"$B$BLas notas de Barbapluma indican que planeaba entregar esta informacion a Falstad Martillo Salvaje, de quien esperaba que enviara un grupo de guerreros para rescatar los huevos.' WHERE `entry`='2907';
UPDATE `locales_page_text` SET `Text_loc7`='Diathorus,$B$BConfio en que esta carta finalmente llegara a ti. Estos demonios son estupidos. Lo unico en lo que piensan son sus garras, sus latigos o en agujerear con las puas de su cabeza. ¡Y no me atrevo a usar uno de los infernales por miedo a que sea reducido a cenizas!$B$BLamentablemente, anhelo los dias en los que podiamos usar a los orcos. Al menos ellos tenian medio cerebro y podian seguir ordenes. ¿Quizas haya alguna oportunidad de tomar algunos esclavos nuevos de algun lugar cercano?' WHERE `entry`='2909';
UPDATE `locales_page_text` SET `Text_loc7`='Los informes de los elfos de la noche que ayudan a los draenei son ciertos. Estan intentando establecer una nueva base de operaciones en Cancion del Bosque, al norte, cerca de Satyrnaar. Deberiamos debatir sobre aunar fuerzas para atacar antes de la proxima luna llena de este patetico mundo. Si los aplastamos ahora, nunca conseguiran un punto de apoyo en nuestro umbral.$B$BSaludos,$B$BGorgannon$B$BP.D.: ¡Deja de usar toda esa tinta de sangre en las cartas de amor para mis azotadoras! Las elfas de la noche virgenes escasean en estos tiempos.' WHERE `entry`='2910';
UPDATE `locales_page_text` SET `Text_loc7`='Diathorus,$B$BConfio en que esta carta finalmente llegara a ti. Estos demonios son estupidos. Lo unico en lo que piensan son sus garras, sus latigos o en agujerear con las puas de su cabeza. ¡Y no me atrevo a usar uno de los infernales por miedo a que sea reducido a cenizas!$B$BLamentablemente, anhelo los dias en los que podiamos usar a los orcos. Al menos ellos tenian medio cerebro y podian seguir ordenes. ¿Quizas haya alguna oportunidad de tomar algunos esclavos nuevos de algun lugar cercano?' WHERE `entry`='2911';
UPDATE `locales_page_text` SET `Text_loc7`='Ha llegado el momento de recuperar algunas de las razas menores, en mi opinion. El Puesto del Hachazo, al noroeste, es un objetivo tentador. Deberiamos debatir sobre aunar fuerzas para atacar antes de la proxima luna llena de este patetico mundo. Si los subyugamos ahora, llegaran a recordar lo que la palabra \"Horda\" solia significar verdaderamente.$B$BSaludos,$B$BGorgannon$B$BP.D.: ¡Deja de usar toda esa tinta de sangre en las cartas de amor para mis azotadoras! Las elfas de la noche virgenes escasean en estos tiempos.' WHERE `entry`='2912';
UPDATE `locales_page_text` SET `Text_loc7`='Aunque casi fue vencido $BSu coraje prevalecio $BTomo su espada, la desenvaino$BY ataco al vil gentio $B$BDesde ese violento dia $BNunca una flecha o espada $BEncontraria, en batalla o reyerta,$BSu camino hasta el senor de los enanos' WHERE `entry`='2913';
UPDATE `locales_page_text` SET `Text_loc7`='...pero ella ya lo sabia. Se habia resignado a vivir como un paria despues de vender su alma a pedazos. La venganza lo habia valido, o al menos eso pensaba ella.$B$BPero ahora tenia una oportunidad de recobrar su humanidad y todo lo que pedia el extrano a cambio era su confianza.$B$B\"Lo hare\", dijo Katreyn. \"No tengo nada que perder.\"' WHERE `entry`='2914';
UPDATE `locales_page_text` SET `Text_loc7`='Articulo 3 de 4.' WHERE `entry`='2915';
UPDATE `locales_page_text` SET `Text_loc7`='Vazruden,$B$BDesde que el Portal Oscuro se ha vuelto a abrir, el Bastion del Honor y Thrallmar han estado recibiendo refuerzos importantes de Azeroth. He visto informes en los que se sugiere que estan planeando un ataque a la ciudadela y, mientras, nosotros tenemos que enfrentarnos tambien con la Legion Ardiente. ¡Esto es inaceptable!$B$B¡No pongas en peligro la operacion del Horno de Sangre! Los viles orcos que se fabrican alli son la clave para nuestro exito. ¡Refuerza las defensas de las murallas y destina mas guardias al perimetro inmediatamente!$B$BConfia en mi, no quieres que te haga una visita.$B$BIllidan' WHERE `entry`='2916';
UPDATE `locales_page_text` SET `Text_loc7`='Vazruden,$B$BDesde que el Portal Oscuro se ha vuelto a abrir, el Bastion del Honor y Thrallmar han estado recibiendo refuerzos importantes de Azeroth. He visto informes en los que se sugiere que estan planeando un ataque a la ciudadela y, mientras, nosotros tenemos que enfrentarnos tambien con la Legion Ardiente. ¡Esto es inaceptable!$B$B¡No pongas en peligro la operacion del Horno de Sangre! Los viles orcos que se fabrican alli son la clave para nuestro exito. ¡Refuerza las defensas de las murallas y destina mas guardias al perimetro inmediatamente!$B$BConfia en mi, no quieres que te haga una visita.$B$BIllidan' WHERE `entry`='2917';
UPDATE `locales_page_text` SET `Text_loc7`='Yo lo hice. No recuerdo haberlo hecho, pero no puede ser de otra manera.$B$BHe cometido pecados en otros momentos de mi vida. Incluso mate a un hombre que no lo merecia. Pero esto es diferente.$B$BHe cambiado. $B$BNo elegi estos asesinatos. Una bestia vive dentro de mi.$B$BMe cazaran y huiran de mi. Tengo que esconderme. Correr. Enterrar los cuerpos.$B$BEstoy saliendo cuando me doy cuenta de lo que realmente me molesta.$B$BNo siento culpa.' WHERE `entry`='2918';
UPDATE `locales_page_text` SET `Text_loc7`='Sirviente,$B$BLos planes de varios anos se han perdido. ¡Los draenei y su lamentable lider han escapado del alcance del Maestro una vez mas! Es mas, ¡me han robado MI navio y lo han estrellado contra Azeroth! $B$BAunque es cierto que la agente eredar del Maestro ha fracasado, pudo transmitirnos la ubicacion de El Exodar. Afortunadamente, su identidad sigue siendo un misterio para los draenei. Puede que todavia podamos usarla para nuestros fines.' WHERE `entry`='2919';
UPDATE `locales_page_text` SET `Text_loc7`='Ponte en contacto con nuestra espia y preparaos para un ataque a gran escala a los draenei. Matadlos a todos y recuperad mi navio. Eres el unico enlace que tengo en Azeroth que puede reparar el dano que esas bestias han causado en El Exodar.$B$BNo me falles, gnomo. Si tu ansia de riqueza interfiere con mis ordenes, pasaras la eternidad en oracion. Rezaras para que venga la muerte y te libere de las torturas a las que dare rienda suelta si vacilas.$B$BRey Caminante del Sol' WHERE `entry`='2920';
UPDATE `locales_page_text` SET `Text_loc7`='P.D Nuestro gran magister ha recibido respuesta de Terrallende acerca de la pregunta de Thrall. La respuesta es, con gran certeza, si.$B$B<Esta carta porta el sello adicional de Lady Sylvanas Brisaveloz>' WHERE `entry`='2922';
UPDATE `locales_page_text` SET `Text_loc7`='Estimados lideres de la Horda,$B$BCon gran placer yo - Lor\'themar Theron, senor regente de Quel\'Thalas ? anuncio la caida del comandante de la Plaga conocido por nosotros como Dar\'Khan Drathir.$B$BEl punto de apoyo de Dar\'Khan en nuestras tierras fue un asunto comentado por los criticos de la raza sin\'dorei en numerosas negociaciones anteriores. Esperamos que este suceso aborde todas y cada una de las objeciones anteriores que sus ilustrisimas pudieran haber abrigado.$B$B<Firmado>Lor\'themar Theron' WHERE `entry`='2923';
UPDATE `locales_page_text` SET `Text_loc7`='Estimados lideres de la Horda,$B$BCon gran placer yo - Lor\'themar Theron, senor regente de Quel\'Thalas ? anuncio la caida del comandante de la Plaga conocido por nosotros como Dar\'Khan Drathir.$B$BEl punto de apoyo de Dar\'Khan en nuestras tierras fue un asunto comentado por los criticos de la raza sin\'dorei en numerosas negociaciones anteriores. Esperamos que este suceso aborde todas y cada una de las objeciones anteriores que sus ilustrisimas pudieran haber abrigado.$B$B<Firmado>Lor\'themar Theron' WHERE `entry`='2924';
UPDATE `locales_page_text` SET `Text_loc7`='Reparacion de naves transdimensionales para tontos, por Jamus Kaesi$B$B<Las finas paginas de este libro estan hechas con un material resplandeciente y llenas de diagramas sumamente tecnicos sobre como conservar en buen estado y reparar un barco transdimensional. Esta copia en concreto esta abierta por un capitulo llamado \"Grietas de la Espiral Vectorial y como evitarlas\".>' WHERE `entry`='2925';
UPDATE `locales_page_text` SET `Text_loc7`='P.D Nuestro gran magister ha recibido respuesta de Terrallende acerca de la pregunta de Thrall. La respuesta es, con gran certeza, si.$B$B<Esta carta porta el sello adicional de Lady Sylvanas Brisaveloz>' WHERE `entry`='2926';
UPDATE `locales_page_text` SET `Text_loc7`='P.D Nuestro gran magister ha recibido respuesta de Terrallende acerca de la pregunta de Thrall. La respuesta es, con gran certeza, si.$B$B<Esta carta porta el sello adicional de Lady Sylvanas Brisaveloz>' WHERE `entry`='2927';
UPDATE `locales_page_text` SET `Text_loc7`='$N,$B$BLa creacion de la Corcesca templada en sangre requiere una variedad de materiales volatiles y pocos frecuentes, que dan como resultado un arma temible de artesania superior. Enumerare los materiales por orden del mas facil hasta el mas dificil de obtener.$B$BNo olvides que tambien debes obtener tu insignia de Caballero de sangre antes de que se acepten los materiales y pueda comenzar el proceso de forja.' WHERE `entry`='2930';
UPDATE `locales_page_text` SET `Text_loc7`='Sangre del Colerico - Para poder templar adecuadamente el metal de tu arma, debe templarse en la sangre del Colerico. Aunque la sangre de la mayoria de los demonios contiene una cantidad de poder considerable, la sangre comun no es suficiente para forjar tu corcesca.$B$BLas preguntas discretas a los brujos de Orgrimmar han probado ser utiles para ubicar un suministro de la sangre. Los cultores de la Hoja Abrasadora de la Sima ignea poseen un orbe lleno del tipo de sangre que necesitaremos para tu arma. Me dicen que el orbe lo vigila un orco que se llama a si mismo Jergosh el Convocador.$B$BSe prudente, como en todos tus asuntos con poderes demoniacos. Nunca se sabe lo que puede ocurrir cuando se maneja una sustancia tan poderosa.' WHERE `entry`='2931';
UPDATE `locales_page_text` SET `Text_loc7`='Caja de lingotes forjados con sangre - Por si solo, el acero es un metal excelente, pero un arma forjada con el es mediocre. Los lingotes forjados con sangre presentan un material superior creado en un proceso que es un secreto bien guardado. Se rumorea que cualquier herrero lo suficientemente audaz como para manejar y trabajar el metal se vuelve maldito, pero nuestros artesanos han demostrado que eso es falso.$B$BLa reputacion del metal aumenta su escasez, pero los Renegados han enviado un mensaje que dice que puede que exista un suministro en el Castillo de Colmillo Oscuro. Un diestro herrero de nombre Landen Fontana tuvo la curiosidad suficiente como para pasar a escondidas una caja de lingotes dentro del castillo. No se sabe donde guarda su alijo Fontana, pero nuestro informador nos dice que el herrero es prisionero en la mazmorra del castillo. Utiliza todos los recursos que esten a tu disposicion para obtener los lingotes.' WHERE `entry`='2932';
UPDATE `locales_page_text` SET `Text_loc7`='Una gema kor corrupta - Los libros del magister registran que estas gemas son utilizadas por algunos taumaturgos para mantener energia para sus encantos, para hacerlos aun mas letales. Aunque los oficiales taumaturgos pueden utilizar versiones purificadas de la gema, son demasiado debiles para nuestros propositos.$B$BEn las cuevas de las Cavernas de Brazanegra, los taumaturgos nagas han atribuido a las gemas kor sus propios poderes oscuros, aumentandolas de una forma que las hace perfectas para usarlas en tu corcesca. Matalos sin piedad hasta que hayas obtenido la gema que necesitaras.' WHERE `entry`='2933';
UPDATE `locales_page_text` SET `Text_loc7`='La insignia que llevaras como adepto la llevo por ultima vez un valiente e intrepido Caballero de sangre de la familia Estrella del Alba. Su servicio ejemplar es un buen augurio para tu propia vida en la orden.$B$BEl Caballero de sangre de la familia Estrella del Alba dirigio un ataque audaz, pero infructuoso contra el Bastion de la Plaga en Ciudad de la Muerte. Su cuerpo descansa donde murio, enzarzado en combate con nuestros mayores enemigos. Encuentralo, $N, y traeme su insignia. Tu exito le llevara honor a el, a ti y a todos los verdaderos Caballeros de sangre.' WHERE `entry`='2934';
UPDATE `locales_page_text` SET `Text_loc7`='¡Has descubierto la ubicacion del santuario! Tras un mayor reconocimiento, detectas una pulsacion mas fuerte del extrano poder que se ha aduenado de la Isla. Te sientes algo incomodo, de pie junto al santuario... y tal vez un poco preocupado.$B$BEl cartel bronce que se encuentra en el lateral del santuario reza:$B$B\"Aqui se encuentra el Santuario de Dath\'Remar, un tributo adecuado para un noble elfo. Que todo el que mire este monumento recuerde su sacrificio contra la Plaga y su dedicacion a la causa de nuestra supervivencia duradera. Todo el que prospera en Quel\'Thalas lo hace gracias a el.\"' WHERE `entry`='2936';
UPDATE `locales_page_text` SET `Text_loc7`='Observe mientras lo golpeaban sin compasion. Su despiadado teniente actuaba como si tal crueldad fuera un acto de normalidad. A traves de la salvaje paliza, sentia que yo mismo me quebrara.$B$BMientras observaba como el vindicador Saruan recibia sus golpes, comence a sollozar. No por miedo... Llore de tristeza. Ver a un draenei del Triunvirato tratado de tal forma. Si yo pudiera haberme liberado de mis ataduras. Si yo pudiera...' WHERE `entry`='2937';
UPDATE `locales_page_text` SET `Text_loc7`='Tras lo que parecio ser una eternidad, el vindicador perdio la conciencia. La tortura era demasiado, incluso para el. El cruel Matis, como descubri que le llamaban intento despertar a Saruan salpicando su devastado cuerpo con agua con contagio. Pero Saruan no desperto.' WHERE `entry`='2938';
UPDATE `locales_page_text` SET `Text_loc7`='Quise gritar, pero solo escapo de mis labios un debil jadeo. Me azotaron de inmediato por aquel acto de rebeldia. Mientras yacia en el suelo, pude ver a Matis paseandose delante del cuerpo de Saruan. Parecia visiblemente preocupado. Afirmo que Sironas, Sirona o alguna entidad tal haria que se le arrancara la carne de los huesos si Saruan habia perecido debido a la paliza.' WHERE `entry`='2939';
UPDATE `locales_page_text` SET `Text_loc7`='Que la entidad Sironas tenia planes para Saruan…$B$B< La escritura se detiene repentinamente y una larga linea se desplaza hasta salirse de la pagina. Aquel draenei murio mientras escribia.>' WHERE `entry`='2940';
UPDATE `locales_page_text` SET `Text_loc7`='Lady Liadrin,$B$BEl reciente problema con la criatura cautiva ha danado parte de nuestro equipo mas esencial. Restablecer control sobre el ha desgastado a mis magistri y mis recursos. He presentado informes del incidente al Gran magister Rommath, pero ha respondido con lentitud. Me temo que, sin los materiales siguientes, puede que nos veamos obligados a reducir nuestras operaciones, y sabes lo que eso significaria para tus caballeros.$B$BPor si hay algo que puedes hacer para ayudarnos, en la pagina siguiente he incluido una lista de los materiales. Agradeceria enormemente cualquier ayuda que tu orden pudiera proporcionar.$B$BMagister Astalor Jurasangre' WHERE `entry`='2941';
UPDATE `locales_page_text` SET `Text_loc7`='* 40 Panos runicos$B$B* 6 Barras de arcanita$B$B* 10 Soleas$B$B* 5 Runas oscuras$B$B* 150 Piezas de oro' WHERE `entry`='2942';
UPDATE `locales_page_text` SET `Text_loc7`='Declaracion del Rey del Sol $B$BHalcones del Sol ? mis queridos y leales hijos ? vuestro rey se encomienda a vosotros. Vuestra presencia en Azeroth es de suma importancia para mi y para el Maestro.$B$BEl portal del Sol pronto estara preparado para transportar refuerzos desde El Castillo de la Tempestad directamente a la Isla Bruma de Sangre. $B$BRecuperaremos El Exodar. Mataremos a los bastardos de piel azul donde esten. Velen pagara por sus crimenes.$B$BRey Kael\'thas, el Caminante del Sol' WHERE `entry`='2943';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><IMG src=\"Interface\Pictures\24475_gordawg_256\"/></BODY></HTML>' WHERE `entry`='2944';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><BR/><H1 align=\"center\">LAS VERDES COLINAS DE TUERCESPINA</H1><BR/><H3 align=\"center\">por Hemet Nesingwary</H3></BODY></HTML>' WHERE `entry`='2945';
UPDATE `locales_page_text` SET `Text_loc7`='Nuestro primer dia fue todo lo bien que se puede esperar que vayan los primeros dias. La mayor parte del tiempo nos preocupamos de realizar los preparativos necesarios para establecer un campamento base. Encontre un lugar ideal, junto a la ensenada de un rio de agua dulce. A juzgar por los viejos y abandonados muelles que hay cerca, este emplazamiento estuvo habitado hace algun tiempo. Por lo que respecta a los habitantes primitivos, solo el tiempo puede contar esa historia.' WHERE `entry`='2946';
UPDATE `locales_page_text` SET `Text_loc7`='Para esta expedicion he reunido a Ajeck Rouack y a Sir S. J. Erlgadin, junto con mi sirviente de confianza, Barnil Jarropetra. Libre muchas batallas al lado del padre de Ajeck en defensa de la Alianza. Verla crecida es bastante especial. Su padre la instruyo bien en las artes de las armas. Su habilidad con el arco me hace preguntarme si corre sangre elfica por esas venas.' WHERE `entry`='2947';
UPDATE `locales_page_text` SET `Text_loc7`='Sir S. J. Erlgadin proviene de la aristocracia humana. Su padre, el conde Erlgadin, era celebre por su generosidad. Fue el conde quien presiono por una mejora en las condiciones de trabajo de la Hermandad de los Albaniles durante la restauracion de Ventormenta despues de la Segunda Gran Guerra.' WHERE `entry`='2948';
UPDATE `locales_page_text` SET `Text_loc7`='En los anos que siguieron, despues de que Ventormenta traicionara a la hermandad de los albaniles, Sir Erlgadin se fue volviendo mas rencoroso sobre el papel de los nobles en el Reino. Ya no deseaba defender la posicion que la estirpe de su padre habia ganado para el en la Casa de Nobles.$B$BPero me estoy yendo por las ramas. El proposito de esta historia no es que sirva como tratado politico o biografia. Este es el relato de mis experiencias en busca de caza mayor por las verdes colinas de Tuercespina....' WHERE `entry`='2949';
UPDATE `locales_page_text` SET `Text_loc7`='Nos levantamos al amanecer. Barnil empezo a preparar el desayuno. Adverti que Ajeck estaba un poco distraida. La caminata del dia prometia ser larga y nos esperaban peligrosos episodios de caza. Cualquier descuido podia ponernos en una situacion dificil. No obstante, Ajeck parecia incapaz de desviar su mirada de Barnil, que estaba de pie frente al rio, limpiando su equipo.' WHERE `entry`='2950';
UPDATE `locales_page_text` SET `Text_loc7`='Justo cuando estaba a punto de cuestionar la falta de interes de Ajeck en la estrategia de caza del dia, alargo la mano hacia su arco, lo tenso con una flecha y la solto justo hacia el pobre Barnil. Pero no era a Barnil a quien Ajeck disparaba, pues, cuando Barnil se echo a un lado boquiabierto, un gran crocolisco de rio floto a la superficie con la flecha de Ajeck perfectamente colocada entre sus grandes ojos.' WHERE `entry`='2951';
UPDATE `locales_page_text` SET `Text_loc7`='Nos pusimos en camino hacia el oeste, a traves de la espesa maleza de la enmaranada jungla. Con pasos lentos y cuidadosos, avanzamos a traves de un espeso follaje en busca de presa. La manana paso en un silencio frustrante. Nada se agitaba en la Vega, ni una brisa. Hacia la tarde, la expedicion se mostraba impaciente. Barnil ya no andaba con los pasos cautelosos de un depredador que rastrea a su presa. En su lugar, caminaba pesada y torpemente por el camino, a menudo pisando ruidosamente hojas secas o ramas caidas.' WHERE `entry`='2952';
UPDATE `locales_page_text` SET `Text_loc7`='Durante semejante tropiezo, Erlgadin poso su mano pesadamente sobre el hombro de Barnil. Ajeck y yo apenas advertimos la escena, suponiendo que el hombre solo estaba dando una merecida reprimenda a Barnil por su descuido. No obstante, Erlgadin hizo un gesto lento con la cabeza en direccion a un arbol caido. Desde alli nos acechaban dos ojos negros penetrantes justo encima de un par de afilados colmillos.' WHERE `entry`='2953';
UPDATE `locales_page_text` SET `Text_loc7`='La bestia era un tigre macho de Tuercespina. Antes de que pudiera amartillar el rifle, Erlgadin levanto la ballesta y disparo en direccion al animal. El virote dejo su marca y sorprendio a la bestia con fuerza en el costado izquierdo. El tigre hizo un intento en vano por huir, pero la herida era demasiado grave. La bestia fue dando traspies durante varios tragicos segundos, hasta que Barnil lo remato lanzando un hacha.' WHERE `entry`='2954';
UPDATE `locales_page_text` SET `Text_loc7`='La matanza provoco un animo festivo en la expedicion. Barnil sirvio aguamiel para disfrute de todos. Pero nuestros festejos fueron efimeros. Mientras preparabamos el cadaver para transportarlo de vuelta al campamento base, un rugido espantoso nos cogio desprevenidos a todos. En todos mis anos nunca he oido nada que helara la sangre de tal modo.' WHERE `entry`='2955';
UPDATE `locales_page_text` SET `Text_loc7`='En un rocoso precipicio en lo alto, perfilado por el sol poniente, podia distinguir el felino depredador mas grande que he visto jamas. Pude liberar una descarga con mi rifle, pero el felino siguio su camino. Rugio una vez mas, en esta ocasion vez con mas fuerza que la anterior, y desaparecio.$B$BRecogimos nuestras pertenencias y nos dirigimos solemnemente de vuelta al campamento.' WHERE `entry`='2956';
UPDATE `locales_page_text` SET `Text_loc7`='Habia prometido a la expedicion que pasariamos el dia siguiente cazando panteras, ya que sus pieles son muy valoradas en Azeroth. Esta demanda se debe a que todos los cazadores, tramperos y comerciantes de pieles arriesgan valientemente su vida en nombre de la Alianza.' WHERE `entry`='2957';
UPDATE `locales_page_text` SET `Text_loc7`='Ajeck y Sir Erlgadin estaban ansiosos por aprender a cazar eficazmente con un rifle de los enanos. Hice que los dos humanos dejaran sus primitivas armas en el campamento base. Barnil y yo los equipamos con algunas de las mejores armas de fuego de Forjaz.' WHERE `entry`='2958';
UPDATE `locales_page_text` SET `Text_loc7`='Ese dia nos aventuramos al sur, siguiendo algunos rastros recientes de pantera. Pronto llegamos a un barranco que se cruzaba por un enorme puente colgante. No pude evitar pensar en la descriptiva obra de Brann sobre aquella region cuando vi aquella maravilla de la ingenieria. Se suponia tan a menudo que los trols nativos eran una raza primitiva e inculta, pero, mientras observaba fijamente la artesania maestra del puente, pude reconocer la habilidad con la que los albaniles trols superaron la hazana, aparentemente imposible.' WHERE `entry`='2959';
UPDATE `locales_page_text` SET `Text_loc7`='Al poco tiempo, Ajeck rastreo a la pantera en direccion al sudoeste. Anduvimos en silencio, con las armas preparadas, anticipandonos a nuestra presa. Un chasquido de ramas en un bosquecillo cercano llamo nuestra atencion de inmediato. Alli habia algo. Una mirada seria a Barnil basto para transmitirle mis pensamientos. Barnil bajo su rifle lentamente. Esta presa no era para nosotros, sino para nuestros companeros humanos. Innumerables panteras habian perdido la vida al enfrentarse a nuestros humeantes canones. Esta presa era para los humanos.' WHERE `entry`='2960';
UPDATE `locales_page_text` SET `Text_loc7`='Tanto Ajeck como Sir Erlgadin se mantuvieron erguidos, con las armas al nivel de la hirsuta maleza que habia bajo unos arboles que se mecian de un lado a otro. El sol del mediodia ardia con fuerza sobre nosotros. Una lenta gota de sudor rodo por la sien de Erlgadin, al tiempo que retiraba el gatillo. Tras el chasquido, el follaje exuberante se abrio en dos y una pantera negra, especimen hermoso, salto veloz sobre la llanura.' WHERE `entry`='2961';
UPDATE `locales_page_text` SET `Text_loc7`='Los humanos tenian la mira puesta en la pantera mientras esta corria rodeando la arboleda. Los canones de los rifles se movian en paralelo con el animal. Barnil me miro impaciente y yo negue con la cabeza para que no abriera fuego. Esta caceria era para los humanos, no para Barnil ni para mi. Erlgadin lanzo un disparo, que erro completamente en su empeno de alcanzar a la pantera. Por lo visto, no estaba preparado para soportar la violenta repercusion de la explosion del rifle.' WHERE `entry`='2962';
UPDATE `locales_page_text` SET `Text_loc7`='El arma dio una violenta sacudida. El canon rodo lateralmente y cayo bajo el rifle de Ajeck. Ajeck habia elegido ese preciso momento para apretar el gatillo. El rifle, con la mira orientada torpemente hacia la fila de arboles, se disparo con un estruendo inconfundible. Una bandada de pajaros chillo desde la copa de un arbol, dispersandose por todas partes. Una columna de humo emergio del arbol. Sobrecogidos, vimos como una tremenda rama caia sobre la pantera que intentaba escapar, partiendole el lomo.' WHERE `entry`='2963';
UPDATE `locales_page_text` SET `Text_loc7`='A medida que pasaban las semanas, nuestras reservas de piel de pantera y tigre se hicieron inmensas. Decidi que era el momento de que la expedicion se centrara en un nuevo reto: los raptores.$B$BLos humanos, aunque agradecieron el adiestramiento que les ofrecimos Barnil y yo, decidieron abstenerse de cazar con armas de fuego. Ajeck se sentia mucho mas comoda con un arco delicadamente tensado y Sir Erlgadin nunca dejo el campamento sin su resistente ballesta.' WHERE `entry`='2964';
UPDATE `locales_page_text` SET `Text_loc7`='Nos pusimos en camino con las primeras luces, en direccion al sur, mas alla de las ruinas de Tkashi. Barnil expreso su preocupacion por el hecho de que pudieramos encontrarnos con miembros de la tribu Sangrapellejo. Recorde a Barnil que los Sangrapellejo estaban mas preocupados por destruir a su enemigo tribal, los Machacacraneos. No hace falta decir que eso no consolo a Barnil lo mas minimo. Sin embargo, yo tenia un rifle cargado, una cartera llena de polvora y tres cazadores letales conmigo para aliviar cualquier preocupacion de un emboscada poco amistosa.' WHERE `entry`='2965';
UPDATE `locales_page_text` SET `Text_loc7`='Me he encontrado ante un infernal enorme en el campo de batalla, mientras el ejercito de la Legion Ardiente avanzaba desde todas direcciones. Un grupo rebelde de trols me parece tan inofensivo como una liebre en las colinas de Dun Morogh.$B$BPasamos ante las ruinas de Tkashi sin suceso alguno, para alivio de Barnil. El grupo procedio a dirigirse hacia el oeste, hacia el Mare Magnum, bordeando las ruinas de Zul\'Kunda justo al sur. Mientras ascendiamos los altos riscos del mar, vimos a nuestro primer raptor.' WHERE `entry`='2966';
UPDATE `locales_page_text` SET `Text_loc7`='La bestia ni siquiera llego a detectar nuestra presencia. De hecho, el unico saludo que recibio de la expedicion fue una bala entre los ojos.$B$BSir Erlgadin solto un caluroso *hurra* mientras Ajeck asintio con la cabeza en mi direccion en senal de entusiasta aprobacion. Rebusque en mi saca en busca de mi pipa, con la esperanza de celebrarlo fumando. Barnil comenzo a subir la ladera a toda prisa para recuperar el cadaver del raptor. Mire fijamente la bestia caida con la satisfaccion que acompana a cada gran matanza.' WHERE `entry`='2967';
UPDATE `locales_page_text` SET `Text_loc7`='Sin embargo, no pude disfrutar de la victoria del cazador durante mucho tiempo, pues al contemplar el horizonte, numerosas siluetas aparecieron en la cresta de la colina, justo encima del pobre Barnil.$B$B*¡Huye, Barnil!*, grite. Ajeck, Sir Erlgadin y yo lanzamos una descarga de balas y flechas por encima de Barnil, directo sobre los raptores. Alguien logro una muerte en medio de la confusion.' WHERE `entry`='2968';
UPDATE `locales_page_text` SET `Text_loc7`='Nuestros disparos, que dirigimos apresuradamente, fueron suficientes para comprar la huida de Barnil. Barnil lanzo un clamor colina abajo y se volvio a unir al grupo. Corrimos a refugiarnos en la jungla; una manada de feroces raptores colazote acechaba todos nuestros movimientos.$B$BLos cazadores eran, ahora, los cazados.' WHERE `entry`='2969';
UPDATE `locales_page_text` SET `Text_loc7`='Conduje a la expedicion hasta el mar, con el fin de que la costa nos protegiera de los raptores. Con nuestro apresuramiento, nos habiamos desviado demasiado al norte, a una altitud peligrosamente elevada. Nos habiamos equivocado. Fue culpa mia. Nos detuvimos delante de un escarpado acantilado, con los raptores justo detras de nosotros.' WHERE `entry`='2970';
UPDATE `locales_page_text` SET `Text_loc7`='Avance lentamente con el arma levantada. Habia conducido a estos valientes cazadores a su fin. Tenia que morir defendiendolos. Los raptores colazote son particularmente feroces, conocidos por ser unos sanguinarios implacables. Eran muchos mas que nosotros. Pero si hubiera dejado que nos mataran a mis camaradas y a mi, sin antes derramar parte de su propia sangre, no me lo habria perdonado.' WHERE `entry`='2971';
UPDATE `locales_page_text` SET `Text_loc7`='Ajeck y Sir Erlgadin dispusieron sus armas, flanqueandome a cada lado, de espaldas al mar. Barnil dejo escapar un suspiro derrotado y blandio su hacha. Los colazotes estaban casi sobre nosotros. Su firme zancada se habia hecho mas lenta. Ahora acechaban a su presa, pues sabian que nos tenian atrapados.' WHERE `entry`='2972';
UPDATE `locales_page_text` SET `Text_loc7`='Entonces ocurrio algo milagroso. Desde donde estabamos escuchamos el inconfundible y aterrador rugido del gran tigre blanco. A pesar de ser numerosos, los raptores dieron media vuelta y se dispersaron. Solo vimos el breve destello blanco de un tigre que salio disparado junto a nosotros, abalanzandose sobre uno de los raptores. No fue necesario dar ninguna orden. Los cuatro miembros de la expedicion sabiamos que era el momento de salir corriendo.' WHERE `entry`='2973';
UPDATE `locales_page_text` SET `Text_loc7`='Corrimos a toda velocidad hasta el campamento base, sin reducir la marcha en ningun momento. Mas tarde aquella noche, nos sentamos en silencio alrededor de la hoguera, sabiendo que un extrano golpe de suerte nos habia salvado la vida. Esos son los riesgos que corre el cazador de caza mayor. Jugamos con el destino. No obstante, todos, en algun momento de nuestras vidas, debemos afrontar las afiladas garras del destino. Este enano se alegra, porque ese momento aun no ha llegado a las verdes colinas de Tuercespina.' WHERE `entry`='2974';
UPDATE `locales_page_text` SET `Text_loc7`='Estupido,$B$BEncuentra comida. Azul buen sabor. Lantresor decir estar bien. Traer comida a Zoquete. Zoquete comer primero, despues comer estupido. ¿De acuerdo? Ir rapido o Zoquete comer tu.$B$BZoquete' WHERE `entry`='2975';
UPDATE `locales_page_text` SET `Text_loc7`='(1) Raliq el Borracho: Ogro. Nunca deja cantina. Malo. Feo. Gordo. Malo. Tambien muy gordo.$B$B[Pag. siguiente]' WHERE `entry`='2976';
UPDATE `locales_page_text` SET `Text_loc7`='(2) Coosh\'coosh: ese molesto hombrecillo-espora de la Marisma de Zangar tiene jardin de setas en la frontera entre la Marisma de Zangar y Terokkar. Perdio muchas piezas de oro con Sal\'salabim en juego de \"Que es ese olor.\" Intento pagar a Sal\'salabim con una estupida seta llamada Fluochampinon. ¡Dice que tan bueno como oro! ¡Ja!$B$B[Pag. siguiente]' WHERE `entry`='2977';
UPDATE `locales_page_text` SET `Text_loc7`='(3) Floon: Arakkoa. Perdio muchas monedas de oro con Sal\'salabim. Sal\'salabim mato a hombre-pajaro por no pagar. Ahora el fantasma de hombre-pajaro camina por la parte noroeste del Vertedero de Huesos.' WHERE `entry`='2978';
UPDATE `locales_page_text` SET `Text_loc7`='La raiz de Olemba es una raiz bastante comun que se encuentra en el bosque de Terokkar. Busca raices respingonas cerca de los arboles de Olemba, que conforman la mayor parte del bosque.$B$BLa baya de la marisma crece unicamente en la marisma de Zangar. Solo se encuentra en el Bastion Ango\'rosh, situado en la region del noroeste del pantano.¡Cuidado con los ogros que protegen con fervor las preciadas bayas!$B$BLa fronda de Telaari es, quizas, la hierba mas facil de conseguir de esta lista. Encontraras las hojas al sur de Garadar, en la cuenca Telaari. Crecen cerca del agua.$B$BLa ultima hierba de la lista crece solo en las montanas Filospada, situadas al norte de la marisma de Zangar. El espinazo de dragon crece en los extremos de las formaciones de rocas dentadas que protegen los canones. Sabras de que hablo cuando veas Filospada por primera vez. Las formaciones de rocas parecen una hilera de dientes afilados y puntiagudos tras otra.' WHERE `entry`='2979';
UPDATE `locales_page_text` SET `Text_loc7`='Tus obras ya estan causando gran malestar a la Legion, $N. Mis exploradores acaban de informarme que la Legion ha recurrido al envio de comunicados a traves de mensajeria. ¡Hemos logrado con exito cortar las tropas de su asentamiento!$B$BLo proximo que te pido es mas de lo que le pediria a un soldado raso.$B$BEl correo de la Legion viaja desde las Pozas de Aggonar al Campamento Forja: Mageddon, al norte de aqui. Debes interceptar el correo en Mageddon, hacerte con el, y volver a mi con la misiva de la Legion.' WHERE `entry`='2981';
UPDATE `locales_page_text` SET `Text_loc7`='Sirviente. Estos orcos estan empezando a ser mas que una pequena molestia. Quiero personalmente ver atacar a ti y a nuestras fuerzas las defensas del Portal Oscuro dirigir toda su energia para arrasar Thrallmar. Es un orden, Arazzius' WHERE `entry`='2982';
UPDATE `locales_page_text` SET `Text_loc7`='Para otorgar al bebedor de la pocion la vision tintada para ver a los muertos, se deben reunir ciertos componentes materiales.$B$BPrimero, un globo ocular de gronn montes del Nagrand occidental, cerca de los campamentos Forja malditos de la Legion Ardiente, al sur de la Colina Mazo de Guerra.$B$BTambien se encuentran en el Nagrand occidental los vientorrocs superiores. Solo servira un ejemplar perfecto.$B$BPor ultimo, tanto en el Nagrand oriental como occidental se encuentra grasa de unagrieta anciano.$B$B¡Reune estos objetos y traemelos! ¡Date prisa!$B$BNitrin' WHERE `entry`='2983';
UPDATE `locales_page_text` SET `Text_loc7`='<Despues de una serie de paginas de aburrida \"ciencia\" que carecen de sentido alguno, hay una nota garabateada en la parte inferior de la ultima pagina....>$B$BPerdona, jefe. Parece que arrancara correr muy pronto la Tormenta Abisal. Es probable que esto alcance la mayor parte de Terrallende.$B$BNo estoy 100% seguro, pero creo que el problema tiene relaccion con lo que los Elfos de Sangre estan haciendo en las Forjas de Mana.$B$B¡Lo bueno es que estamos construyendo aquel cohete!$B$B-- Nicuproso$B$BP.D. ¿Cree que podria conseguir una plaza en el cohete? ¡Seguro que necesitara un Abisologo mientras viaja traves del Vacio Abisal!' WHERE `entry`='2984';
UPDATE `locales_page_text` SET `Text_loc7`='General,$B$BEstamos defendiendo nuestra posicion en los Peldanos del Destino, pero la Legion envia una oleada tras otra para asaltar nuestras filas. Deben de tener una zona de preparacion cercana que nuestros exploradores aun no han ubicado. Aunque las fuerzas de la Alianza estan de nuestra parte, me temo que la Legion, superior en numero, invadira nuestras defensas. Envie toda la ayuda que pueda, no fuera que el Portal cayera y nos quedaramos atrapados en este mundo de pesadilla para siempre.$B$BTeniente General Orion' WHERE `entry`='2985';
UPDATE `locales_page_text` SET `Text_loc7`='Cuando Nahni subio deslizandose, el experimentado guerrero la miro duramente. \"Supongo que estas aqui para cobrar la recompensa por matar a aquellos murlocs?\"$B$BSus ojos se desviaron a la deslumbrante colada que se cenia al costado del guerrero. \"Eso depende de cual sea la recompensa, Marcus.\" Se retorcio el pelo con coqueteria, fingiendo no advertir la forma incomoda en que se movia el guerrero en su presencia. \"Puede que no la quiera.\"' WHERE `entry`='2986';
UPDATE `locales_page_text` SET `Text_loc7`='Marcus dio un paso hacia ella, encrespandose con una mezcla de temor e ira. \"¡La recompensa no es negociable!\" Callo durante un momento mientras se recomponia y se apretujo contra su diminuto cuerpo. Sus labios se encontraron con vehemencia, fundiendo su hechizo de armadura gelida en un torrente de vapor sofocante.$B$B\"Entonces, ¿hay otro paso en esta mision?\", bromeo, mientras sus ojos brillaban de agitacion.$B$B<Esto sigue por varios cientos de paginas mas, sin adelantar el argumento.>' WHERE `entry`='2987';
UPDATE `locales_page_text` SET `Text_loc7`='Este extrano objeto parece ser algun tipo de dispositivo de ingenieria, aunque no se parece a nada que hayas visto antes.$B$BA juzgar por su apariencia, se diria que, sea lo que sea, ya se ha utilizado.$B$BY, a juzgar por la distribucion de los cadaveres que hay por aqui, se supone que, sea lo que sea, se utilizo con efectos mortiferos.' WHERE `entry`='2988';
UPDATE `locales_page_text` SET `Text_loc7`='Comandante,$B$BLa Cifra se debe entregar a Zuluhed. Permanecera bajo su custodia hasta nuevo aviso.$B$B-Lord Illidan Tempestira' WHERE `entry`='2989';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><H1 align=\"center\">LAMENTO DE LOS ALTONATO </H1><BR/><IMG src=\"Interface\FlavorImages\BloodElfLogo-small\"/></BODY></HTML>' WHERE `entry`='2990';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><P>Traduccion libre...</P><BR/><P>Por la luz, por la luz del sol,</P><P>Hijos de la sangre,</P><P>Nuestros enemigos se abren paso.</P><P>Hijos de la sangre,</P><P>Por la luz,</P><P>Debiles hijos de la sangre,</P><P>Se abren paso.</P><P>Oh, hijos de la sangre,</P><P>Por la luz del sol.</P><P>Debiles hijos de la sangre,</P><P>Se abren paso.</P><P>Oh, hijos de la sangre,</P><P>Por la luz del sol,</P><P>El sol.</P></BODY></HTML>' WHERE `entry`='2991';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><P>Version thalassiana...</P><BR/><P>Anar\'alah, anar\'alah belore,</P><P>Sin\'dorei,</P><P>Shindu fallah na.</P><P>Sin\'dorei,</P><P>Anar\'alah,</P><P>Shindu sin\'dorei,</P><P>Shindu fallah na.</P><P>Sin\'dorei,</P><P>Anar\'alah belore.</P><P>Shindu sin\'dorei,</P><P>Shindu fallah na.</P><P>Sin\'dorei,</P><P>Anar\'alah belore,</P><P>Belore.</P></BODY></HTML>' WHERE `entry`='2992';
UPDATE `locales_page_text` SET `Text_loc7`='Estimado canciller,$B$BLa tarea de llevar a los gigantes de Sombraluna al redil esta en tus habiles manos. Usa tu encanto de elfo de sangre para que vean las cosas a mi manera. Si eso no funciona, extermina hasta al ultimo de ellos...$B$BLord Illidan Tempestira' WHERE `entry`='2993';
UPDATE `locales_page_text` SET `Text_loc7`='En todo, los paladines deben reflejar la Luz, que complementa nuestra fuerza. Esforzarse para ser divino para uno de nuestra especie no significa que nos esforcemos por la bondad ? nos esforzamos para ser buenos en todas las acciones.' WHERE `entry`='2994';
UPDATE `locales_page_text` SET `Text_loc7`='Aunque se te llame para golpear al mal en estos duros tiempos, siempre debes recordar que es ayudar a los demas lo que te distinguira de los demas ciudadanos. Compasion, paciencia, valentia ? estas cosas significan tanto para un paladin como la fuerza en la batalla.$B$BAprendetelo bien y nunca lo olvides.' WHERE `entry`='2995';
UPDATE `locales_page_text` SET `Text_loc7`='LIBERAR CUMPLIMIENTO DE RESPONSABILIDAD.$B$BEL PILOTO DE PRUEBAS ES TOTALMENTE CONSCIENTE Y RECONOCE QUE HAY RIESGO DE LESIoN, MUTILACIoN O MUERTE EN FORMA DE: PULVERIZACIoN, ELECTROCUCIoN, TRAUMATISMO CONTUSO EN oRGANOS VITALES, CONSUMO POR FAUNA INDiGENA O PARADOJAS DEL TIEMPO/ESPACIO DURANTE EL USO DEL CEFIRIUM CAPACITORIUM (EL \"DISPOSITIVO\"). POR LA PRESENTE, EL PILOTO DE PRUEBAS ELIGE ENTRAR VOLUNTARIAMENTE EN EL DISPOSITIVO Y ASUMIR TODOS LOS RIESGOS ANTERIORMENTE MENCIONADOS.$B$BEL PILOTO DE PRUEBAS ACUERDA RENUNCIAR, LIBERAR Y DISPENSAR DE CUALQUIERA RECLAMACIoN POR LESIoN O DAnO CONTRA TALLY PILLAZAS Y SU FAMILIA, Y, EN CASO DE SU MUERTE, ACUERDA DONAR SUS RESTOS AL ESTADO DE PILLAZAS PARA SU ESTUDIO.$B$Bfirmado,$BX:___$N' WHERE `entry`='2996';
UPDATE `locales_page_text` SET `Text_loc7`='Saludos, erudito Antonivich:$B$BEspero que esta carta te halle en Thrallmar y que tus estudios sean de provecho. Me basta con mis artilugios en Rompeloma para mis propios experimentos, aunque a veces desearia estar en Entranas con herramientas y sirvientes...$B$BAh, los lamentos de un cadaver.$B$BCumplidos aparte, tengo noticias: la sangre que $N recogio de los orcos viles muestra una concentracion de contaminaciones demoniacas. Es casi identica a la de los orcos viles originarios de Azeroth... la de Mannoroth.' WHERE `entry`='2997';
UPDATE `locales_page_text` SET `Text_loc7`='$N,$B$BLa Clave de Condenacion es sin duda un conjuro poderoso. Antigua y caotica, la clave ha sido responsable de muchas tragedias en la historia de nuestros mundos. Es decir, no es algo unico de Draenor.$B$BEl simbolo ardiente era la marca de Kael\'Thas. Su conocimiento de las palabras antiguas explica mucho.$B$BTu descubrimiento ha despertado a los naaru. A\'dal ha solicitado tu presencia en la Ciudad de Shattrath. Si decides venir, buscame en el Bancal de la Luz.$B$BHumildemente tuyo,$B$BKhadgar' WHERE `entry`='2998';
UPDATE `locales_page_text` SET `Text_loc7`='Antonivich,$B$BEn primer lugar, dejame decirte que tu $gultimo:ultima; agente, $N, ha realizado sus obligaciones muy satisfactoriamente. He tomado posesion de su cuerpo y ahora escribo esto de su propia mano. Me produce tal diversion, que espero tener algun dia la fortuna de poseerte, ¡oh, poderoso boticario!$B$BBueno, el informe segun me lo has pedido: La Ciudadela del Fuego Infernal se ve frecuentada por un demonio mas fuerte que ninguno que haya conocido. Supongo que Mannoroth ? el senor del foso de fama de la Tercera Gran Guerra ? poseia la misma fuerza, pero pocos la tienen. Sin embargo, siento que hay un aire de confinamiento en este demonio. No me sorprenderia que actuara contra su voluntad, o quizas este encarcelado, o este bajo el dominio de un poder aun mayor.$B$BEl tiempo lo dira...' WHERE `entry`='2999';
UPDATE `locales_page_text` SET `Text_loc7`='El tiempo lo dira, pues tambien he notado un giro del destino en los muros de la Ciudadela del Fuego Infernal.$B$BVosotros, los inmigrantes de Azeroth ? ese horrible, verde y suntuoso lugar donde seres como yo hallan un infierno de Luz y vida ? vereis la cara de ese demonio. Y vereis que los males que hay en la Ciudadela del Fuego Infernal son solo la punta ? si me permites la metafora ? de un enorme iceberg.$B$B¡Que gloriosas batallas se avecinan! ¡Si pudiera estar alli para probar el dolor y el miedo que habra!$B$BBueno, acabare mi prosaica escritura, estimado herbolario: puede que tu cuerpo este muerto, Antonivich, pero tu alma sigue muy intacta, y yo la tendre y me dare un festin con ella con un deleite sublime el dia que bajes la guardia... ¡y encuentre el camino a tu interior!$B$BEsperare hasta el Cuarto Dia del Juicio si hace falta, pero... ¡seras mio!$B$BBuenas noches,$B-D' WHERE `entry`='3000';
UPDATE `locales_page_text` SET `Text_loc7`='<En esta pagina tan quemada no quedan palabras reconocibles.>' WHERE `entry`='3001';
UPDATE `locales_page_text` SET `Text_loc7`='Despues de que te marchases de Sotoeterno, continue mi estudio del Libro del Cuervo y he descubierto informacion que te ayudara a derrotar al dios Cuervo.$B$BAl liberar a estos espiritus, se han vinculado a ti, asi que espero que se ofrezcan a ayudarte en tu lucha contra el dios Cuervo.$B$BCuando desencadenes a los espiritus por primera vez, estaran inactivos, como lo han estado durante siglos. Usando tus hechizos de Sanacion en el tiempo, podras imbuirlos de energia, devolviendolos a la vida y logrando su ayuda en el combate. Cuando la magia se desvanezca, regresaran a su sueno.$B$BA continuacion tienes una recopilacion mis notas sobre cada espiritu pajaro:' WHERE `entry`='3002';
UPDATE `locales_page_text` SET `Text_loc7`='El espiritu del falcon$B$BEl espiritu del falcon esta imbuido con velocidad y agilidad increibles, muy reconocido entre los arakkoa por su gran habilidad para la caza.$B$BSi energizas este espiritu con un hechizo de Sanacion en el tiempo te prestara parte de su velocidad al invocar hechizos y en el combate cuerpo a cuerpo.' WHERE `entry`='3003';
UPDATE `locales_page_text` SET `Text_loc7`='El espiritu aguila$B$BLa tradicion arakkoa mantiene que el espiritu aguila no era tan veloz como su hermano el falcon, pero que poseia la mayor destreza en la caza. El espiritu aguila usara sus habilidades contra tus enemigos si usas un hechizo de Sanacion en el tiempo para despertarlo de su sueno.$B$B.' WHERE `entry`='3004';
UPDATE `locales_page_text` SET `Text_loc7`='El espiritu del halcon$B$BLa tablilla habla maravillas del halcon, como si en algun momento hubiese estado aliado con el Cuervo. Un antiguo simbolo arakkoa de venganza, el espiritu a menudo era invocado por victimas de actos malvados. Usar un hechizo de Sanacion en el tiempo para despertar al espiritu te permitira compartir su poder, danando a aquellos que te hagan dano.' WHERE `entry`='3005';
UPDATE `locales_page_text` SET `Text_loc7`='En el nombre de nuestro gran maestro... ¡Por la gloria de Illidan!$BMor\'ghor,$B$BTe envio a este desdichado como siervo de Illidan. Aunque que no tiene futuro como Caballero de la Muerte, quiza sus habilidades sean de tu agrado. Sometelo. Moldealo y conviertelo en un guerrero de los Illidari.$B$BY si no esta a la altura... si muestra sintomas de debilidad... desechalo. ¡A las fosas!$B$BLord Teron Sanguino' WHERE `entry`='3006';
UPDATE `locales_page_text` SET `Text_loc7`='No desesperes. Tu ogro especial esta ahi en alguna parte.' WHERE `entry`='3007';
UPDATE `locales_page_text` SET `Text_loc7`='Deja tu espada, tu maza y tu lanza. Los crsitales nos han dicho que le demos una oportunidad a la paz.' WHERE `entry`='3008';
UPDATE `locales_page_text` SET `Text_loc7`='¡No tienes temer con una cabeza llena de la cerveza!' WHERE `entry`='3009';
UPDATE `locales_page_text` SET `Text_loc7`='¡Dos cabezas son mejor que una! ¡Asi siempre tendras un amigo!' WHERE `entry`='3010';
UPDATE `locales_page_text` SET `Text_loc7`='La sabiduria llega de muchas formas, pero siempre fluye de los cristales.' WHERE `entry`='3011';
UPDATE `locales_page_text` SET `Text_loc7`='¡Piensa en positivo! Una panza grande para uno es un par de manijas del amor para otro.' WHERE `entry`='3012';
UPDATE `locales_page_text` SET `Text_loc7`='Manana encontraras a alguien especial, asi que recuerda: consigue mas amigos con bondad que peleas hasta la muerte.' WHERE `entry`='3013';
UPDATE `locales_page_text` SET `Text_loc7`='Numeros de la suerte: 13 9 17 4' WHERE `entry`='3014';
UPDATE `locales_page_text` SET `Text_loc7`='Tu pasion por el baile hace sonreir a todo el mundo.' WHERE `entry`='3015';
UPDATE `locales_page_text` SET `Text_loc7`='Eres especial, no te preocupes por lo que tus amigos, parientes, colegas, cualquier extrano, o tu conciencia te diga.' WHERE `entry`='3016';
UPDATE `locales_page_text` SET `Text_loc7`='Cuando todo falla, es momento de recapacitar.' WHERE `entry`='3017';
UPDATE `locales_page_text` SET `Text_loc7`='En estado de ebriedad me siento como una deidad.' WHERE `entry`='3018';
UPDATE `locales_page_text` SET `Text_loc7`='Aviso: Los despojos no son seguros para el consumo humano.' WHERE `entry`='3019';
UPDATE `locales_page_text` SET `Text_loc7`='¡Has sido Sgelegido:elegida; por la gran mano de metal de los cielos! Ahora manos a la obra.' WHERE `entry`='3020';
UPDATE `locales_page_text` SET `Text_loc7`='Deja dormir la gran mentira.' WHERE `entry`='3021';
UPDATE `locales_page_text` SET `Text_loc7`='El Comite de Explotacion de Ogri\'la te ha traido este mensaje. Hecho en Gadgetzan.' WHERE `entry`='3022';
UPDATE `locales_page_text` SET `Text_loc7`='El significado de la vida es ... (El resto de este mensaje se ha comido por accidente).' WHERE `entry`='3023';
UPDATE `locales_page_text` SET `Text_loc7`='Lo siento, no has tenido suerte esta vez, por favor intentalo de nuevo.' WHERE `entry`='3024';
UPDATE `locales_page_text` SET `Text_loc7`='Si haces un enemigo hoy, matalo rapidamente.' WHERE `entry`='3025';
UPDATE `locales_page_text` SET `Text_loc7`='Busca una meta mas alta en la vida. Asi evitaras a los seres inferiores.' WHERE `entry`='3027';
UPDATE `locales_page_text` SET `Text_loc7`='En nombre de nuestro gran maestro... ¡Por la gloria de Illidan!$B$BLos componentes comunes del canon de almas se consiguen en distintas profesiones. Los ingenieros y los mineros te podran abastecer de las barras de acero vil, el nucleo de poder de korio, y el marco de adamantita.$B$BPara la esencia arcana inmaculada, tendras que viajar al Bosque de Terokkar. En medio del bosque, sobre la avanzada de la Horda del Bastion Rompepedras, encontraras el hogar de Sar\'this, un arakkoa heretico. El area se conoce como Lago Jorune.$B$BHabla con Sar\'this y dile lo que buscas. Acuerdate de mencionar que vienes de mi parte, pues me debe un favor o dos.$B$BHaz lo que Sar\'this te diga y tendras la esencia.$B$BBalthas' WHERE `entry`='3028';
UPDATE `locales_page_text` SET `Text_loc7`='La ultima vez que estuve en las salas, el rey Garra Ikiss llevaba la punta de Terokk consigo como simbolo de su autoridad. Probablemente aun la lleve para convencer a sus seguidores de que es la reencarnacion de Terokk.$B$BMi hermano, Tejeoscuro Syth, llevaba la mascara de Terokk cuando dirigia los rituales Sethekk.$B$BGuardan la saga de Terokk en un circulo de runas en la gran camara abovedada fuera de la del propio rey Garra Ikiss.' WHERE `entry`='3029';
UPDATE `locales_page_text` SET `Text_loc7`='Es imposible que la influencia de Mannoroth llegue hasta Terrallende, la bestia lleva muerta anos, pero me temo que otro senor del foso sea el protagonista. Un senor del foso con el poder de Mannoroth... uno capaz de corromper a los orcos indigenas.$B$BContinuare con mis estudios de la sangre Foso Sangrante pero te insto a que sigas la pista. Y, me temo que te lleve a las profundidades de la Ciudadela del Fuego Infernal.$B$BUn saludo erudito,$BBoticario Albreck' WHERE `entry`='3030';
UPDATE `locales_page_text` SET `Text_loc7`='¡Escucha, Budd!$B$BEsta es la ultima vez que te mando algo hasta que me pagues todo lo que debes.$B$B¡No quiero mas excusas!$B$BGriftah' WHERE `entry`='3031';
UPDATE `locales_page_text` SET `Text_loc7`='Garn:$B$BNo tengo que recordarte la importancia de tu mision: proteger tu precioso cargamento a toda costa. Tal y como prometi, el exito y la discrecion se veran muy bien recompensados.$B$BHaz que tus \"socios\" mantengan el piquito cerrado, haz caso a la tripulacion y todo saldra bien. Cuento con que tendras exito.' WHERE `entry`='3032';
UPDATE `locales_page_text` SET `Text_loc7`='Te he enviado un ejercito de tamano considerable, anciano, pero cuidado cuando realices cualquier movimiento. Lo primero es detener el avance de la Alianza en nuestro territorio. Algunos de los humanos mas aventureros han comenzado a construir una gran edificacion en la frontera con Los Baldios.$B$BOcupate de que sepan que no son bienvenidos mas alla de las fronteras de su despreciable Theramore. Por ultimo, una advertencia, anciano: no informes de tus planes a los ogros de Murohelecho. Esos brutos son incapaces de guardar un minimo de discrecion.$B$BAgashem.' WHERE `entry`='3033';
UPDATE `locales_page_text` SET `Text_loc7`='Uno de los seres mas terrorificos que he visto en toda mi vida es Tethyr. Solo he visto una vez a esa bestia, ¡y me considero afortunado de haber salido vivo!$B$BEra una noche oscura y de tormenta. A traves de las agitadas olas del mar y de la lluvia torrencial, apenas se distinguia la luz del faro de Theramore. Hicimos todo lo posible para navegar hacia el. ¡De repente, esa bestia salio de entre las olas delante de nuestro barco!' WHERE `entry`='3034';
UPDATE `locales_page_text` SET `Text_loc7`='La terrorifica y enorme boca de esa bestia se abrio, lanzando agua helada a nuestra embarcacion. Aquellas terribles mandibulas detuvieron la proa de nuestro barco, arrancandola del casco. Los marineros y el cargamento se vieron violentamente precipitados al mar a medida que los restos del barco desaparecian entre las olas.$B$BLo ultimo que recuerdo es que estaba volando desde el casco roto, golpeandome contra las oscuras olas. El mundo se volvio oscuro y crei que ahi quedaria mi ultimo aliento. Cuando desperte en la enfermeria de Theramore, supe que alguien debia contar nuestra historia.$B' WHERE `entry`='3035';
UPDATE `locales_page_text` SET `Text_loc7`='La terrorifica y enorme boca de esa bestia se abrio, lanzando agua helada a nuestra embarcacion. Aquellas terribles mandibulas detuvieron la proa de nuestro barco, arrancandola del casco. Los marineros y el cargamento se vieron violentamente precipitados al mar a medida que los restos del barco desaparecian entre las olas.$B$BLo ultimo que recuerdo es que estaba volando desde el casco roto, golpeandome contra las oscuras olas. El mundo se volvio oscuro y crei que ahi quedaria mi ultimo aliento. Cuando desperte en la enfermeria de Theramore, supe que alguien debia contar nuestra historia.' WHERE `entry`='3036';
UPDATE `locales_page_text` SET `Text_loc7`='Hijos: Joel y Gina. Esposa: Suzannah' WHERE `entry`='3037';
UPDATE `locales_page_text` SET `Text_loc7`='No permanezcas ante mi tumba llorando,$Bno estoy alli, no estoy descansando.$B$BEstoy en los miles de vientos que los arboles mueven,$Ben Rasganorte sobre la brillante nieve.$B$BSoy la suave lluvia que cae gentilmente$Ben los campos de Paramos de Poniente.$B$BEstoy en la calma matutina,$Bde la selva de Tuercespina.$B$BEstoy en el redoble magistral,$Bde las pezunas en Nagrand al pisar.$B$BEn las calidas y brillantes estrellas me muevo,$Bmientras sonando sobre Darnassus me elevo.$B$BEstoy en los pajaros cantores$By en las cosas que inspiran pasiones.$B$BNo permanezcas ante mi tumba llorando,$Bno estoy alli, no estoy descansando.' WHERE `entry`='3039';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><P>Bien, $N, aqui tienes una lista de las cosas que voy a necesitar para nuestra nueva super secreta... eh... super arma. De momento, llamemosla \"Rayo reductor prototipico ultraavanzado de Ingeniosa\".</P><BR/><P>¡Hazme caso, va a ser mas revolucionario que esa antigua maquina para aumentar el tamano del mundo!</P><BR/><P>Da igual. No le des muchas vueltas de momento. ¡Tu traeme lo que te he pedido y nos haremos de oro!</P></BODY></HTML>' WHERE `entry`='3040';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><H1 align=\"center\">Mis herramientas</H1><BR/><P>Lo primero es lo primero... necesito mis herramientas. ¡Ningunas otras valdrian para un proyecto tan complicado como este!</P><BR/><P>Cuando tuvimos que correr para escapar de la Estacion de Bombeo y salvar la vida -esa es otra historia- no tuve tiempo de recogerlas. ¡Vamos a ver! ¡Es logico! Me iba la vida en ello. Lo que si recuerdo es que las deje bajo la Estacion de Bombeo, al este.</P></BODY></HTML>' WHERE `entry`='3041';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><H1 align=\"center\">Condensador sobrecargado</H1><BR/><P>Cualquier ingeniero que se precie deberia poder fabricarte unos cuantos en un periquete si tu no sabes. Que demonios, te los haria yo, pero ahora no tengo mis herramientas, ¿recuerdas?</P><BR/><P>Asi que haz lo imposible para traerme uno. Sin el, nuestro proyecto no va a llegar a ninguna parte. Si es necesario, ¡dirigete a los continentes del sur y busca en la casa de subastas!</P></BODY></HTML>' WHERE `entry`='3042';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><H1 align=\"center\">Un punado de polvo de Rocanar</H1><BR/><P>Esto ultimo puede parecer algo complicado, pero es necesario dar forma a la lente del rayo solo con el polvo mas fino. Asi que hay que usar el del mejor de los elementales.</P><BR/><P>Rocanar deberia valer. Lo han visto en algun lugar al sureste del Tramo Helado entre Unu\'pe y el Zigurat de los Lamentos. Ve y destroza su exterior helado para alcanzar su nucleo de roca y traeme un punado.</P></BODY></HTML>' WHERE `entry`='3043';
UPDATE `locales_page_text` SET `Text_loc7`='Primera guardia, siete campanas$BCedric hallado en el armario del ron de nuevo. Disciplinado.' WHERE `entry`='3044';
UPDATE `locales_page_text` SET `Text_loc7`='Primera guardia, ocho campanadas$BCambio de guardia. Todo en orden.' WHERE `entry`='3045';
UPDATE `locales_page_text` SET `Text_loc7`='Guardia intermedia, una campanada$BCedric hallado intentando acceder al mueble del vino del capitan. Disciplinado.' WHERE `entry`='3046';
UPDATE `locales_page_text` SET `Text_loc7`='Guardia intermedia, dos campanadas$BCedric trepa las jarcias, se enreda y cae. Atendido por el cirujano del barco. Enviado abajo a dormir la mona.' WHERE `entry`='3047';
UPDATE `locales_page_text` SET `Text_loc7`='Guardia intermedia, cuatro campanadas$BBotella de ron hallada en los pantalones de Cedric. Disciplinado.' WHERE `entry`='3049';
UPDATE `locales_page_text` SET `Text_loc7`='Guardia intermedia, cinco campanadas$BRopa de Cedric hallada. Cedric hallado en otro lado. Cedric disciplinado.' WHERE `entry`='3050';
UPDATE `locales_page_text` SET `Text_loc7`='Guardia intermedia, seis campanadas$BCedric canta a voz en grito. Despierta al capitan. El capitan disciplina a Cedric.' WHERE `entry`='3051';
UPDATE `locales_page_text` SET `Text_loc7`='Guardia intermedia, ocho campanadas$BCambio de guardia. Todo tranquilo. Dos hombres enviados a buscar a Cedric.' WHERE `entry`='3052';
UPDATE `locales_page_text` SET `Text_loc7`='Guardia de la manana, una campanada$BFuego en los aposentos del Capitan. Todos en pie. Lanzamos la polvora por la borda.' WHERE `entry`='3053';
UPDATE `locales_page_text` SET `Text_loc7`='Hace mucho tiempo que se cuenta la historia de El Cuerno de Furia Elemental alrededor de las fogatas de los campamentos de los taunka. Generaciones de guerreros han crecido escuchando estas historias y han buscado el escondite del cuerno en vano. Como los otros, el poderoso Pezuna Tempestuosa prometio buscar el cuerno y dejo atras su pueblo y a su familia.$B$BHaciendo frente con valentia al penetrante frio y a los feroces elementales de Pico Filoescarcha, Pezuna Tempestuosa ascendio la montana solo. Una vez en la cumbre, fue recibido por el propio Viento del Norte. Sabiendo que el viento no le entregaria el cuerno sin luchar, el valiente guerrero lo desafio.' WHERE `entry`='3054';
UPDATE `locales_page_text` SET `Text_loc7`='Durante cinco dias, Pezuna Tempestuosa lucho contra el viento. Al amanecer del sexto dia, desterro el viento de Filoescarcha y se hizo con el cuerno. Maltrecho y herido, Pezuna Tempestuosa inicio su viaje de vuelta hacia su poblado.$B$BEl Viento del Norte reunio gran cantidad de elementales inferiores y le ataco cuando casi habia alcanzado su destino. El guerrero debilitado no pudo defenderse del ataque y el Viento del Norte se vengo. Rompio el cuerno en dos, encerro cada piezas en un adorno lustroso y se las dio a dos poderosos cabecillas gorloc como prueba de su autoridad.$B$B<El texto original acaba de forma abrupta, pero alguien, quizas la Domadora del viento Barah parece haber hecho un apendice mas abajo.>$B$BTal y como les habia ordenado el Viento del Norte, los cabecillas gorloc pasaron estas preciosas reliquias a sus hijos y nietos. Hoy, las mitades del cuerno permanecen a los cabecillas Gorgogluglu y Borbogluglu.' WHERE `entry`='3055';
UPDATE `locales_page_text` SET `Text_loc7`='SE BUSCA: HEMET NESINGWARY$B$BPOR CRIMENES CONTRA LA NATURALEZA!$B$BInterpretacion del artista de \"El Extinguidor\" en su habitat natural: el fuego eterno del submundo.$B$BUbicacion del ultimo holocausto natural: Nagrand, Terrallende$B$BTambien conocido como: El Gran Cazador, El Carnicero de las Tierras Inhospitas, El Destripador de Tuercespina, El Viejo de la Muerte, Hemet, Nessie, Viejo Sucio, Viejo Sucio D, Padre de Recompensas, El Extinguidor.' WHERE `entry`='3057';
UPDATE `locales_page_text` SET `Text_loc7`='ct,t' WHERE `entry`='3058';
UPDATE `locales_page_text` SET `Text_loc7`='Con el exito de Kel\'Thuzad en Lordaeron, el Rey Exanime hizo los preparativos finales para su asalto a la civilizacion humana.Dejando su energia en una serie de artefactos portatiles llamados calderos de peste, Ner\'zhul ordeno a Kel\'Thuzad el transporte de los calderos a Lordaeron, donde serian ocultados en varios pueblos del culto controlados.' WHERE `entry`='3063';
UPDATE `locales_page_text` SET `Text_loc7`='Los calderos, protegidos por los miembros del culto leal, actuarian entonces como generadores de plaga, filtrando la plaga a traves de granjas fuera de sospecha y ciudades del norte de Lordaeron.' WHERE `entry`='3064';
UPDATE `locales_page_text` SET `Text_loc7`='Leales siervos de los senores elementales, HA LLEGADO NUESTRA HORA.$B$BHemos pasado demasiado tiempo en las sombras, ofreciendo nuestro tiempo, sirviendo a nuestros maestros, esperando el final que ya conociamos. ¡Ahora que las llamas de aplacamiento de Ragnaros iluminan la noche, tenemos en nuestro poder las herramientas para instigar a una guerra y un caos de proporciones catastroficas!$B$BEl Senor del Fuego esta encarcelado en nuestro mundo. No tiene todo su poder, aunque su fuerza sigue siendo formidable. Con un oponente igual de formidable en este reino, el enfrentamiento resultaria en la gran guerra elemental que traera el fin que tanto hemos esperado.' WHERE `entry`='3065';
UPDATE `locales_page_text` SET `Text_loc7`='Hay un gran senor de la Escarcha al servicio de Neptulon conocido como Ahune. En estos mismos momentos reune sus fuerzas para hacerle la guerra a Ragnaros. Todo lo que necesita es una entrada a nuestro mundo; una entrada que nosotros proporcionaremos. Contamos con los aliados necesarios. Tenemos el lugar. Tenemos la fuerza y la voluntad.$B$BLas negociaciones finales con nuestros nuevos y leales aliados e invitados tendra lugar en un lugar seguro al noroeste de nuestra primera situacion en Vallefresno.$B$BAntes de que este \"festival\" de masas ignorantes llegue a su fin, Ahune se enfrentara a Ragnaros en la sombra de Roca Negra. El mundo se estremecera con la liberacion de estas fuerzas.' WHERE `entry`='3066';
UPDATE `locales_page_text` SET `Text_loc7`='Garomosh,$B$BTe envio a un representante de El Etereum. El embajador Duyheen me cuenta que hay mas como el que se unirian a la causa. Tantea el terreno e intenta encontrar un uso para el.$B$BSi resulta de ayuda, quiza considere aceptar a mas de los suyos.$B$B¡No seas idiota, quema esta carta en cuanto la hayas leido!' WHERE `entry`='3067';
UPDATE `locales_page_text` SET `Text_loc7`='¡Lok\'tar ogar! Victoria o muerte: estas son las palabras que me unen a la Horda. Ya que es la verdad mas fundamental y sagrada de cualquier guerrero de la Horda.$B$BEntrego mi carne y sangre libremente al Jefe de Guerra. Soy el instrumento del deseo de mi Jefe de Guerra. Soy un arma del mando de mi Jefe de Guerra.$B$BA partir de este momento y hasta el final de mis dias y el momento de mi muerte: ¡Por la Horda!' WHERE `entry`='3068';
UPDATE `locales_page_text` SET `Text_loc7`='$N,$B$BSi estas leyendo esta carta significa que sigues con vida y de una pieza: o al menos aun tienes tus ojos.$B$BDebo disculparme por el secretismo. Agmar exige que se lea todo el correo antes de su entrega: hay demasiados traidores y ladrones aqui como bien indica el cartel de Se Busca. Hay temas delicados que la nueva guardia podria malinterpretar con facilidad.$B$BPara un soldado de la Horda, perder es absoluto. Perder significa la muerte y no hay negociacion o interpretacion posible con la muerte. Solo puedes esperar que su muerte fuese honorable.' WHERE `entry`='3069';
UPDATE `locales_page_text` SET `Text_loc7`='Pero la victoria... La victoria puede significar muchas cosas. Como seguramente has notado, los Kor\'kron tienen la fuerza de su lado. El Jefe de Guerra ha enviado a su guardia de elite a asegurarse de que se hacen con la victoria en Rasganorte. Estos, junto contigo y otros heroes, estan empujando al Rey Exanime y sus fuerzas hacia el inevitable final. Con cada reto que superas, estamos un paso mas cerca de limpiar nuestro mundo de Arthas y de la Plaga.$B$BHe ahi el dilema. Ya que, veras, nuestras fuerzas en Rasganorte trabajan bajo los auspicios del joven Grito Infernal. Cada victoria refuerza la moral de las fuerzas de la Horda alli y se contagia por todo Azeroth.' WHERE `entry`='3070';
UPDATE `locales_page_text` SET `Text_loc7`='Es poco afortunado, entonces, que Grito Infernal use unas tacticas tan salvajes. A medida que la victoria se aproxima, Grito Infernal gana mas justificacion para sus metodos, lo cual nos acerca a la vez a un lugar al que no hemos ido en muchos anos: un lugar oscuro.$B$BHe enviado a mi hijo a dirigir nuestras fuerzas a la Puerta de Colera. Se que luchara con honor y espero que las fuerzas se den cuenta de su coraje y tenacidad y los imiten. Esta en mi corazon y en mi fuerza en un lugar donde no puedo estar... Seras mis ojos y mis oidos. Juntos, lo lograremos.$B$BSangre y rayos... Que nuestra llegada cause ambos.$B$BColmillosauro' WHERE `entry`='3071';
UPDATE `locales_page_text` SET `Text_loc7`='Todos nuestros esfuerzos daran su fruto. Nuestros maestros se levantaran el uno contra el otro en una gloriosa batalla que partira Azeroth en dos.$B$BEstamos viviendo el final de los tiempos, hermanos. No descuideis vuestras tareas. Aguantad. ¡Pronto nos alzaremos triunfantes!$B' WHERE `entry`='3072';
UPDATE `locales_page_text` SET `Text_loc7`='Mi reina,$B$BHemos descubierto que el Vuelo Azul habia liberado la interseccion de lineas Ley situada en los Jardines Reposo Lunar. Sus fuerzas usaron una aguja de flujo para destruir la piedra que la tapaba, pero en vez de guiar el poder de vuelta a El Nexo, usaron una serie de focos para transportarlo con torpeza y aumentar el poder bajo el suelo del Santuario de Dragones Azur.$B$BHuelga decir que esto ha tenido consecuencias tremendas alli donde la magia se ha filtrado a traves de la tierra. Los Ancestros del bosque, los Kalu\'ak, y otras criaturas de la zona se han visto afectadas negativamente. Por suerte, $N, $gel:la; $r que se encuentra ante ti, desempeno un papel decisivo en su investigacion y control.$B$BEn mi opinion, $N podria ser de gran ayuda en la lucha contra los problemas que afectan al Reposo del Dragon. Te pido humildemente que consideres valerte de su ayuda; a mi me resulto de incalculable valor.$B$BSiempre con respeto,$B$BModera$BArchimaga y miembro de Los Seis' WHERE `entry`='3073';
UPDATE `locales_page_text` SET `Text_loc7`='Mi reina,$B$BHemos descubierto que el Vuelo Azul habia liberado la interseccion de lineas Ley situada en los Jardines Reposo. Sus fuerzas usaron una aguja de flujo para destruir la piedra que la tapaba, pero en vez de guiar el poder de vuelta a El Nexo, usaron una serie de focos para transportarlo con torpeza y aumentar el poder bajo el suelo del Santuario de Dragones Azur.$B$BHuelga decir que esto tiene consecuencias tremendas cada vez que la magia se filtra a traves de la tierra. Los Ancestros del bosque, los Kalu\'ak, y otras criaturas de la zona se han visto afectadas negativamente. $N, $gel:la; $r que se encuentra ante ti, jugo un papel decisivo en su investigacion y control.$B$BEn mi opinion, $N podria ser de gran ayuda en la lucha contra los problemas que afectan al Reposo del Dragon. Te pido humildemente que consideres valerte de su ayuda, que a mi me resulto bastante util.$B$BSiempre con respeto,$B$BAethas Atracasol$BArchimago y miembro de Los Seis' WHERE `entry`='3074';
UPDATE `locales_page_text` SET `Text_loc7`='¡Hola!$B$BNo se me da muy bien escribir cartas, asi que la supervisora me ha ayudado a escribirte esta. Solo queria darte las GRACIAS porque eres $gun heroe:una heroina; y quiza algun dia yo tambien pueda salvar a gente como tu.$B$BBueno, ahora me tengo que ir a jugar, asi que ¡GRACIAS DE NUEVO por ser tan $gbueno:buena; y fuerte, ademas de super $gguapo:guapa;!$B$B--Tu admirador secreto' WHERE `entry`='3075';
UPDATE `locales_page_text` SET `Text_loc7`='<En esta pagina tan quemada no quedan palabras reconocibles.>' WHERE `entry`='3076';
UPDATE `locales_page_text` SET `Text_loc7`='Ah\'tusa repto en silencio a traves del gigantesco tunel subterraneo, mirando inquieta por encima de su hombro por si venia el veloz tranvia. \"¿Donde esta...?\" farfullo enfadada, deteniendose repentinamente cuando algo se movio detras de ella. Sus ojos se entrecerraron peligrosamente mientras su daga se deslizaba fuera de su funda.$B$BUna voz ronca resono en la camara vacia, \"Creia que a los picaros les gustaba estar detras de su objetivo.\" Se dio la vuelta rapidamente, dirigiendo su hoja contra un impenetrable escudo de fuerza. \"Y yo creia que los paladines eran castos\", respondio con una mueca de confianza.' WHERE `entry`='3077';
UPDATE `locales_page_text` SET `Text_loc7`='Inclinandose hacia delante, conto en voz alta hasta que la burbuja invisible desaparecio con un ruidito audible. \"¿Doce segundos? Se que tienes habilidades que duran mas.\"$B$BUnos dedos huesudos trabajaban sin esfuerzo bajo su armadura, desatando su peto y dejandolo expuesto al frio aire. \"No te preocupes, mi dulce Marcus, esta noche no vamos a acabar... solo estoy empezando.\" el rodeo con sus brazos lo que quedaba de su cintura, obteniendo una carcajada de excitacion al mordisquear las vertebras de su cuello.$B$B<El resto del libro tiene varias secciones que estan muy desgastadas por el uso.>' WHERE `entry`='3078';
UPDATE `locales_page_text` SET `Text_loc7`='El diario de la alta general Abbendis' WHERE `entry`='3079';
UPDATE `locales_page_text` SET `Text_loc7`='La voz susurro, \"Ven a mi\". Desde el principio sabia que era la Luz Sagrada que me hablaba en suenos. ¡Por fin! Tras tantos anos de oraciones y buenas acciones, llega la limpieza de la plaga de no-muertos de la faz de Azeroth. Tras todos los errores y las resurrecciones.$B$B¡Por fin!' WHERE `entry`='3080';
UPDATE `locales_page_text` SET `Text_loc7`='Ha vuelto a ocurrir. \"Ven a mi...\", ordeno la Luz.$B$BMe levante con escalofrios a pesar de que no hacia frio en mis aposentos. ¡Voy a redoblar mis esfuerzos! Manana le dire al sumo abad que quiero mas oraciones. ¡Debo tomar todas las medidas posibles!$B$BLa Luz Sagrada ha tomado nota de nuestra buena labor. ¡Puedo sentirlo!' WHERE `entry`='3081';
UPDATE `locales_page_text` SET `Text_loc7`='¡Esta vez no estaba sonando! Era muy real, durante mas de un minuto, en mitad del dia calido y soleado, me salia un aliento de vaho y frio. Uno de los sacerdotes se dio cuenta y se arrodillo para rezar.$B$BSin embargo, nadie mas oyo la voz. Al menos, este testigo demuestra que no me estoy volviendo loca. Quizas podria pedirle a Landgren que rece por ello.$B$BManana hare que Jordan y Calle tengan en cuenta hasta el minimo detalle a la hora de reclutar. Tenemos un monton de infieles que solo desean acabar con los no-muertos. ¡Eso no basta!' WHERE `entry`='3082';
UPDATE `locales_page_text` SET `Text_loc7`='Tabla de contenidos$B$BCapitulo 1 ? Los muertos y tu$B$BCapitulo 2 ? Mantener una figura de necrofago: siete ejercicios sencillos$B$BCapitulo 3 ? No dejar que entre el frio: elegir un ataud con aislamiento$B$BCapitulo 4 ? Garantias del cementerio: 50 senales inequivocas de amor$B$B<El libro esta en perfectas condiciones y parece que nadie lo ha leido>' WHERE `entry`='3083';
UPDATE `locales_page_text` SET `Text_loc7`='Dejare aqui a la mayor parte de la Cruzada para que continue realizando operaciones contra los no-muertos en nuestro patio trasero. Me imagino que cuando acabemos lo que hemos empezado, la mayoria volveran a sus hogares para vivir en paz.$B$BEso suena bien. Siempre hemos sido la sal de la tierra, alzandonos para recuperar nuestros hogares de manos de la corrupcion de la Plaga, para devolverle a Lordaeron su antigua gloria. Para volver a una epoca anterior a la Plaga, anterior a Arthas y al regicidio... anterior al Rey Exanime.' WHERE `entry`='3084';
UPDATE `locales_page_text` SET `Text_loc7`='Entre los hombres corre el rumor de que pronto llegara el dia en que todo cambiara para la Cruzada Escarlata. El obispo Calle le ha puesto un nombre: lo llama el Alba Carmesi.$B$BReflexionare acerca de esto, aunque en mis huesos puedo sentir su verdad. Rezo para que nos traiga mas alegrias que penas.' WHERE `entry`='3085';
UPDATE `locales_page_text` SET `Text_loc7`='La Luz ha vuelto a hablar con gran urgencia. Sali del sueno con una sensacion de impaciencia. No defraudare. No debe haber mas retrasos. ¡Debemos ponernos en marcha!$B$BQue gran coincidencia (tuve que controlarme para no mostrar signos de alivio): esta tarde el capitan Shely nos procuro un buen numero de barcos para el viaje. O quizas no fuera una coincidencia... La Luz Sagrada expresa su voluntad de maneras que no somos capaces de comprender.$B$BTomare la Locura del Pecador como bandera. Creo que su nombre es mas que adecuado.' WHERE `entry`='3086';
UPDATE `locales_page_text` SET `Text_loc7`='Ahora se por que la Luz me ha instado a ponerme en camino. A altas horas de la noche aparecio en el aire sobre nosotros una necropolis de la Plaga ¡y de ella empezaron a manar los esbirros del infierno!$B$BUna nueva raza de caballeros de la Muerte lidera el asalto. El numero de muertos ya es catastrofico. Con una Plaga capaz de atacarnos a su voluntad desde arriba y en cualquier lugar parece imposible encontrar un modo apropiado de defensa.$B$BMe temo que nuestros planes de expedicion a Rasganorte han terminado incluso antes de empezar.' WHERE `entry`='3087';
UPDATE `locales_page_text` SET `Text_loc7`='Me han informado de que Vega del Amparo y sus aledanos ya han comenzado a reunir a la multitud. El propio alto comandante Galvar Purasangre pretende liderarlos para salvarnos. Sus esfuerzos seran en vano.$B$BTengo que asegurarme de que mis mejores mensajeros atraviesen las filas enemigas y le avisen de que no lo haga. Vega del Amparo debe prepararse para atrincherarse y reunir al resto de la Cruzada.$B$BCon algo de suerte, llegaran alli antes de que anochezca.' WHERE `entry`='3088';
UPDATE `locales_page_text` SET `Text_loc7`='No hemos tenido noticias de ninguno de mis mensajeros esta manana. Esta claro que ninguno consiguio llegar a Vega del Amparo. Las Tierras de la Peste estan perdidas. Purasangre llegara con su ejercito y los aniquilaran alli mismo.$B$BEsta tarde he tenido una vision de la Luz. Vi que destruian todo lo que hemos construido aqui. El mensaje era claro: me decia que me llevara a los supervivientes mas fieles y abandonase a la Cruzada a su suerte.$B$BMas tarde, Landgren me dijo que el habia tenido la misma vision. No llego a comprender la razon por la que la Luz nos habra hecho emprender una accion tan deshonrosa, pero no tengo el derecho de preguntar... Estoy aqui para obedecer y obedecere.' WHERE `entry`='3089';
UPDATE `locales_page_text` SET `Text_loc7`='Observe, temblando, Nuevo Avalon, sospecho que por ultima vez. Nuestro destino esta en Rasganorte. Por alguna razon me invaden malos presentimientos. La mision frente a mi debiera liberarme de estas preocupaciones. Las borrare de mi mente.$B$BPuede que, si hay suerte, el alto comandante Purasangre, de algun modo, soporte y prepare a los supervivientes. ¡Yo soy cobarde, un perro que huye con el rabo entre las piernas!' WHERE `entry`='3090';
UPDATE `locales_page_text` SET `Text_loc7`='Me dicen que el viaje va a durar dos meses. Los otros barcos no pueden alcanzar la velocidad de la Locura. Llevan a bordo la mayoria de nuestras fuerzas y equipamiento y no son mucho mas que simples cargueros a vela, pero llegaran a su destino.$B$BNo es que me apetezca mucho el viaje, pero por la Luz aguantare los mareos. Solo debo evitar que el resto se de cuenta.' WHERE `entry`='3091';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">El diario de la alta general Abbendis</H1><BR/><IMG src=\"Interface\FlavorImages\ScarletCrusadeLogo\"/></BODY></HTML>' WHERE `entry`='3092';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>La voz susurro, \"Ven a mi\". Desde el principio sabia que era la Luz Sagrada que me hablaba en suenos. ¡Por fin! Tras tantos anos de oraciones y buenas acciones, llega la limpieza de la plaga de no-muertos de la faz de Azeroth. Tras todos los errores y las resurrecciones.</P><BR/><P>¡Por fin!</P></BODY></HTML>' WHERE `entry`='3093';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Ha vuelto a ocurrir. \"Ven a mi...\", ordeno la Luz.</P><BR/><P>Me desperte helada, a pesar de que no hacia frio en mis aposentos. ¡Voy a redoblar mis esfuerzos! Manana le dire al sumo abad que quiero mas oraciones. ¡Nada de quedarse a medias!</P><BR/><P>La Luz Sagrada ha tomado nota de nuestra buena labor. ¡Puedo sentirlo!</P></BODY></HTML>' WHERE `entry`='3094';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>¡Esta vez no estaba sonando! Era muy real, durante mas de un minuto, en mitad del dia calido y soleado, me salia un aliento de vaho y frio. Uno de los sacerdotes se dio cuenta y se arrodillo para rezar.</P><BR/><P>Sin embargo, nadie mas oyo la voz. Al menos, este testigo demuestra que no me estoy volviendo loca. Quizas podria pedirle a Landgren que rece por ello.</P><BR/><P>Manana hare que Jordan y Calle tengan en cuenta hasta el minimo detalle a la hora de reclutar. Tenemos un monton de infieles que solo desean acabar con los no-muertos. ¡Eso no basta!</P></BODY></HTML>' WHERE `entry`='3095';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Tanto el comandante como el obispo se mostraron receptivos, aunque tampoco les quedaba otro remedio. El obispo Calle en particular parecia muy entusiasmado. Hablo de una nueva cruzada y juro que descubriria a los debiles de fe que se ocultaban entre nuestras lineas.</P><BR/><P>Le dije que se lo tomase con calma. La idea no es destrozar la Cruzada. No obstante, me gusta la idea de formar un cuadro de elite con los mas leales para llevar a cabo la voluntad de la Luz en Rasganorte. Temo que su amistad con LeCraft le esta corrompiendo poco a poco. Aunque ambos tienen sus ideas.</P></BODY></HTML>' WHERE `entry`='3096';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Dejare aqui a la mayor parte de la Cruzada para que continue realizando operaciones contra los no-muertos en nuestro patio trasero. Me imagino que cuando acabemos lo que hemos empezado, la mayoria volveran a sus hogares para vivir en paz.</P><BR/><P>Eso suena bien. Siempre hemos sido la sal de la tierra, alzandonos para recuperar nuestros hogares de manos de la corrupcion de la Plaga, para devolverle a Lordaeron su antigua gloria. Para volver a una epoca anterior a la Plaga, anterior a Arthas y al regicidio... anterior al Rey Exanime.</P></BODY></HTML>' WHERE `entry`='3097';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Entre los hombres corre el rumor de que pronto llegara el dia en que todo cambiara para la Cruzada Escarlata. El obispo Calle le ha puesto un nombre: lo llama el Alba Carmesi.</P><BR/><P>Reflexionare acerca de esto, aunque en mis huesos puedo sentir su verdad. Rezo para que nos traiga mas alegrias que penas.</P></BODY></HTML>' WHERE `entry`='3098';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>La Luz ha vuelto a hablar con gran urgencia. Sali del sueno con una sensacion de impaciencia. No defraudare. No debe haber mas retrasos. ¡Debemos ponernos en marcha!</P><BR/><P>Que gran coincidencia (tuve que controlarme para no mostrar signos de alivio): esta tarde el capitan Shely nos procuro un buen numero de barcos para el viaje. O quizas no fuera una coincidencia... La Luz Sagrada expresa su voluntad de maneras que no somos capaces de comprender.</P><BR/><P>Tomare la Locura del Pecador como bandera. Creo que su nombre es mas que adecuado.</P></BODY></HTML>' WHERE `entry`='3099';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Ahora se por que la Luz me ha instado a ponerme en camino. A altas horas de la noche aparecio en el aire sobre nosotros una necropolis de la Plaga ¡y de ella empezaron a manar los esbirros del infierno! </P><BR/><P>Una nueva raza de caballeros de la Muerte lidera el asalto. El numero de muertos ya es catastrofico. Con una Plaga capaz de atacarnos a su voluntad desde arriba y en cualquier lugar parece imposible encontrar un modo apropiado de defensa.</P><BR/><P>Me temo que nuestros planes de expedicion a Rasganorte han terminado incluso antes de empezar.</P></BODY></HTML>' WHERE `entry`='3100';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Me han informado de que Vega del Amparo y sus aledanos ya han comenzado a reunir a la multitud. El propio alto comandante Galvar Purasangre pretende liderarlos para salvarnos. Sus esfuerzos seran en vano.</P><BR/><P>Tengo que asegurarme de que mis mejores mensajeros atraviesen las filas enemigas y le avisen de que no lo haga. Vega del Amparo debe prepararse para atrincherarse y reunir al resto de la Cruzada.</P><BR/><P>Con algo de suerte, llegaran alli antes de que anochezca.</P></BODY></HTML>' WHERE `entry`='3101';
UPDATE `locales_page_text` SET `Text_loc7`='En honor al Rey Varian Wrynn$B$Bconocido como LO\'GOSH el \"Lobo fantasmal\" para algunos$B$BSu milagroso retorno tras largos anos de privaciones en el exilio ha encendido una nueva llama en el espiritu y coraje de la Alianza.$B$BMarcharemos con atrevimiento en estas tierras peligrosas abrazando su valor y tenacidad en nuestros corazones.$B$B- Alto senor Bolvar Fordragon' WHERE `entry`='3102';
UPDATE `locales_page_text` SET `Text_loc7`='Dia 1: La basura humana parece esta completamente feliz ya que no ha notado mi presencia, tal y como estaba planeado. Estan listos para el bombardeo y solo es cuestion de tiempo que registre su posicion.$B$BDia 2: Registro completado: una tarea trivial para un tipo tan preparado como yo. No entiendo por que me ha tocado esta tarea: deberian habersela asignado a J-Y C. Ese ridiculo acento que tiene me parece justificarlo. $B$BDia 3: El aire de la primera bocanada se esta viciando. Quiza deberia salir a la superficie un momento para tomar otra bocanada de aire en unos dias. Aun me aburro hasta llorar: ¿cuando llegara la maldita senal?$B$B< Sigue echando pestes... >' WHERE `entry`='3103';
UPDATE `locales_page_text` SET `Text_loc7`='Dia 12: Ah, que alivio. La segunda bocanada de aire me ha sabido a gloria. Para mi sorpresa, aun no he recibido la senal y juraria que uno de los cangrejos intenta tamborilear con mi dedo gordo del pie. $B$B< Se queja de otras cosas... >' WHERE `entry`='3104';
UPDATE `locales_page_text` SET `Text_loc7`='Dia 36: Al final se me ha acabado la tercera bocanada de aire y las venas de mi codo izquierdo se ha vuelto a llenar de burbujas mientras subia. Ahora el tamborileo de los cangrejos es una compania constante, una percusion de fondo en mi pequeno rincon del infierno particular. Esta claro, esos desdichados se han olvidado de mi. Si J-Y estuviese aqui abajo, ¡seguro que no se habrian olvidado de el! ¡Nunca dejarian a Hans o a Sylvia atras! ¿Por que a mi? $B$B< Las entradas terminan siendo garabatos descorazonados... >' WHERE `entry`='3105';
UPDATE `locales_page_text` SET `Text_loc7`='1. Si no te gusta la potencia de un veneno, duplica los ingredientes o reducelos a la mitad.$B2. Si un veneno no funciona de la forma que deseabas, es porque estas mas equivocado de lo que creias estar.$B3. Prototipo, Prototipo, Prototipo (los gnomos hacen grandes temas de prueba).$B4. Los venenos deben ser rapidos en empezar a tener efecto y dificiles de detenerlos.$B5. Simples ingredientes trabajan juntos para crear una interesante complejidad.' WHERE `entry`='3106';
UPDATE `locales_page_text` SET `Text_loc7`='Al principio la escritura es pollo ininteligible, pero puedes distinguir referencias en color rosa..' WHERE `entry`='3107';
UPDATE `locales_page_text` SET `Text_loc7`='Mi padre me ha vuelto a enviar a cuidar a la vieja Bess. Creo que le gusta ver como me ataca. No es culpa mia si no le gusto como los otros... Solo porque una vez me comi una manzana que era para ella. Aun no se ni como se entero de eso.' WHERE `entry`='3109';
UPDATE `locales_page_text` SET `Text_loc7`='A la alta general Abbendis$BNuevo Avalon, Tierras Escarlata$B$BAlta general Abbendis:$B$BEspero que, cuando reciba esta carta, goce de buena salud. Me llamo Dansel Adams, cazador de profesion y cruzado Escarlata de sangre. Le escribo esta carta para informarle de que he presenciado un milagro.' WHERE `entry`='3110';
UPDATE `locales_page_text` SET `Text_loc7`='Durante una practica de purga de paganos en el Bosque de la Peste, mi grupo y yo nos vimos interrumpidos por un chirrido ensordecedor que provenia del cielo. Cuando miramos hacia arriba hacia Naxxramas, nos quedamos con la boca hasta el suelo. Asi se lo digo, Alta General, ¡fue una vision inverosimil! La terrorifica ciudadela se movia. Al principio, despacio, y luego con un rugido tormentoso... ¡paf! ¡Desaparecio!$B$BEso es, Alta general, ¡Naxxramas ha desaparecido! ¡Au revoir! ¿No podria ser sino un milagro de la Luz? ¡Yo digo que no!' WHERE `entry`='3111';
UPDATE `locales_page_text` SET `Text_loc7`='Que asi sea, que los impuros y sucios barbaros sean juzgados y sentenciados los siguientes. ¡Bendita sea la Luz!$B$BPor favor, Alta General, permitid que nuestros hermanos y hermanas conozcan este milagro.$B$BQue la Luz os guarde por toda la eternidad para que podais extender la buena nueva hasta el fin de los tiempos.$B$BRespetuosamente,$B$BD.Adams$B\"El cierre\"' WHERE `entry`='3112';
UPDATE `locales_page_text` SET `Text_loc7`='1. Dejarse crecer una barba bien desalinada, pues disimula las imperfecciones faciales de cara a la Luz. $B$B2. Purificarse las manos en leche de yegua adulta para que esten suaves y flexibles en el momento de llevar la ofrenda. $B$B3. Banarse en los rios, permitiendo que los gloriosos dones naturales de la Luz limpien la piel.' WHERE `entry`='3113';
UPDATE `locales_page_text` SET `Text_loc7`='4. Cambiarse con regularidad los habitos sagrados, para que la apariencia y los olores no ofendan a los que sirven a la Luz. $B$B5. Mantener en todo momento una postura erguida, para no cansar ni curvar la columna con posturas descuidadas. $B$B<La lista se extiende a varias paginas mas de menudencias>' WHERE `entry`='3114';
UPDATE `locales_page_text` SET `Text_loc7`='Desde el despacho del sumo abad Landgren:$BPuede parecer que en tiempos dificiles de conflicto y confusion uno debe preocuparse por si mismo, protegiendo a su familia y seres queridos, excluyendo todo lo demas. Pero ese es un sentimiento falso... Sin la luz de esperanza que constituye la Iglesia, la desesperacion podria inundar rapidamente hasta el ultimo poro de la esencia de tu familia. Todos los estomagos llenos, pies calientes y sonrisas de felicidad que los bienes mundanos pueden comprar no salvaran a tu familia del vacio sigiloso que provocara el incumplimiento de tu responsabilidad hacia la Luz.' WHERE `entry`='3115';
UPDATE `locales_page_text` SET `Text_loc7`='Da a la Iglesia. Las penurias soportadas durante la falta de bienes mundanos actuan como forja para el alma, quemando las impurezas y elevando a tu ser a otro plano. No te avergüences por los rugidos de tu estomago hambriento, por el mordisco de una piedra punzante sobre tu pie desnudo o por el frio del viento invernal. Estos no son sino medios por los que la Luz comunica su inspiracion al alma. Estos sacrificios aseguran que las alabanzas hechas a la Luz que tanto inspiran al culto se mantengan adecuadamente. $B$BAbstente de los placeres de la carne, y acercate mas a la Luz.' WHERE `entry`='3116';
UPDATE `locales_page_text` SET `Text_loc7`='El tema de la indulgencia es algo peliagudo, pero hay veces en la vida en que se convierte en un mal necesario. Una falta de juicio, una palabra cruel pronunciada sin reflexion, un punetazo o patada dados con rabia... Todas estas cosas son defectos del hombre mortal, y la Iglesia debe ser consciente de ello. $B$BSin embargo, tal consciencia requiere un precio. Para pecados de palabras crueles, un mero punado de plata limpiara el alma. Para pecados de fuerza fisica, un punado de oro expurgara los defectos de la carne mortal. Otro tipo de pecados requeriran una confesion completa y una contribucion algo mas generosa.' WHERE `entry`='3117';
UPDATE `locales_page_text` SET `Text_loc7`='Golpear: $B Tortazo : 2 monedas de oro$B Golpe bajo: 3 monedas de oro$B Golpe en el pecho: 1 moneda de oro y 45 monedas de plata$B $B<Continua una larga lista detallada de precios y otros pecados>' WHERE `entry`='3118';
UPDATE `locales_page_text` SET `Text_loc7`='Purificar el cuerpo con esmero. Purificar el alma con oracion. Purificar a tus enemigos con fuego. El dolor no es un enemigo, sino un aliado que abrazar, tener cerca y otorgar tanto a los incredulos como a los creyentes. $B$BLa resistencia a los caminos de la Luz es un signo de impureza, y debe extirparse de la carne de la comunidad.' WHERE `entry`='3119';
UPDATE `locales_page_text` SET `Text_loc7`='Alta General:$B$BLos ejercitos de Vega del Amparo y Tirisfal estan a menos de un dia de viaje de Nuevo Avalon. Cabalgamos con la Luz brillando sobre nuestra espalda y con el viento bajo nuestros talones. Pronto, ¡la Plaga debera lidiar con el poder pleno de la Cruzada Escarlata!$B$BQue la Luz le bendiga.$B$BAlto comandante Galvar Purasangre' WHERE `entry`='3120';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><H1 align=\"center\">El Camino de la Redencion</H1><BR/><IMG src=\"Interface\FlavorImages\ScarletCrusadeLogo\"/></BODY></HTML>' WHERE `entry`='3121';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><P>La voz susurro, \"Ven a mi\". Desde el principio sabia que era la Luz Sagrada que me hablaba en suenos. ¡Por fin! Tras tantos anos de oraciones y buenas acciones, llega la limpieza de la plaga de no-muertos de la faz de Azeroth. Tras todos los errores y las resurrecciones.</P><BR/><P>¡Por fin!</P></BODY></HTML>' WHERE `entry`='3122';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><P>Ha vuelto a ocurrir. \"Ven a mi...\", ordeno la Luz.</P><BR/><P>Esta vez me levante con escalofrios a pesar de que no hacia frio en mis aposentos. ¡Voy a redoblar mis esfuerzos! Manana le dire al sumo abad que quiero mas oraciones. ¡Debo tomar todas las medidas posibles!</P><BR/><P>La Luz Sagrada ha tomado nota de nuestra buena labor. ¡Puedo sentirlo!</P></BODY></HTML>' WHERE `entry`='3123';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><P>¡Esta vez no estaba sonando! Era muy real, durante mas de un minuto, en mitad del dia calido y soleado, me salia un aliento de vaho y frio. Uno de los sacerdotes se dio cuenta y se arrodillo para rezar.</P><BR/><P>Sin embargo, nadie mas oyo la voz. Al menos, este testigo demuestra que no me estoy volviendo loca. Quizas podria pedirle a Landgren que rece por ello.</P><BR/><P>Manana hare que Jordan y Calle tengan en cuenta hasta el minimo detalle a la hora de reclutar. Tenemos un monton de infieles que solo desean acabar con los no-muertos. ¡Eso no basta!</P></BODY></HTML>' WHERE `entry`='3124';
UPDATE `locales_page_text` SET `Text_loc7`='El comandante y el obispo se mostraron muy receptivos, aunque tampoco les quedaba otro remedio. En particular, el obispo Calle parecia muy entusiasmado. Hablo de una nueva cruzada y juro que descubriria a los debiles de fe de entre nuestras filas.$B$BLe dije que se lo tomara con calma. No vamos a destruir la Cruzada sino a formar un cuadro de elite de los mas fieles para hacer la voluntad de la Luz en Rasganorte. Temo que la amistad del hombre con LeCraft le esta corrompiendo. Aunque cada uno tiene sus ideas.$B$BPero sigo reflexionando sobre lo que conto Calle acerca de una nueva cruzada. Creo que cuando esta nueva fuerza este formada, le dare un nuevo nombre. Tiene que evocar nuestra nueva mision, y al mismo tiempo estar vinculado al pasado. El abad y yo rezaremos por esto.' WHERE `entry`='3125';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><P>Nuestras oraciones han sido escuchadas. El sumo abad se ha sentido particularmente conmovido por la fuerza, la claridad y la determinacion de la voz de la Luz.</P><BR/><P>Los que vamos hacia el norte, los mas fieles, seremos conocidos como El Embate Escarlata.</P><BR/><P>¡Seremos un embate sobre Rasganorte! La enfermedad de la Plaga amenaza con rebosar en la cima del mundo para ahogarnos a todos. ¡Ha llegado el momento de llevar la lucha hasta la puerta del mismisimo Rey Exanime!</P></BODY></HTML>' WHERE `entry`='3126';
UPDATE `locales_page_text` SET `Text_loc7`='Observe, temblando, Nuevo Avalon, sospecho que por ultima vez. Nuestro destino esta en Rasganorte. Por alguna razon me invaden malos presentimientos. La mision frente a mi debiera liberarme de estas preocupaciones. Las borrare de mi mente.$B$BEl Alba Carmesi esta sobre nosotros.' WHERE `entry`='3127';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><P>Observe Nuevo Avalon con inquietud, temiendo que fuese la ultima vez. El destino de nuestra causa se encuentra en Rasganorte. Por alguna razon, me llena una sensacion de mal agüero. La mision que se avecina deberia hacerme olvidar mis preocupaciones. Las borrare de mi mente.</P><BR/><P>El Alba Carmesi esta con nosotros. </P></BODY></HTML>' WHERE `entry`='3129';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>No hemos tenido noticias de ninguno de mis mensajeros esta manana. Esta claro que ninguno consiguio llegar a Vega del Amparo. Las Tierras de la Peste estan perdidas. Purasangre llegara con su ejercito y los aniquilaran alli mismo.</P><BR/><P>Esta tarde he tenido una vision de la Luz. Vi que destruian todo lo que hemos construido aqui. El mensaje era claro: me decia que me llevara a los supervivientes mas fieles y abandonase a la Cruzada a su suerte.</P></BODY></HTML>' WHERE `entry`='3143';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><BR/><P>Mas tarde, Landgren me dijo que el habia tenido la misma vision. No llego a comprender la razon por la que la Luz nos habra hecho emprender una accion tan deshonrosa, pero no tengo el derecho de preguntar... Estoy aqui para obedecer y obedecere.</P><BR/><P>Observe, temblando, Nuevo Avalon, sospecho que por ultima vez. Nuestro destino esta en Rasganorte. Por alguna razon me invaden malos presentimientos. La mision frente a mi debiera liberarme de estas preocupaciones. Las borrare de mi mente.</P></BODY></HTML>' WHERE `entry`='3144';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Puede que, si hay suerte, el alto comandante Purasangre, de algun modo, soporte y prepare a los supervivientes. ¡Yo soy cobarde, un perro que huye con el rabo entre las piernas!</P><BR/><P>Me dicen que el viaje va a durar dos meses. Los otros barcos no pueden alcanzar la velocidad de la Locura. Llevan a bordo la mayoria de nuestras fuerzas y equipamiento y no son mucho mas que simples cargueros a vela, pero llegaran a su destino.</P><BR/><P>No es que me apetezca mucho el viaje, pero por la Luz aguantare los mareos. Solo debo evitar que el resto se de cuenta.</P></BODY></HTML>' WHERE `entry`='3145';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Llevo tiempo sin escribir. Lo unico que puedo hacer es ponerme en pie e intentar no vomitar. Los hombres empiezan a preguntarse por que estoy encerrada en mis habitaciones la mayor parte del tiempo. No seria bueno para la moral que me vieran asi.</P><BR/><P>Ya no debe de quedar mucho. Rezo para que asi sea. Ya llevamos seis semanas y cada dia parece que el tiempo empeora. Solo puedo esperar que en Rasganorte no haga tan mal tiempo. No estoy hecha para el frio.</P><BR/><P>La Luz Sagrada lleva en silencio mucho tiempo.</P></BODY></HTML>' WHERE `entry`='3146';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>¡Nos atacaron de repente! ¡Unos gigantes en un enorme barco vikingo salieron de la bruma como si fueran fantasmas! Eran mas silenciosos que un muerto.</P><BR/><P>Perdi un barco y todos los hombres que se encontraban en el. Luchamos con gran valor y habilidad, si consideramos el hecho de que recibimos el entrenamiento mas basico en combate naval.</P><BR/><P>Despues, los gritos de nuestros hombres capturados por los gigantes retumbaron por el agua. Despues de un rato volvio el silencio. El obispo Calle condujo las oraciones de los hombres.</P></BODY></HTML>' WHERE `entry`='3147';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Esta manana me desperte haciendo cambios a mis mapas. La Luz guiaba mi mano, mostrandome el lugar exacto al que debemos ir.</P><BR/><P>¡Estamos cerca!</P></BODY></HTML>' WHERE `entry`='3148';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>¡Recalada!</P><BR/><P>Al llegar con mi bote de remos a la playa, coloque nuestro estandarte y la Luz Sagrada me invadio, hablando desde mi interior. Hoy es el Alba Carmesi... el gran dia que tanto hemos esperado. Este es el lugar para Nueva Vega del Amparo. Ya no somos la Cruzada Escarlata, ¡ahora somos El Embate Escarlata!</P><BR/><P>¡Seremos un embate sobre Rasganorte! La enfermedad de la Plaga amenaza con rebosar en la cima del mundo para ahogarnos a todos. ¡Ha llegado el momento de llevar la lucha hasta la puerta del mismisimo Rey Exanime!</P></BODY></HTML>' WHERE `entry`='3149';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Ha pasado casi un mes y la construccion avanza a pasos agigantados. He estado demasiado ocupada para escribir. Mis exploradores me dicen que estas tierras estan llenas de dragones y otras extranas bestias. Viviremos aislados hasta que estemos preparados.</P><BR/><P>En mitad del oficio de hoy el sumo abad anuncio que pronto recibiriamos una visita, una que la Luz nos enviaba para liderarnos a la victoria.</P><BR/><P>No se que pensar. ¿Por que no me lo dijo la Luz? ¿Acaso no he servido con lealtad? ¿Ahora un extrano me va a reemplazar?</P></BODY></HTML>' WHERE `entry`='3150';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>¡Anoche el almirante Barean Viento Oeste se presento ante mi puerta! Todos creian que habia muerto en estas costas hace anos.</P><BR/><P>No parecia suficientemente mayor y, a pesar de ello, supe que era el. Afirmo que era el unico superviviente de su flota y que sobrevivio por gracia divina de la Luz.</P></BODY></HTML>' WHERE `entry`='3151';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Nos quedamos hablando hasta el alba. Me aseguro que no tenia ninguna intencion de quedarse con mi puesto, pero que la Luz le habia ordenado que viajara por el Cementerio de Dragones para servirme de consejero y comandante, solo en titulo. Afirmo que ocurriria un cambio importante en Rasganorte. La Alianza y la Horda iban a acudir en masa en respuesta a una enorme plaga que el Rey Exanime estaba a punto de lanzar sobre ellos.</P></BODY></HTML>' WHERE `entry`='3152';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Los hombres tienen en alta estima al gran Almirante, especialmente el Sumo abad Landgren y el obispo Calle. Al parecer, la Luz Sagrada le susurro una nueva bendicion al almirante mientras dormia, y este se la comunico a Landgren. Algunos de los hombres se han convertido al sacerdocio y ahora reciben el nombre de \"sacerdotes cuervo\".</P><BR/><P>El unico que parece no estar impresionado es Jordan. Supongo que es logico. Probablemente sienta que su puesto este en peligro si el mio tambien lo esta.</P></BODY></HTML>' WHERE `entry`='3153';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Hay algo que no va bien. No pondria la mano en el fuego, pero se que no puedo fiarme completamente del almirante. En realidad no ha hecho nada malo, ¡al contrario! Sin embargo, tengo que fiarme de mis instintos.</P><BR/><P>Rezare para comprender. La Luz nos trajo al almirante para que nos guiara hacia la victoria y no soy quien para poner en duda sus decisiones. Seguire obedeciendo. Tengo fe.</P></BODY></HTML>' WHERE `entry`='3154';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Han pasado otros dos meses. Hemos hecho grandes avances en la construccion de Nueva Vega del Amparo. El muro y los barracones casi estan terminados. Los hombres de Kaleiki trabajan de manera milagrosa.</P><BR/><P>No he sentido la inspiracion necesaria para escribir. He estado evitando plasmar aqui mis pensamientos por alguna razon. La Luz no me ha visitado ultimamente, aunque el almirante me asegura que no hay nada de lo que preocuparse.</P></BODY></HTML>' WHERE `entry`='3155';
UPDATE `locales_page_text` SET `Text_loc7`='<HTML><BODY><P>Mis hombres me informan de que un pequeno grupo de Renegados ha establecido un campamento a lo largo de la ladera al norte. Parece que al otro lado de la colina la Alianza ha comenzado a construir una base mucho mayor.</P><BR/><P>El almirante dice que deberiamos dejarlos tranquilos. Hay otras fuerzas de la Horda reunidas al oeste que vendrian en su ayuda en caso de que atacaramos. No me da buena espina, pero creo que su razonamiento es logico.</P></BODY></HTML>' WHERE `entry`='3156';
UPDATE `locales_page_text` SET `Text_loc7`='Skor\'zad miro nerviosamente por un lado de la columna. Tal y como habia planeado, la vio flotando alli, a solas en sus habitaciones. ¿Habia tomado la decision correcta al visitar la habitacion privada del alma en pena a esas horas de la noche? De ser descubierto, los nigromantes le volverian a meter bajo tierra sin duda alguna. Peor aun, ni siquiera sabia si sus sentimientos eran correspondidos y ademas, Madame Sidnari era famosa por su escasa paciencia.' WHERE `entry`='3157';
UPDATE `locales_page_text` SET `Text_loc7`='Las pocas dudas que tenia el zombi quedaron rapidamente descartadas cuando pudo contemplar su belleza. Sus inconfundibles rasgos elficos, un recordatorio de su forma en su vida anterior, ahora eran aun mas hermosos gracias a las heridas supurantes y costras que salpicaban su rostro. Su piel morada, casi traslucida estaba cubierta de forma frugal por vendajes que abrazaban su desaseada figura. La fria carne se unia con lo efimero en su torso sin piernas que descansaba sobre una esfera azulada de energia, flotando a varios centimetros sobre el frio suelo de granito. Skor\'zad se relajo un poco, y se limpio el sudor y el icor que banaban su prominente frente.' WHERE `entry`='3158';
UPDATE `locales_page_text` SET `Text_loc7`='Aparentemente sin notar su presencia, ella se deslizo hasta la unica ventana en la habitacion y miro al exterior, al campo de batalla. Ante ella se extendia la desolacion. Seis horas atras, los gritos agudos de los ninos humanos rasgaban el aire, pero ahora... solo quedaba el dulce silencio de la muerte.' WHERE `entry`='3159';
UPDATE `locales_page_text` SET `Text_loc7`='El viento comenzo a jugar con su pelo, agitando sus mechones parecidos a gusanos alrededor de las cuencas vacias de sus ojos. Skor\'zad, de pronto consciente de que estaba inclinandose hacia delante lleno de lujuria, volvio a esconderse tras la columna, aterrorizado ante la idea de que la planidera oscura pudiera haberle visto. Ella si le habia visto, pero no tenia nada que temer.' WHERE `entry`='3160';
UPDATE `locales_page_text` SET `Text_loc7`='\"Skor\'zad,\" dijo ella. \"Te estaba esperando. Hoy has luchado bien.\" El alma en pena lanzo una mirada timida sobre su putrefacto hombro derecho, permitiendo que sus vendajes se soltasen levemente. \"Es hora de que... recojas tu recompensa.\"' WHERE `entry`='3161';
UPDATE `locales_page_text` SET `Text_loc7`='<Las siguientes paginas parecen estar pegadas unas con otras.>' WHERE `entry`='3162';
UPDATE `locales_page_text` SET `Text_loc7`='Tartek el Iracundo. Una de las abominaciones mas poderosas que jamas haya creado la Plaga. La ensamblo el exanime Amnennar el Gelido para ayudar en la creacion de una base en Zahurda Rajacieno. Tartek, sin ayuda de nadie, acabo con mas de 1.200 jabaespines antes de caer. Su sacrificio le proporciono cadaveres suficientes a Amnennar para crear rapidamente un ejercito de jabaespines no-muertos propio, consiguiendo asi el primer gran puesto destacado de la Plaga en Kalimdor. Parte de los restos de Tartek fueron reanimados y utilizados para crear a Gloton, el lugarteniente actual de Amnennar.' WHERE `entry`='3163';
UPDATE `locales_page_text` SET `Text_loc7`='Obrahiim, el Viajante. Fue un arquitecto brillante, vital en el diseno de Naxxramas y la mente genial detras de la creacion de Acherus. Ascendio rapidamente de peon esqueleto de rango bajo hasta llegar a ser uno de los consejeros de mas confianza de Kel\'Thuzad.$b$bSiempre fue un estudioso; viajaba por el mundo en busca de conocimiento, recibiendo la inspiracion para sus primeras obras de la arquitectura de los nerubianos y la magia de los brujos orcos. Fue visto por ultima vez hace varios anos cuando se dirigia al oeste para estudiar las ruinas de Zin-Azshari en Kalimdor.' WHERE `entry`='3164';
UPDATE `locales_page_text` SET `Text_loc7`='Diodor el Maldito. Nigromante humano, uno de los primeros miembros del Culto de los Condenados, un heroe condecorado en la Primera Guerra. Lidero con exito un contingente de la Plaga hasta la victoria durante el primer y segundo asalto de la Marea de Verano contra el Monasterio Escarlata. Murio en acto de servicio durante el tercer (y ultimo) asalto de la Marea de Verano, que fue repelido con exito por parte de los agentes de la Cruzada Escarlata, que ya habian descubierto su modus operandi.' WHERE `entry`='3165';
UPDATE `locales_page_text` SET `Text_loc7`='Kirkessen el Ferviente. Poderoso exanime que responde directamente ante el propio Rey Exanime. Un veterano condecorado en incontables batallas que diseno y lidero el Asedio de los Sanguinos, uno de los mayores asaltos organizados contra la Capilla de la Esperanza de la Luz. A pesar de su apabullante ventaja, el ataque fue contrarrestado rapidamente y sus tropas sufrieron enormes bajas. El propio Kirkessen recibio un golpe critico de Lord Maxwell Neofitus y ya no puede adoptar una forma corporea.' WHERE `entry`='3166';
UPDATE `locales_page_text` SET `Text_loc7`='OESTE DE RASGANORTE: los informes del Escuadron de Vigilancia Aerea en el Glaciar de Corona de Hielo muestran que la comunicacion con el Senor Exanime Invierno Helado se ha cortado de forma repentina. \"Invierno Helado fue visto por ultima vez pilotando la necropolis Talramas sobre la Tundra Boreal en una mision confidencial,\" informo el Ingeniero de la Plaga Karomon, el oficial jefe de informacion del Escuadron. \"Nuestros diagnosticos muestran que sus cristales de comunicacion funcionan a la perfeccion... es solo que... estan callados como muertos.\"' WHERE `entry`='3167';
UPDATE `locales_page_text` SET `Text_loc7`='BRECHA DE LA MUERTE, REINOS DEL ESTE: el instructor Razuvious hablo hace unos dias, reiterando su politica sobre los motines y la insubordinacion. \"Cualquier caballero de la Muerte que ose desertar a los Renegados conocera un destino peor que la muerte. Su sufrimiento no conocera fin, me ocupare de ello personalmente.\"' WHERE `entry`='3169';
UPDATE `locales_page_text` SET `Text_loc7`='CEMENTERIO DE DRAGONES: el principe Valanar esta buscando a un caballero de la Muerte excepcional para viajar hasta el Cementerio de Dragones, la llanura helada central de Rasganorte y liderar a un grupo de soldados de elite en un ataque contra el vuelo rojo. \"Vuestro entrenamiento casi ha terminado,\" dijo en un discurso publico reciente. \"¡Ha llegado la hora de atacar a aquellos que podrian proteger a los vivos! ¡Les mostraremos el verdadero significado de Cementerio de Dragones!\"' WHERE `entry`='3171';
UPDATE `locales_page_text` SET `Text_loc7`='Recientemente han visto a Alexstrasza, reina de los dragones rojos y posiblemente la enemiga mas poderosa de la Plaga en Rasganorte, construyendo su propio ejercito, aunque se cree que sus esfuerzos estan dirigidos contra los renegados del vuelo azul.' WHERE `entry`='3172';
UPDATE `locales_page_text` SET `Text_loc7`='ENTRAnAS: nuestras fuentes dentro de la ciudad informan que la Horda se esta movilizando para organizar un asalto a Rasganorte. \"La tropas de la Horda, la mayoria de las cuales regresan del despliegue en Terrallende, se estan reuniendo ahora y preparandose para asaltar Corona de Hielo.\"' WHERE `entry`='3173';
UPDATE `locales_page_text` SET `Text_loc7`='Ademas, los agentes encubiertos informan de que la Sociedad de Boticarios Reales de los Renegados ha aumentado enormemente su actividad. \"Aunque a estas alturas aun no hay nada confirmado, los rumores indican que la Sociedad ha avanzado mucho en su investigacion de la plaga. Una vez mas, son solo habladurias pero se aconseja a todas las fuerzas que eviten a los alquimistas Renegados.\"' WHERE `entry`='3174';
UPDATE `locales_page_text` SET `Text_loc7`='Los caballeros de la Muerte de Acherus$B$BNombre: Jayde$BEstado: vivo$BComentario: sediento de sangre' WHERE `entry`='3175';
