UPDATE `locales_page_text` SET `Text_loc7`='Nombre: Cobalt$BEstado: fallecido$BComentario: murio de una caida. Tenia pocas luces. Probablemente se evito una vida de sufrimiento.' WHERE `entry`='3178';
UPDATE `locales_page_text` SET `Text_loc7`='Nombre: Armonia$BEstado: fallecido$BComentario: el nombre de este caballero de la Muerte violaba la decencia comun de los Renegados. Eliminado por el Rey Exanime.' WHERE `entry`='3179';
