UPDATE `locales_page_text` SET `Text_loc7`='Nombre: Sixen$BEstado: fallecido$BComentario: murio con la boca abierta.' WHERE `entry`='3185';
UPDATE `locales_page_text` SET `Text_loc7`='Nombre: Munch$BEstado: vivo$BComentario: furioso. Lleno de odio. Lo hara bien.' WHERE `entry`='3186';
